/**
 * ãƒ•ã‚§ã‚¤ãƒ«ã‚»ãƒ¼ãƒ•å‹•ä½œãƒ†ã‚¹ãƒˆ
 * Phase 3 M3: Session/CSPãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ã®ãƒ•ã‚§ã‚¤ãƒ«ã‚»ãƒ¼ãƒ•æ¤œè¨¼
 */

jest.mock('@/lib/logger', () => {
  const mockLogger = {
    debug: jest.fn(),
    info: jest.fn(),
    warn: jest.fn(),
    error: jest.fn(),
    log: jest.fn(),
  };

  return {
    logger: mockLogger,
    createLogger: jest.fn(() => mockLogger),
    LogLevel: {
      DEBUG: 0,
      INFO: 1,
      WARN: 2,
      ERROR: 3,
      NONE: 4,
    },
  };
});

import { SessionManager } from '@/lib/session-manager';
import { AuditLogger } from '@/lib/audit-logger';
import { logger } from '@/lib/logger';

const createMockSupabase = () => ({
  from: jest.fn().mockReturnThis(),
  select: jest.fn().mockReturnThis(),
  insert: jest.fn().mockReturnThis(),
  update: jest.fn().mockReturnThis(),
  delete: jest.fn().mockReturnThis(),
  eq: jest.fn().mockReturnThis(),
  neq: jest.fn().mockReturnThis(),
  in: jest.fn().mockReturnThis(),
  gte: jest.fn().mockReturnThis(),
  lt: jest.fn().mockReturnThis(),
  or: jest.fn().mockReturnThis(),
  limit: jest.fn().mockReturnThis(),
  order: jest.fn().mockReturnThis(),
  single: jest.fn(),
  auth: {
    getUser: jest.fn(),
    onAuthStateChange: jest.fn(),
  },
});

let mockSupabase = createMockSupabase();

jest.mock('@supabase/ssr', () => ({
  createServerClient: jest.fn(() => mockSupabase),
  createBrowserClient: jest.fn(() => mockSupabase),
}));

jest.mock('@/lib/supabase', () => ({
  createClient: jest.fn(async () => mockSupabase),
  createAdminClient: jest.fn(() => mockSupabase),
}));

jest.setTimeout(30000);

describe('ãƒ•ã‚§ã‚¤ãƒ«ã‚»ãƒ¼ãƒ•å‹•ä½œãƒ†ã‚¹ãƒˆ', () => {
  let consoleWarnSpy: jest.SpyInstance;
  let consoleErrorSpy: jest.SpyInstance;

  beforeEach(() => {
    process.env.SUPABASE_SERVICE_ROLE_KEY = 'mock-service-role-key';
    jest.clearAllMocks();
    mockSupabase = createMockSupabase();

    // console.warn/errorã‚’ã‚¹ãƒ‘ã‚¤ï¼ˆå®Ÿè£…ãŒconsole.warnã‚’ä½¿ç”¨ã™ã‚‹ãŸã‚ï¼‰
    consoleWarnSpy = jest.spyOn(console, 'warn').mockImplementation(() => {});
    consoleErrorSpy = jest.spyOn(console, 'error').mockImplementation(() => {});

    const supabaseSSR = jest.requireMock('@supabase/ssr') as {
      createServerClient: jest.Mock;
      createBrowserClient: jest.Mock;
    };

    supabaseSSR.createServerClient.mockReturnValue(mockSupabase);
    supabaseSSR.createBrowserClient.mockReturnValue(mockSupabase);
  });

  afterEach(() => {
    consoleWarnSpy?.mockRestore();
    consoleErrorSpy?.mockRestore();
  });

  describe('SessionManager ãƒ•ã‚§ã‚¤ãƒ«ã‚»ãƒ¼ãƒ•', () => {
    it('DBæ¥ç¶šã‚¨ãƒ©ãƒ¼æ™‚ã«ã‚»ãƒƒã‚·ãƒ§ãƒ³ä½œæˆãŒé©åˆ‡ã«ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯', async () => {
      const sessionManager = new SessionManager();

      // DBæ¥ç¶šã‚¨ãƒ©ãƒ¼ã‚’ã‚·ãƒŸãƒ¥ãƒ¬ãƒ¼ãƒˆ
      mockSupabase.single.mockRejectedValue(
        new Error('Connection refused: Database unavailable')
      );

      const result = await sessionManager.createSession(
        'user-123',
        'clinic-001',
        {
          deviceInfo: { device: 'desktop', os: 'Windows', browser: 'Chrome' },
          ipAddress: '192.168.1.1',
        }
      );

      // ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯ã§ã‚»ãƒƒã‚·ãƒ§ãƒ³ãŒä½œæˆã•ã‚Œã‚‹
      expect(result).toBeDefined();
      expect(result.session).toBeDefined();
      expect(result.token).toBeDefined();
      expect(result.session.user_id).toBe('user-123');

      // å®Ÿè£…ã¯console.warnã‚’ä½¿ç”¨ã—ã¦ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯ã‚’ãƒ­ã‚°
      expect(consoleWarnSpy).toHaveBeenCalledWith(
        'createSession fallback:',
        expect.any(Error)
      );
    });

    it('ã‚»ãƒƒã‚·ãƒ§ãƒ³æ¤œè¨¼å¤±æ•—æ™‚ã«å®‰å…¨ãªå¿œç­”ã‚’è¿”ã™', async () => {
      const sessionManager = new SessionManager();

      // DBéšœå®³ã‚·ãƒŸãƒ¥ãƒ¬ãƒ¼ãƒˆ
      mockSupabase.single.mockRejectedValue(new Error('Query timeout'));

      const result = await sessionManager.validateSession('invalid-token');

      // æ¤œè¨¼å¤±æ•—ã ãŒä¾‹å¤–ã¯æŠ•ã’ãªã„
      expect(result.isValid).toBe(false);
      expect(result.reason).toBe('not_found');

      // ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ãŒè¨˜éŒ²ã•ã‚Œã‚‹ï¼ˆå®Ÿè£…ã¯console.warnã‚’ä½¿ç”¨ï¼‰
      expect(consoleWarnSpy).toHaveBeenCalled();
    });

    it('ã‚»ãƒƒã‚·ãƒ§ãƒ³æ›´æ–°å¤±æ•—æ™‚ã«ã‚¨ãƒ©ãƒ¼ã‚’åã¿è¾¼ã‚€', async () => {
      const sessionManager = new SessionManager();

      mockSupabase.single.mockRejectedValue(new Error('Database write failed'));

      const result = await sessionManager.refreshSession(
        'test-token',
        '192.168.1.1'
      );

      // å¤±æ•—ã‚’è¿”ã™ãŒä¾‹å¤–ã¯æŠ•ã’ãªã„
      expect(result).toBe(false);
      // å®Ÿè£…ã¯console.warnã‚’ä½¿ç”¨
      expect(consoleWarnSpy).toHaveBeenCalled();
    });

    it('å¤§é‡ã‚»ãƒƒã‚·ãƒ§ãƒ³æ“ä½œæ™‚ã§ã‚‚ã‚¨ãƒ©ãƒ¼ãŒä¼æ’­ã—ãªã„', async () => {
      const sessionManager = new SessionManager();

      // 50%ã®ç¢ºç‡ã§å¤±æ•—ã™ã‚‹ãƒ¢ãƒƒã‚¯
      let callCount = 0;
      mockSupabase.single.mockImplementation(() => {
        callCount++;
        if (callCount % 2 === 0) {
          return Promise.reject(new Error('Random failure'));
        }
        return Promise.resolve({
          data: { id: `session-${callCount}`, is_active: true },
          error: null,
        });
      });

      const promises = Array.from({ length: 10 }, (_, i) =>
        sessionManager.validateSession(`token-${i}`)
      );

      // ã™ã¹ã¦ã®æ“ä½œãŒå®Œäº†ï¼ˆä¾‹å¤–ã§ä¸­æ–­ã•ã‚Œãªã„ï¼‰
      const results = await Promise.all(promises);

      expect(results).toHaveLength(10);
      results.forEach(result => {
        expect(result).toHaveProperty('isValid');
      });
    });
  });

  describe('AuditLogger ãƒ•ã‚§ã‚¤ãƒ«ã‚»ãƒ¼ãƒ•', () => {
    it('DBéšœå®³æ™‚ã«ç›£æŸ»ãƒ­ã‚°ãŒæ§‹é€ åŒ–ãƒ­ã‚°ã¨ã—ã¦å‡ºåŠ›ã•ã‚Œã‚‹', async () => {
      // DBæ›¸ãè¾¼ã¿ã‚¨ãƒ©ãƒ¼ã‚’ã‚·ãƒŸãƒ¥ãƒ¬ãƒ¼ãƒˆï¼ˆinsertãƒ¡ã‚½ãƒƒãƒ‰ãŒPromiseã§{error}ã‚’è¿”ã™ï¼‰
      mockSupabase.insert.mockReturnValue(
        Promise.resolve({ error: new Error('audit_logs table unavailable') })
      );

      await AuditLogger.logLogin(
        'user-123',
        'test@example.com',
        '192.168.1.1',
        'Mozilla/5.0'
      );

      // ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ãŒå‡ºåŠ›ã•ã‚Œã‚‹ãŒä¾‹å¤–ã¯æŠ•ã’ãªã„ï¼ˆloggerãƒ¢ã‚¸ãƒ¥ãƒ¼ãƒ«ã‚’ãƒ¢ãƒƒã‚¯æ¸ˆã¿ï¼‰
      expect(logger.error).toHaveBeenCalledWith(
        'ç›£æŸ»ãƒ­ã‚°DBæ›¸ãè¾¼ã¿å¤±æ•— - ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯å‡ºåŠ›',
        expect.objectContaining({
          error: expect.any(Error),
          logData: expect.objectContaining({
            event_type: 'login',
            user_id: 'user-123',
          }),
        })
      );
    });

    it('ãƒ­ã‚°è¨˜éŒ²å¤±æ•—ãŒé€£ç¶šã—ã¦ã‚‚ã‚·ã‚¹ãƒ†ãƒ ã¯å‹•ä½œç¶™ç¶š', async () => {
      // insertãŒ{error}ã‚’è¿”ã™ã‚ˆã†ã«ãƒ¢ãƒƒã‚¯
      mockSupabase.insert.mockReturnValue(
        Promise.resolve({ error: new Error('Persistent DB failure') })
      );

      // 10å›é€£ç¶šã§ãƒ­ã‚°è¨˜éŒ²ã‚’è©¦è¡Œ
      for (let i = 0; i < 10; i++) {
        await AuditLogger.logDataAccess(
          'user-123',
          'test@example.com',
          'patients',
          `patient-${i}`,
          'clinic-001'
        );
      }

      // ã™ã¹ã¦å®Œäº†ï¼ˆä¾‹å¤–ã§åœæ­¢ã—ãªã„ï¼‰
      expect(logger.error).toHaveBeenCalledTimes(10);
    });

    it('ç„¡åŠ¹ãªãƒ‡ãƒ¼ã‚¿ã§ã‚‚ãƒ­ã‚°ã‚·ã‚¹ãƒ†ãƒ ãŒã‚¯ãƒ©ãƒƒã‚·ãƒ¥ã—ãªã„', async () => {
      mockSupabase.insert.mockResolvedValue({
        data: null,
        error: null,
      });

      // ç•°å¸¸ãªãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿ã§ãƒ­ã‚°è¨˜éŒ²
      await AuditLogger.logLogin(
        '', // ç©ºæ–‡å­—
        '', // ç©ºãƒ¡ãƒ¼ãƒ«
        undefined,
        undefined
      );

      // ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¦ã‚‚å‡¦ç†ç¶™ç¶š
      expect(logger.error).not.toHaveBeenCalled();
    });
  });

  describe('CSPãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ ãƒ•ã‚§ã‚¤ãƒ«ã‚»ãƒ¼ãƒ•ï¼ˆæƒ³å®šå‹•ä½œï¼‰', () => {
    it('CSPé•åãƒ¬ãƒãƒ¼ãƒˆå‡¦ç†å¤±æ•—æ™‚ã§ã‚‚ãƒªã‚¯ã‚¨ã‚¹ãƒˆã¯ç¶™ç¶š', async () => {
      // CSPé•åãƒ¬ãƒãƒ¼ãƒˆå‡¦ç†ã®ãƒ¢ãƒƒã‚¯
      const mockCSPHandler = async (report: any) => {
        try {
          // DBä¿å­˜è©¦è¡Œ
          const result = await mockSupabase
            .from('csp_violations')
            .insert(report)
            .single();

          if (result.error) {
            throw result.error;
          }
        } catch (error) {
          // ã‚¨ãƒ©ãƒ¼ã‚’åã¿è¾¼ã‚“ã§ç¶™ç¶š
          logger.warn('CSP violation report failed, continuing...', error);
        }
      };

      // DBéšœå®³ã‚·ãƒŸãƒ¥ãƒ¬ãƒ¼ãƒˆ
      mockSupabase.single.mockRejectedValue(
        new Error('CSP violations table unavailable')
      );

      // CSPé•åãƒ¬ãƒãƒ¼ãƒˆå‡¦ç†
      await mockCSPHandler({
        'document-uri': 'https://example.com',
        'violated-directive': 'script-src',
      });

      // è­¦å‘ŠãŒå‡ºåŠ›ã•ã‚Œã‚‹ãŒå‡¦ç†ã¯ç¶™ç¶š
      expect(logger.warn).toHaveBeenCalledWith(
        'CSP violation report failed, continuing...',
        expect.any(Error)
      );
    });
  });

  describe('å†ªç­‰æ€§ãƒ†ã‚¹ãƒˆ', () => {
    it('åŒä¸€æ“ä½œã‚’è¤‡æ•°å›å®Ÿè¡Œã—ã¦ã‚‚å®‰å…¨', async () => {
      const sessionManager = new SessionManager();

      mockSupabase.single.mockResolvedValue({
        data: { id: 'session-001', is_active: true },
        error: null,
      });

      // åŒä¸€ã‚»ãƒƒã‚·ãƒ§ãƒ³ã«å¯¾ã—ã¦è¤‡æ•°å›æ›´æ–°
      const promises = Array.from({ length: 5 }, () =>
        sessionManager.refreshSession('test-token', '192.168.1.1')
      );

      const results = await Promise.all(promises);

      // ã™ã¹ã¦æˆåŠŸã¾ãŸã¯å¤±æ•—ã ãŒã€ã‚·ã‚¹ãƒ†ãƒ ã¯å®‰å®š
      expect(results).toHaveLength(5);
    });

    it('ä¸¦è¡Œã‚»ãƒƒã‚·ãƒ§ãƒ³ä½œæˆã§ã‚‚ç«¶åˆãŒç™ºç”Ÿã—ãªã„', async () => {
      const sessionManager = new SessionManager();

      let insertCount = 0;
      mockSupabase.single.mockImplementation(() => {
        insertCount++;
        return Promise.resolve({
          data: {
            id: `session-${insertCount}`,
            user_id: 'user-123',
            is_active: true,
          },
          error: null,
        });
      });

      // ä¸¦è¡Œã‚»ãƒƒã‚·ãƒ§ãƒ³ä½œæˆ
      const promises = Array.from({ length: 3 }, (_, i) =>
        sessionManager.createSession('user-123', 'clinic-001', {
          deviceInfo: {
            device: 'desktop',
            os: 'Windows',
            browser: 'Chrome',
          },
          ipAddress: `192.168.1.${i}`,
        })
      );

      const results = await Promise.all(promises);

      expect(results).toHaveLength(3);
      results.forEach(result => {
        expect(result.session).toBeDefined();
        expect(result.token).toBeDefined();
      });
    });
  });

  describe('ãƒªã‚«ãƒãƒªãƒ¼å‹•ä½œ', () => {
    it('ä¸€æ™‚çš„ãªDBéšœå®³ã‹ã‚‰è‡ªå‹•å¾©æ—§', async () => {
      const sessionManager = new SessionManager();

      let attemptCount = 0;
      mockSupabase.single.mockImplementation(() => {
        attemptCount++;
        // æœ€åˆã®2å›ã¯å¤±æ•—ã€3å›ç›®ã§æˆåŠŸ
        if (attemptCount <= 2) {
          return Promise.reject(new Error('Temporary DB failure'));
        }
        return Promise.resolve({
          data: { id: 'session-recovered', is_active: true },
          error: null,
        });
      });

      // 3å›è©¦è¡Œ
      const result1 = await sessionManager.validateSession('test-token-1');
      const result2 = await sessionManager.validateSession('test-token-2');
      const result3 = await sessionManager.validateSession('test-token-3');

      // æœ€åˆã®2å›ã¯å¤±æ•—ã€3å›ç›®ã§æˆåŠŸ
      expect(result1.isValid).toBe(false);
      expect(result2.isValid).toBe(false);
      expect(result3.isValid).toBe(true);
    });
  });

  describe('ã‚°ãƒ¬ãƒ¼ã‚¹ãƒ•ãƒ«ãƒ‡ã‚°ãƒ©ãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³', () => {
    it('æ©Ÿèƒ½ç¸®é€€ã—ã¦ã‚‚æœ€å°é™ã®å‹•ä½œã¯ç¶™ç¶š', async () => {
      const sessionManager = new SessionManager();

      // ã™ã¹ã¦ã®DBæ“ä½œãŒå¤±æ•—ã™ã‚‹çŠ¶æ³
      mockSupabase.single.mockRejectedValue(new Error('Complete DB failure'));

      // ã‚»ãƒƒã‚·ãƒ§ãƒ³ä½œæˆã¯ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯ã§å‹•ä½œ
      const createResult = await sessionManager.createSession(
        'user-123',
        'clinic-001',
        {
          deviceInfo: { device: 'desktop', os: 'Windows', browser: 'Chrome' },
        }
      );

      expect(createResult.session).toBeDefined();
      expect(createResult.session.user_id).toBe('user-123');

      // æ¤œè¨¼ã¯å¤±æ•—ã™ã‚‹ãŒä¾‹å¤–ã¯æŠ•ã’ãªã„
      const validateResult = await sessionManager.validateSession('any-token');
      expect(validateResult.isValid).toBe(false);

      // ç›£æŸ»ãƒ­ã‚°ã¯ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯å‡ºåŠ›
      await AuditLogger.logLogin('user-123', 'test@example.com');
      expect(logger.error).toHaveBeenCalled();
    });
  });
});

/**
 * ãƒšãƒãƒˆãƒ¬ãƒ¼ã‚·ãƒ§ãƒ³ãƒ†ã‚¹ãƒˆæº–å‚™ãƒ»è‡ªå‹•åŒ–ã‚¹ã‚¯ãƒªãƒ—ãƒˆ
 * ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£è„†å¼±æ€§ãƒ†ã‚¹ãƒˆç”¨ã®ã‚·ãƒŠãƒªã‚ªã¨ãƒ˜ãƒ«ãƒ‘ãƒ¼
 */

import { SessionManager } from '@/lib/session-manager';
import { SecurityMonitor } from '@/lib/security-monitor';

/**
 * ãƒšãƒãƒˆãƒ¬ãƒ¼ã‚·ãƒ§ãƒ³ãƒ†ã‚¹ãƒˆã‚·ãƒŠãƒªã‚ª
 * å®Ÿéš›ã®ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ†ã‚¹ãƒˆã§ä½¿ç”¨ã•ã‚Œã‚‹æ”»æ’ƒãƒ‘ã‚¿ãƒ¼ãƒ³ã‚’å®‰å…¨ã«ã‚·ãƒŸãƒ¥ãƒ¬ãƒ¼ãƒˆ
 */
export class PenetrationTestRunner {
  private sessionManager: SessionManager;
  private securityMonitor: SecurityMonitor;
  private testResults: TestResult[] = [];

  constructor() {
    this.sessionManager = new SessionManager();
    this.securityMonitor = new SecurityMonitor();
  }

  /**
   * å…¨ãƒšãƒãƒˆãƒ¬ãƒ¼ã‚·ãƒ§ãƒ³ãƒ†ã‚¹ãƒˆã‚¹ã‚¤ãƒ¼ãƒˆã‚’å®Ÿè¡Œ
   */
  async runAllTests(): Promise<PentestReport> {
    console.log('ğŸ”’ ãƒšãƒãƒˆãƒ¬ãƒ¼ã‚·ãƒ§ãƒ³ãƒ†ã‚¹ãƒˆé–‹å§‹...');

    const testSuites = [
      this.testSessionHijacking,
      this.testBruteForceAttack,
      this.testSessionFixation,
      this.testCSRFProtection,
      this.testSessionEnumeration,
      this.testPrivilegeEscalation,
      this.testTimeBasedAttacks,
      this.testConcurrentSessionAttacks,
      this.testAdvancedVulnerabilityScanning,
      this.testPerformanceStressTest,
      this.testDDoSResistance,
      this.testSQLInjectionAttempts,
      this.testXSSVulnerabilities,
    ];

    for (const testSuite of testSuites) {
      try {
        await testSuite.call(this);
      } catch (error) {
        this.recordResult({
          testName: testSuite.name,
          status: 'error',
          severity: 'high',
          description: `ãƒ†ã‚¹ãƒˆå®Ÿè¡Œã‚¨ãƒ©ãƒ¼: ${error}`,
          timestamp: new Date(),
        });
      }
    }

    return this.generateReport();
  }

  /**
   * ã‚»ãƒƒã‚·ãƒ§ãƒ³ãƒã‚¤ã‚¸ãƒ£ãƒƒã‚¯æ”»æ’ƒãƒ†ã‚¹ãƒˆ
   */
  private async testSessionHijacking(): Promise<void> {
    console.log('ğŸ“¡ ã‚»ãƒƒã‚·ãƒ§ãƒ³ãƒã‚¤ã‚¸ãƒ£ãƒƒã‚¯æ”»æ’ƒãƒ†ã‚¹ãƒˆ...');

    const testCases = [
      {
        name: 'IP spoofing attack',
        scenario: async () => {
          // æ­£å¸¸ãªã‚»ãƒƒã‚·ãƒ§ãƒ³ä½œæˆ
          const legitimateSession = await this.createMockSession('user-123', {
            ipAddress: '192.168.1.100',
            userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64)',
          });

          // ç•°ãªã‚‹IPã‹ã‚‰ã®ã‚¢ã‚¯ã‚»ã‚¹è©¦è¡Œ
          const threats = await this.securityMonitor.analyzeSessionActivity(
            legitimateSession,
            {
              ipAddress: '203.0.113.50', // ç•°ãªã‚‹IP
              userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64)',
            }
          );

          // ä½ç½®ç•°å¸¸ãŒæ¤œçŸ¥ã•ã‚Œã‚‹ã¹ã
          const locationThreat = threats.find(
            t => t.type === 'location_anomaly'
          );
          return locationThreat !== undefined;
        },
      },
      {
        name: 'User-Agent spoofing attack',
        scenario: async () => {
          const session = await this.createMockSession('user-456', {
            userAgent:
              'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
          });

          // å…¨ãç•°ãªã‚‹User-Agentã§ã‚¢ã‚¯ã‚»ã‚¹
          const threats = await this.securityMonitor.analyzeSessionActivity(
            session,
            {
              ipAddress: session.ip_address,
              userAgent: 'curl/7.68.0', // ã‚³ãƒãƒ³ãƒ‰ãƒ©ã‚¤ãƒ³ãƒ„ãƒ¼ãƒ«
            }
          );

          // ã‚»ãƒƒã‚·ãƒ§ãƒ³ãƒã‚¤ã‚¸ãƒ£ãƒƒã‚¯ãŒæ¤œçŸ¥ã•ã‚Œã‚‹ã¹ã
          const hijackThreat = threats.find(
            t => t.type === 'session_hijacking'
          );
          return hijackThreat !== undefined;
        },
      },
    ];

    for (const testCase of testCases) {
      const success = await testCase.scenario();
      this.recordResult({
