    for (const payload of xssPayloads) {
      try {
        // XSSãƒšã‚¤ãƒ­ãƒ¼ãƒ‰ã‚’ã‚»ãƒƒã‚·ãƒ§ãƒ³ãƒˆãƒ¼ã‚¯ãƒ³ã¨ã—ã¦é€ä¿¡
