        if (payload.includes('<') && !result.reason?.includes('sanitized')) {
          potentialXSS++;
        } else {
          sanitizedInputs++;
        }
      } catch (error) {
        // ã‚¨ãƒ©ãƒ¼ã§ã‚‚é©åˆ‡ã«å‡¦ç†ã•ã‚ŒãŸã¨ã¿ãªã™
