'use server';

import { revalidatePath } from 'next/cache';
import { redirect } from 'next/navigation';
import { headers } from 'next/headers';
import {
  loginSchema,
  signupSchema,
  sanitizeAuthInput,
  type AuthResponse,
} from '@/lib/schemas/auth';
import { getServerClient } from '@/lib/supabase';
import { getSafeRedirectUrl, getDefaultRedirect } from '@/lib/url-validator';
import { AuditLogger, getRequestInfoFromHeaders } from '@/lib/audit-logger';

const INACTIVE_ACCOUNT_MESSAGE =
  'ã‚¢ã‚«ã‚¦ãƒ³ãƒˆãŒç„¡åŠ¹åŒ–ã•ã‚Œã¦ã„ã¾ã™ã€‚ç®¡ç†è€…ã«ãŠå•ã„åˆã‚ã›ãã ã•ã„';
const INVALID_CREDENTIALS_MESSAGE =
  'ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹ã¾ãŸã¯ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰ãŒæ­£ã—ãã‚ã‚Šã¾ã›ã‚“';
const GENERIC_AUTH_ERROR_MESSAGE = 'ã‚·ã‚¹ãƒ†ãƒ ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ';

function isRedirectLikeError(error: unknown): error is Error {
  if (error instanceof Error && error.message.startsWith('REDIRECT:')) {
    return true;
  }

  if (typeof error === 'object' && error !== null) {
    const digest = (error as { digest?: string }).digest;
    if (typeof digest === 'string' && digest.startsWith('NEXT_REDIRECT')) {
      return true;
    }
  }

  return false;
}

type AuthErrorDetail = {
  message?: string | null | undefined;
  status?: number | null | undefined;
};

function mapAuthError(error?: AuthErrorDetail | null) {
  const status = error?.status ?? null;
  const message = (error?.message ?? '').toLowerCase();

  if (status === 403 || /inactive|ban|blocked/i.test(message)) {
    return INACTIVE_ACCOUNT_MESSAGE;
  }

  if (status === 400 || /invalid|wrong|mismatch/i.test(message)) {
    return INVALID_CREDENTIALS_MESSAGE;
  }

  return GENERIC_AUTH_ERROR_MESSAGE;
}

/**
 * ãƒ­ã‚°ã‚¤ãƒ³å‡¦ç†ï¼ˆå…¥åŠ›å€¤æ¤œè¨¼å¼·åŒ–ç‰ˆï¼‰
 */
function extractAuthFormValues(formData: FormData) {
  const emailValue = formData.get('email');
  const passwordValue = formData.get('password');

  return {
    email: typeof emailValue === 'string' ? emailValue : '',
    password: typeof passwordValue === 'string' ? passwordValue : '',
  };
}

export async function login(_: any, formData: FormData): Promise<AuthResponse> {
  const supabase = await getServerClient();
  const headerList = await headers();
  const { ipAddress, userAgent } = getRequestInfoFromHeaders(headerList);

  try {
    // 1. å…¥åŠ›å€¤ã®æ¤œè¨¼ã¨ã‚µãƒ‹ã‚¿ã‚¤ã‚º
    const parsed = loginSchema.safeParse(extractAuthFormValues(formData));
    if (!parsed.success) {
      const { fieldErrors } = parsed.error.flatten();
      if (!fieldErrors.password || fieldErrors.password.length === 0) {
        fieldErrors.password = ['ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰ã‚’å…¥åŠ›ã—ã¦ãã ã•ã„'];
      }
      console.warn('[Auth] Login validation failed:', fieldErrors);
      return {
        success: false,
        errors: fieldErrors,
      };
    }

    const sanitizedEmail = sanitizeAuthInput(parsed.data.email).toLowerCase();
    const sanitizedPassword = sanitizeAuthInput(parsed.data.password);

    // 3. ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒã‚§ãƒƒã‚¯ï¼ˆåŸºæœ¬çš„ãªãƒ–ãƒ«ãƒ¼ãƒˆãƒ•ã‚©ãƒ¼ã‚¹å¯¾ç­–ï¼‰
    // TODO: ã‚ˆã‚Šè©³ç´°ãªãƒ¬ãƒ¼ãƒˆåˆ¶é™å®Ÿè£…

    // 4. Supabaseèªè¨¼
    const { error, data } = await supabase.auth.signInWithPassword({
      email: sanitizedEmail,
      password: sanitizedPassword,
    });

    if (error) {
      const errorMessage = mapAuthError(error);
      const warningPayload: {
        email: string;
        error: string;
        status?: number | null;
        details?: string;
      } = {
        email: sanitizedEmail,
        error: errorMessage,
      };

      if (typeof error.status !== 'undefined') {
        warningPayload.status = error.status ?? null;
      }

      if (error.message && error.message !== errorMessage) {
        warningPayload.details = error.message;
      }

      console.warn('[Security] Login attempt failed:', warningPayload);
      await AuditLogger.logFailedLogin(
        sanitizedEmail,
        ipAddress,
        userAgent,
        errorMessage
      );
      return {
        success: false,
        errors: {
          password: [errorMessage],
          _form: [errorMessage],
        },
      };
    }

    if (!data.user) {
      const fallbackMessage = 'èªè¨¼ã«å¤±æ•—ã—ã¾ã—ãŸã€‚å†åº¦ãŠè©¦ã—ãã ã•ã„';
      return {
        success: false,
        errors: {
          password: [fallbackMessage],
          _form: [fallbackMessage],
        },
      };
    }

    await AuditLogger.logLogin(
      data.user.id,
      sanitizedEmail,
      ipAddress,
      userAgent
    );

    // 5. ãƒ¦ãƒ¼ã‚¶ãƒ¼æ¨©é™ã®ç¢ºèª
    const profileResult = await supabase
      .from('profiles')
      .select('role, is_active')
      .eq('user_id', data.user.id)
      .single();

    type ProfileData = { role: string; is_active: boolean } | null;
    const profile = profileResult?.data as ProfileData;

    if (!profile?.is_active) {
      await supabase.auth.signOut();
      return {
        success: false,
        errors: {
          password: [INACTIVE_ACCOUNT_MESSAGE],
          _form: [INACTIVE_ACCOUNT_MESSAGE],
        },
      };
    }

    // 6. æˆåŠŸãƒ­ã‚°
    console.info('[Auth] Successful login:', {
      email: sanitizedEmail,
      role: profile!.role,
      timestamp: new Date().toISOString(),
    });

    // 7. ãƒ‘ã‚¹å†æ¤œè¨¼ã¨ãƒªãƒ€ã‚¤ãƒ¬ã‚¯ãƒˆ
    revalidatePath('/', 'layout');
    const redirectPath = getDefaultRedirect(profile!.role);
    redirect(redirectPath);
  } catch (error) {
    if (isRedirectLikeError(error)) {
      throw error;
    }
    console.error('[Auth] Login error:', error);
    return {
      success: false,
      errors: {
        password: [GENERIC_AUTH_ERROR_MESSAGE],
        _form: [GENERIC_AUTH_ERROR_MESSAGE],
      },
    };
  }
}

/**
 * ã‚µã‚¤ãƒ³ã‚¢ãƒƒãƒ—å‡¦ç†ï¼ˆå…¥åŠ›å€¤æ¤œè¨¼å¼·åŒ–ç‰ˆï¼‰
 */
export async function signup(
  _: any,
  formData: FormData
): Promise<AuthResponse> {
  const supabase = await getServerClient();

  // 1. å…¥åŠ›å€¤ã®æ¤œè¨¼
  const parsed = signupSchema.safeParse(extractAuthFormValues(formData));
  if (!parsed.success) {
    const { fieldErrors } = parsed.error.flatten();
    if (!fieldErrors.password || fieldErrors.password.length === 0) {
      fieldErrors.password = ['ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰ã‚’å…¥åŠ›ã—ã¦ãã ã•ã„'];
    }
    console.warn('[Auth] Signup validation failed:', fieldErrors);
    return {
      success: false,
      errors: fieldErrors,
    };
  }

  const sanitizedEmail = sanitizeAuthInput(parsed.data.email).toLowerCase();
  const sanitizedPassword = sanitizeAuthInput(parsed.data.password);

  try {
    const { error, data } = await supabase.auth.signUp({
      email: sanitizedEmail,
      password: sanitizedPassword,
      options: {
        emailRedirectTo: `${process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000'}/admin/callback`,
      },
    });

    if (error) {
      console.warn('[Security] Signup attempt failed:', {
        email: sanitizedEmail,
        error: error.message,
        timestamp: new Date().toISOString(),
      });

      const errorMessage = error.message.includes('already registered')
        ? 'ã“ã®ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹ã¯æ—¢ã«ç™»éŒ²ã•ã‚Œã¦ã„ã¾ã™'
        : 'ã‚¢ã‚«ã‚¦ãƒ³ãƒˆã®ä½œæˆã«å¤±æ•—ã—ã¾ã—ãŸã€‚å…¥åŠ›å†…å®¹ã‚’ç¢ºèªã—ã¦ãã ã•ã„';

      return {
        success: false,
        errors: {
          _form: [errorMessage],
        },
      };
    }

    console.info('[Auth] Successful signup:', {
      email: sanitizedEmail,
      userId: data.user?.id,
      timestamp: new Date().toISOString(),
    });

    revalidatePath('/', 'layout');

    return {
      success: true,
      message:
        'ç¢ºèªãƒ¡ãƒ¼ãƒ«ã‚’é€ä¿¡ã—ã¾ã—ãŸã€‚ãƒ¡ãƒ¼ãƒ«ã‚’ç¢ºèªã—ã¦ã‚¢ã‚«ã‚¦ãƒ³ãƒˆã‚’æœ‰åŠ¹åŒ–ã—ã¦ãã ã•ã„ã€‚',
    };
  } catch (error) {
    console.error('[Auth] Signup error:', error);
    return {
      success: false,
      errors: {
        _form: [GENERIC_AUTH_ERROR_MESSAGE],
      },
    };
  }
}

/**
 * ãƒ­ã‚°ã‚¢ã‚¦ãƒˆå‡¦ç†
 */
export async function logout(): Promise<void> {
  const supabase = await getServerClient();
  const headerList = await headers();
  const { ipAddress } = getRequestInfoFromHeaders(headerList);

  try {
    // ç¾åœ¨ã®ãƒ¦ãƒ¼ã‚¶ãƒ¼æƒ…å ±ã‚’å–å¾—ï¼ˆãƒ­ã‚°ç”¨ï¼‰
    const {
      data: { user },
    } = await supabase.auth.getUser();

    const { error } = await supabase.auth.signOut();

    if (error) {
      console.error('[Auth] Logout error:', error);
      redirect('/admin/login?error=logout_failed');
    }

    // ãƒ­ã‚°ã‚¢ã‚¦ãƒˆãƒ­ã‚°
    if (user) {
      console.info('[Auth] Successful logout:', {
        email: user.email,
        timestamp: new Date().toISOString(),
      });
      await AuditLogger.logLogout(user.id, user.email || '', ipAddress);
    }

    revalidatePath('/', 'layout');
    redirect('/admin/login?message=ãƒ­ã‚°ã‚¢ã‚¦ãƒˆã—ã¾ã—ãŸ');
  } catch (error) {
    if (isRedirectLikeError(error)) {
      throw error;
    }
    console.error('[Auth] Logout error:', error);
    redirect('/admin/login?error=logout_failed');
  }
}

/**
 * ãƒªãƒ€ã‚¤ãƒ¬ã‚¯ãƒˆä»˜ããƒ­ã‚°ã‚¢ã‚¦ãƒˆï¼ˆURLãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿ã‹ã‚‰ãƒªãƒ€ã‚¤ãƒ¬ã‚¯ãƒˆå…ˆã‚’æŒ‡å®šï¼‰
 */
export async function logoutWithRedirect(redirectTo?: string): Promise<void> {
  await logout();

  // å®‰å…¨ãªãƒªãƒ€ã‚¤ãƒ¬ã‚¯ãƒˆå…ˆã‚’æ¤œè¨¼
  const safeUrl = getSafeRedirectUrl(
    redirectTo,
    process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000'
  );
  const finalRedirect = safeUrl || '/admin/login';

  redirect(finalRedirect);
}

import { createClient } from '@/lib/supabase';
import { NextResponse } from 'next/server';
import { getSafeRedirectUrl, getDefaultRedirect } from '@/lib/url-validator';
import type { Database } from '@/types/supabase';

type ProfileRow = Database['public']['Tables']['profiles']['Row'];

export async function GET(request: Request) {
  const { searchParams, origin } = new URL(request.url);
  const code = searchParams.get('code');
  const nextParam = searchParams.get('next');

  // ã‚»ã‚­ãƒ¥ã‚¢ãªãƒªãƒ€ã‚¤ãƒ¬ã‚¯ãƒˆå…ˆã‚’æ¤œè¨¼
  const safeRedirectUrl = getSafeRedirectUrl(nextParam, origin);

  if (code) {
    const supabase = await createClient();

    try {
      const { error, data } = await supabase.auth.exchangeCodeForSession(code);

      if (!error && data.user) {
        // ãƒ¦ãƒ¼ã‚¶ãƒ¼æƒ…å ±ã‚’å–å¾—ã—ã¦é©åˆ‡ãªãƒªãƒ€ã‚¤ãƒ¬ã‚¯ãƒˆå…ˆã‚’æ±ºå®š
        const { data: profile } = await supabase
          .from('profiles')
          .select<
            'role, clinic_id',
            Pick<ProfileRow, 'role' | 'clinic_id'>
          >('role, clinic_id')
          .eq('user_id', data.user.id)
          .maybeSingle();

        const userRole = profile?.role ?? 'staff';
        const hasClinic = !!profile?.clinic_id;

        // clinic_idãŒãªã„å ´åˆã¯ã‚ªãƒ³ãƒœãƒ¼ãƒ‡ã‚£ãƒ³ã‚°ã¸
        let finalRedirectPath: string;
        if (!hasClinic) {
          finalRedirectPath = '/onboarding';
        } else if (safeRedirectUrl) {
          finalRedirectPath = new URL(safeRedirectUrl).pathname;
        } else {
          finalRedirectPath = getDefaultRedirect(userRole);
        }

        // æˆåŠŸãƒ­ã‚°
        console.info(
          `[Auth] Successful login: ${data.user.email} -> ${finalRedirectPath}`
        );

        return NextResponse.redirect(`${origin}${finalRedirectPath}`);
      } else {
        // èªè¨¼ã‚¨ãƒ©ãƒ¼ã‚’ãƒ­ã‚°ã«è¨˜éŒ²
        console.error('[Auth] Exchange code failed:', {
          error: error?.message,
          code: code?.substring(0, 10) + '...', // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã®ãŸã‚ä¸€éƒ¨ã®ã¿
          timestamp: new Date().toISOString(),
        });
      }
    } catch (error) {
      // äºˆæœŸã—ãªã„ã‚¨ãƒ©ãƒ¼ã‚’ãƒ­ã‚°ã«è¨˜éŒ²
      console.error('[Auth] Unexpected error during code exchange:', error);
    }
  } else {
    console.warn('[Auth] No authorization code provided in callback');
  }

  // èªè¨¼å¤±æ•—æ™‚ã¯å®‰å…¨ãªã‚¨ãƒ©ãƒ¼ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã§ãƒªãƒ€ã‚¤ãƒ¬ã‚¯ãƒˆ
  const errorUrl = `${origin}/admin/login?error=auth_failed`;

  // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ­ã‚°
  console.warn('[Security] Authentication callback failed:', {
    hasCode: !!code,
    nextParam,
    safeRedirectUrl,
    userAgent: request.headers.get('user-agent')?.substring(0, 100),
    timestamp: new Date().toISOString(),
  });

  return NextResponse.redirect(errorUrl);
}

