import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@/lib/supabase';
import { z } from 'zod';
import { processApiRequest } from '@/lib/api-helpers';
import { CLINIC_ADMIN_ROLES } from '@/lib/constants/roles';

// ã‚¯ã‚¨ãƒªãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿ã®ã‚¹ã‚­ãƒ¼ãƒ
const QuerySchema = z.object({
  limit: z
    .string()
    .optional()
    .transform(val => (val ? parseInt(val) : 50)),
  offset: z
    .string()
    .optional()
    .transform(val => (val ? parseInt(val) : 0)),
  severity: z.enum(['low', 'medium', 'high', 'critical']).optional(),
  directive: z.string().optional(),
  client_ip: z.string().optional(),
  hours: z
    .string()
    .optional()
    .transform(val => (val ? parseInt(val) : 24)),
});

export async function GET(request: NextRequest) {
  try {
    const auth = await processApiRequest(request, {
      allowedRoles: Array.from(CLINIC_ADMIN_ROLES),
      requireClinicMatch: false,
    });
    if (!auth.success) {
      return auth.error!;
    }

    const { searchParams } = new URL(request.url);
    const params = QuerySchema.parse({
      limit: searchParams.get('limit'),
      offset: searchParams.get('offset'),
      severity: searchParams.get('severity') as any,
      directive: searchParams.get('directive'),
      client_ip: searchParams.get('client_ip'),
      hours: searchParams.get('hours'),
    });

    const supabase = await createClient();

    // æœŸé–“è¨­å®š
    const sinceTime = new Date();
    sinceTime.setHours(sinceTime.getHours() - params.hours);

    // ã‚¯ã‚¨ãƒªæ§‹ç¯‰
    let query = supabase
      .from('csp_violations')
      .select(
        `
        id,
        document_uri,
        violated_directive,
        blocked_uri,
        effective_directive,
        original_policy,
        disposition,
        line_number,
        column_number,
        source_file,
        script_sample,
        client_ip,
        user_agent,
        referrer,
        severity,
        threat_score,
        is_false_positive,
        created_at
      `
      )
      .gte('created_at', sinceTime.toISOString())
      .order('created_at', { ascending: false });

    // ãƒ•ã‚£ãƒ«ã‚¿ãƒ¼é©ç”¨
    if (params.severity) {
      query = query.eq('severity', params.severity);
    }

    if (params.directive) {
      query = query.ilike('violated_directive', `%${params.directive}%`);
    }

    if (params.client_ip) {
      query = query.eq('client_ip', params.client_ip);
    }

    // ãƒšãƒ¼ã‚¸ãƒãƒ¼ã‚·ãƒ§ãƒ³
    if (params.limit) {
      query = query.limit(params.limit);
    }

    if (params.offset) {
      query = query.range(params.offset, params.offset + params.limit - 1);
    }

    const { data: violations, error, count } = await query;

    if (error) {
      throw error;
    }

    // çµ±è¨ˆæƒ…å ±ã‚‚å«ã‚ã‚‹
    const statsQuery = await supabase
      .from('csp_violations')
      .select('severity', { count: 'exact' })
      .gte('created_at', sinceTime.toISOString());

    const severityStats =
      violations?.reduce(
        (acc, v) => {
          acc[v.severity] = (acc[v.severity] || 0) + 1;
          return acc;
        },
        {} as Record<string, number>
      ) || {};

    const response = {
      violations: violations || [],
      total_count: count || 0,
      params: {
        limit: params.limit,
        offset: params.offset,
        hours: params.hours,
        filters: {
          severity: params.severity,
          directive: params.directive,
          client_ip: params.client_ip,
        },
      },
      statistics: {
        severity_breakdown: severityStats,
        period_hours: params.hours,
        generated_at: new Date().toISOString(),
      },
    };

    return NextResponse.json(response);
  } catch (error) {
    console.error('CSPé•åä¸€è¦§å–å¾—ã‚¨ãƒ©ãƒ¼:', error);

    if (error instanceof z.ZodError) {
      return NextResponse.json(
        {
          error: 'ã‚¯ã‚¨ãƒªãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿ãŒç„¡åŠ¹ã§ã™',
          details: error.errors,
        },
        { status: 400 }
      );
    }

    return NextResponse.json(
      {
        error: 'CSPé•åä¸€è¦§ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ',
        details: error instanceof Error ? error.message : 'Unknown error',
      },
      { status: 500 }
    );
  }
}

/**
 * CSPé•åã®æ‰‹å‹•ãƒ¬ãƒ“ãƒ¥ãƒ¼ãƒ»æ›´æ–°
 */
export async function PATCH(request: NextRequest) {
  try {
