      },
      { status: 500 }
    );
  }
}

/**
 * ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ¡ãƒˆãƒªã‚¯ã‚¹ API
 * ä»•æ§˜æ›¸: docs/ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ç›£è¦–é‹ç”¨_MVPä»•æ§˜æ›¸.md
 *
 * GET /api/admin/security/metrics - ãƒ€ãƒƒã‚·ãƒ¥ãƒœãƒ¼ãƒ‰ç”¨ã®é›†è¨ˆå€¤ã‚’è¿”ã™
 */

import { NextRequest } from 'next/server';
import {
  createErrorResponse,
  createSuccessResponse,
  logError,
  processApiRequest,
} from '@/lib/api-helpers';
import { ADMIN_UI_ROLES } from '@/lib/constants/roles';

/**
 * GET /api/admin/security/metrics
 * ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ€ãƒƒã‚·ãƒ¥ãƒœãƒ¼ãƒ‰ç”¨ã®ãƒ¡ãƒˆãƒªã‚¯ã‚¹ã‚’å–å¾—
 */
export async function GET(request: NextRequest) {
  const { searchParams } = new URL(request.url);
  const clinicId = searchParams.get('clinic_id');
  const days = parseInt(searchParams.get('days') || '30', 10);

  try {
    // @spec docs/stabilization/spec-auth-role-alignment-v0.1.md (DOD-08)
    const processResult = await processApiRequest(request, {
      allowedRoles: Array.from(ADMIN_UI_ROLES),
      clinicId,
    });

    if (!processResult.success) {
      return processResult.error!;
    }

    const { supabase, auth } = processResult;

    // ãƒãƒªãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³
    if (!clinicId) {
      return createErrorResponse('clinic_idã¯å¿…é ˆã§ã™', 400);
    }

    const startDate = new Date();
    startDate.setDate(startDate.getDate() - Math.min(days, 90));

    // ä¸¦åˆ—ã§ãƒ¡ãƒˆãƒªã‚¯ã‚¹ã‚’å–å¾—
    const [
      eventsResult,
      sessionsResult,
      totalUsersResult,
      mfaEnabledResult,
      recentThreatsResult,
    ] = await Promise.all([
      // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã‚¤ãƒ™ãƒ³ãƒˆæ•°
      supabase
        .from('security_events')
        .select('*', { count: 'exact', head: true })
        .eq('clinic_id', clinicId)
        .gte('created_at', startDate.toISOString()),

      // ã‚¢ã‚¯ãƒ†ã‚£ãƒ–ã‚»ãƒƒã‚·ãƒ§ãƒ³æ•°
      supabase
        .from('user_sessions')
        .select('*', { count: 'exact', head: true })
        .eq('clinic_id', clinicId)
        .eq('is_active', true),

      // ç·ãƒ¦ãƒ¼ã‚¶ãƒ¼æ•°
      supabase
        .from('profiles')
        .select('id', { count: 'exact', head: true })
        .eq('clinic_id', clinicId),

      // MFAæœ‰åŠ¹ãƒ¦ãƒ¼ã‚¶ãƒ¼æ•°
      supabase
        .from('user_mfa_settings')
        .select('id', { count: 'exact', head: true })
        .eq('clinic_id', clinicId)
        .eq('is_enabled', true),

      // ç›´è¿‘ã®è„…å¨ã‚¤ãƒ™ãƒ³ãƒˆï¼ˆhigh/criticalï¼‰
      supabase
        .from('security_events')
        .select('*', { count: 'exact', head: true })
        .eq('clinic_id', clinicId)
        .in('severity_level', ['error', 'critical'])
        .gte('created_at', startDate.toISOString()),
    ]);

    // ãƒ–ãƒ­ãƒƒã‚¯ã•ã‚ŒãŸè©¦è¡Œæ•°ï¼ˆfailed_loginç³»ã®ã‚¤ãƒ™ãƒ³ãƒˆï¼‰
    const blockedResult = await supabase
      .from('security_events')
      .select('*', { count: 'exact', head: true })
      .eq('clinic_id', clinicId)
      .in('event_type', ['brute_force_blocked', 'login_blocked', 'ip_blocked'])
      .gte('created_at', startDate.toISOString());

    // æˆåŠŸãƒ­ã‚°ã‚¤ãƒ³æ•°
    const successfulLoginsResult = await supabase
      .from('security_events')
      .select('*', { count: 'exact', head: true })
      .eq('clinic_id', clinicId)
      .eq('event_type', 'login_success')
      .gte('created_at', startDate.toISOString());

    // ã‚¤ãƒ™ãƒ³ãƒˆã‚¿ã‚¤ãƒ—åˆ¥é›†è¨ˆ
    const { data: eventsByType } = await supabase
      .from('security_events')
      .select('event_type')
      .eq('clinic_id', clinicId)
      .gte('created_at', startDate.toISOString());

    const eventTypeCount: Record<string, number> = {};
    if (eventsByType) {
      for (const event of eventsByType) {
        eventTypeCount[event.event_type] =
          (eventTypeCount[event.event_type] || 0) + 1;
      }
    }

    // æ—¥åˆ¥é›†è¨ˆ
    const { data: eventsByDay } = await supabase
      .from('security_events')
      .select('created_at, severity_level')
      .eq('clinic_id', clinicId)
      .gte('created_at', startDate.toISOString())
      .order('created_at', { ascending: true });

    const dailyCounts: Record<string, { count: number; severity: string }> = {};
    if (eventsByDay) {
      for (const event of eventsByDay) {
        const date = new Date(event.created_at).toISOString().split('T')[0];
        if (!dailyCounts[date]) {
          dailyCounts[date] = { count: 0, severity: 'low' };
        }
        dailyCounts[date].count++;

        // æœ€ã‚‚é«˜ã„é‡è¦åº¦ã‚’ä¿æŒ
        const severityOrder = ['info', 'warning', 'error', 'critical'];
        const currentIdx = severityOrder.indexOf(dailyCounts[date].severity);
        const newIdx = severityOrder.indexOf(event.severity_level);
        if (newIdx > currentIdx) {
          dailyCounts[date].severity = event.severity_level;
        }
      }
    }

    const totalUsers = totalUsersResult.count || 0;
    const mfaEnabledUsers = mfaEnabledResult.count || 0;

    // ãƒ¡ãƒˆãƒªã‚¯ã‚¹æ§‹ç¯‰
    const metrics = {
      totalEvents: eventsResult.count || 0,
      activeSessions: sessionsResult.count || 0,
      totalUsers,
      mfaEnabledUsers,
      recentThreats: recentThreatsResult.count || 0,
      blockedAttempts: blockedResult.count || 0,
      successfulLogins: successfulLoginsResult.count || 0,
      eventsByType: eventTypeCount,
      eventsByDay: Object.entries(dailyCounts).map(([date, data]) => ({
        date,
        count: data.count,
        severity: data.severity,
      })),
      mfaPercentage:
        totalUsers > 0 ? Math.round((mfaEnabledUsers / totalUsers) * 100) : 0,
    };

    return createSuccessResponse(metrics);
  } catch (error) {
    logError(error, {
      endpoint: '/api/admin/security/metrics',
      method: 'GET',
      userId: 'unknown',
    });
    return createErrorResponse('ã‚µãƒ¼ãƒãƒ¼ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ', 500);
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { AppError, ERROR_CODES } from '../../../lib/error-handler';
import { ensureClinicAccess } from '@/lib/supabase/guards';
import type { SupabaseServerClient } from '@/lib/supabase';

const PATH = '/api/ai-comments';

export async function GET(request: NextRequest) {
  try {
    const searchParams = request.nextUrl.searchParams;
    const clinicId = searchParams.get('clinic_id');
    const date =
      searchParams.get('date') ?? new Date().toISOString().slice(0, 10);

    if (!clinicId) {
      return NextResponse.json(
        { error: 'clinic_id is required' },
        { status: 400 }
      );
    }

    const { supabase } = await ensureClinicAccess(request, PATH, clinicId);

    const { data, error } = await supabase
      .from('ai_comments')
      .select('*')
      .eq('clinic_id', clinicId)
      .eq('comment_date', date)
      .single();

    if (error && error.code !== 'PGRST116') {
      throw error;
    }

    if (!data) {
      const generatedComment = await generateDailyComment(
        supabase,
        clinicId,
        date
      );
      return NextResponse.json({
        success: true,
        data: generatedComment,
      });
    }

    return NextResponse.json({
      success: true,
      data: {
        id: data.id,
        summary: data.summary,
        highlights: data.good_points ? [data.good_points] : [],
        improvements: data.improvement_points ? [data.improvement_points] : [],
        suggestions: data.suggestion_for_tomorrow
          ? [data.suggestion_for_tomorrow]
          : [],
        created_at: data.created_at,
      },
    });
  } catch (error) {
    if (error instanceof AppError) {
      return NextResponse.json(
        { error: error.message },
        { status: error.statusCode }
      );
    }

    console.error('AI Comments GET error:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { clinic_id, date } = body;

    if (!clinic_id) {
      return NextResponse.json(
        { error: 'clinic_id is required' },
        { status: 400 }
      );
    }

    const { supabase } = await ensureClinicAccess(request, PATH, clinic_id, {
      allowedRoles: ['manager'],
    });

    const commentDate = date || new Date().toISOString().split('T')[0];
    const generatedComment = await generateDailyComment(
      supabase,
      clinic_id,
      commentDate
    );

    return NextResponse.json({
      success: true,
      data: generatedComment,
    });
  } catch (error) {
    if (error instanceof AppError) {
      return NextResponse.json(
        { error: error.message },
        { status: error.statusCode }
      );
    }

    console.error('AI Comments POST error:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

async function generateDailyComment(
  supabase: SupabaseServerClient,
  clinicId: string,
  date: string
) {
  try {
    const { data: dailyData, error: dailyError } = await supabase
      .from('daily_revenue_summary')
      .select('*')
      .eq('clinic_id', clinicId)
      .eq('revenue_date', date)
      .single();

    if (dailyError && dailyError.code !== 'PGRST116') {
      throw dailyError;
    }

    const yesterday = new Date(date);
    yesterday.setDate(yesterday.getDate() - 1);
    const { data: previousData, error: previousError } = await supabase
      .from('daily_revenue_summary')
      .select('*')
      .eq('clinic_id', clinicId)
      .eq('revenue_date', yesterday.toISOString().split('T')[0])
      .single();

    if (previousError && previousError.code !== 'PGRST116') {
      throw previousError;
    }

    const weekAgo = new Date(date);
    weekAgo.setDate(weekAgo.getDate() - 7);
    const { data: weeklyData, error: weeklyError } = await supabase
      .from('daily_revenue_summary')
      .select('*')
      .eq('clinic_id', clinicId)
      .gte('revenue_date', weekAgo.toISOString().split('T')[0])
      .lt('revenue_date', date);

    if (weeklyError) {
      throw weeklyError;
    }

    const analysis = analyzePerformance(
      dailyData,
      previousData,
      weeklyData || []
    );

    const { data: savedComment, error } = await supabase
      .from('ai_comments')
      .upsert(
        {
          clinic_id: clinicId,
          comment_date: date,
          summary: analysis.summary,
          good_points: analysis.highlights.join('\n'),
          improvement_points: analysis.improvements.join('\n'),
          suggestion_for_tomorrow: analysis.suggestions.join('\n'),
          raw_ai_response: analysis,
        },
        {
          onConflict: 'clinic_id,comment_date',
        }
      )
      .select()
      .single();

    if (error) {
      throw error;
    }

    return {
      id: savedComment.id,
      summary: analysis.summary,
      highlights: analysis.highlights,
      improvements: analysis.improvements,
      suggestions: analysis.suggestions,
      created_at: savedComment.created_at,
    };
  } catch (error) {
    console.error('Generate daily comment error:', error);
    throw new AppError(
      ERROR_CODES.INTERNAL_SERVER_ERROR,
      'Failed to generate AI comment',
      500,
      { clinicId, date }
    );
  }
}

function analyzePerformance(
  dailyData: any,
  previousData: any,
  weeklyData: any[]
) {
  const todayRevenue = dailyData?.total_revenue || 0;
  const todayPatients = dailyData?.unique_patients || 0;
  const yesterdayRevenue = previousData?.total_revenue || 0;
  const yesterdayPatients = previousData?.unique_patients || 0;

  const weeklyAvgRevenue =
    weeklyData.length > 0
      ? weeklyData.reduce((sum, day) => sum + (day.total_revenue || 0), 0) /
        weeklyData.length
      : 0;
  const weeklyAvgPatients =
    weeklyData.length > 0
      ? weeklyData.reduce((sum, day) => sum + (day.unique_patients || 0), 0) /
        weeklyData.length
      : 0;

  let summary = '';
  const highlights: string[] = [];
  const improvements: string[] = [];
  const suggestions: string[] = [];

  if (todayRevenue > yesterdayRevenue) {
    const increase = (
      ((todayRevenue - yesterdayRevenue) / Math.max(yesterdayRevenue, 1)) *
      100
    ).toFixed(1);
    highlights.push(`å‰æ—¥æ¯”å£²ä¸ŠãŒ${increase}%å‘ä¸Šã—ã¾ã—ãŸ`);
  } else if (todayRevenue < yesterdayRevenue) {
    const decrease = (
      ((yesterdayRevenue - todayRevenue) / Math.max(yesterdayRevenue, 1)) *
      100
    ).toFixed(1);
    improvements.push(`å‰æ—¥æ¯”å£²ä¸ŠãŒ${decrease}%ä½ä¸‹ã—ã¦ã„ã‚‹å¯èƒ½æ€§ãŒã‚ã‚Šã¾ã™`);
  }

  if (todayPatients > yesterdayPatients) {
    highlights.push('æ‚£è€…æ•°ãŒå‰æ—¥ã‚ˆã‚Šå¢—åŠ ã—ã¦ã„ã¾ã™');
  }

  if (todayPatients < yesterdayPatients) {
    improvements.push('æ‚£è€…æ•°ãŒå‰æ—¥ã‚ˆã‚Šæ¸›å°‘ã—ã¦ã„ã¾ã™');
  }

  if (weeklyAvgRevenue > 0) {
    const diff = todayRevenue - weeklyAvgRevenue;
    if (diff > 0) {
      highlights.push('é€±é–“å¹³å‡ã‚’ä¸Šå›ã‚‹å£²ä¸Šã‚’è¨˜éŒ²ã—ã¾ã—ãŸ');
    } else if (diff < 0) {
      improvements.push(
        'é€±é–“å¹³å‡ã‚’ä¸‹å›ã£ã¦ã„ã¾ã™ã€‚æ–½ç­–ã®è¦‹ç›´ã—ã‚’æ¤œè¨ã—ã¦ãã ã•ã„'
      );
    }
  }

  if (!summary) {
    summary =
      'æœ¬æ—¥ã®å£²ä¸Šã¨æ‚£è€…æ•°ã‚’åˆ†æã—ã¾ã—ãŸã€‚è©³ç´°ã¯ãƒã‚¤ãƒ©ã‚¤ãƒˆã¨æ”¹å–„ææ¡ˆã‚’ã”ç¢ºèªãã ã•ã„ã€‚';
  }

  if (suggestions.length === 0) {
    suggestions.push(
      'ã‚¹ã‚¿ãƒƒãƒ•ã¨ã®å…±æœ‰ãƒŸãƒ¼ãƒ†ã‚£ãƒ³ã‚°ã§å¥½èª¿ãƒ»ä¸èª¿è¦å› ã‚’ç¢ºèªã—ã¦ãã ã•ã„'
    );
  }

  return {
    summary,
    highlights,
    improvements,
    suggestions,
  };
}

import { NextRequest } from 'next/server';
import { PatientAnalysisData, PatientRiskScore } from '@/types/api';
import {
  normalizeSupabaseError,
  createApiError,
  ERROR_CODES,
  AppError,
  logError,
} from '@/lib/error-handler';
import { ensureClinicAccess } from '@/lib/supabase/guards';
import { AuditLogger, getRequestInfo } from '@/lib/audit-logger';
import { createErrorResponse, createSuccessResponse } from '@/lib/api-helpers';
import { z } from 'zod';

interface PatientVisitSummaryRow {
  patient_id: string;
  patient_name: string;
  visit_count: number;
  total_revenue: number;
  last_visit_date: string | null;
  visit_category: string | null;
}

const analysisQuerySchema = z.object({
  clinic_id: z.string().uuid('clinic_id ã¯UUIDå½¢å¼ã§æŒ‡å®šã—ã¦ãã ã•ã„'),
  analysis: z.enum(['conversion', 'ltv', 'churn', 'segment']).optional(),
});

export async function GET(request: NextRequest) {
  const path = '/api/customers/analysis';
  const { ipAddress, userAgent } = getRequestInfo(request);

  try {
    const rawParams = {
      clinic_id: request.nextUrl.searchParams.get('clinic_id'),
      analysis: request.nextUrl.searchParams.get('analysis') ?? undefined,
    };

    const parsedQuery = analysisQuerySchema.safeParse(rawParams);
    if (!parsedQuery.success) {
      return createErrorResponse(
        'å…¥åŠ›å€¤ã«ã‚¨ãƒ©ãƒ¼ãŒã‚ã‚Šã¾ã™',
        400,
        parsedQuery.error.flatten()
      );
    }

    const { clinic_id, analysis } = parsedQuery.data;

    const { supabase, user } = await ensureClinicAccess(
      request,
      path,
      clinic_id,
      {
        requireClinicMatch: true,
      }
    );

    await AuditLogger.logDataAccess(
      user.id,
      user.email || '',
      'patient_visit_summary',
      clinic_id,
      clinic_id,
      ipAddress,
      {
        analysis_type: analysis,
        request_params: rawParams,
      }
    );

    const { data: patients, error: patientsError } = await supabase
      .from('patient_visit_summary')
      .select('*')
      .eq('clinic_id', clinic_id);

    if (patientsError) {
      throw normalizeSupabaseError(patientsError, path);
    }

    const typedPatients = (patients as PatientVisitSummaryRow[]) ?? [];

    const conversionAnalysis = () => {
      const newPatients = typedPatients.filter(p => p.visit_count === 1);
      const returnPatients = typedPatients.filter(p => p.visit_count > 1);
      const total = newPatients.length + returnPatients.length;
      const conversionRate =
        total > 0
          ? Math.round((returnPatients.length / total) * 100 * 100) / 100
          : 0;

      return {
        newPatients: newPatients.length,
        returnPatients: returnPatients.length,
        conversionRate,
        stages: [
          { name: 'åˆå›æ¥é™¢', value: total },
          { name: '2å›ç›®æ¥é™¢', value: returnPatients.length },
          {
            name: 'ç¶™ç¶šé€šé™¢',
            value: typedPatients.filter(p => p.visit_count >= 5).length,
          },
        ],
      };
    };

    const ltvRanking = await Promise.all(
