'use client';

import React, { useEffect, useState } from 'react';
import Link from 'next/link';
import {
  Card,
  CardHeader,
  CardTitle,
  CardDescription,
  CardContent,
} from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { api, isSuccessResponse } from '@/lib/api-client';
import { useUserProfileContext } from '@/providers/user-profile-context';

type ReportRow = {
  id: string | number;
  date: string;
  patients: number;
  revenue: number;
};

type Summary = {
  totalReports: number;
  averagePatients: number;
  averageRevenue: number;
  totalRevenue: number;
};

type MonthlyTrend = {
  month: string;
  reports: number;
  totalPatients: number;
  totalRevenue: number;
};

const Page: React.FC = () => {
  const {
    profile,
    loading: profileLoading,
    error: profileError,
  } = useUserProfileContext();
  const clinicId = profile?.clinicId ?? null;

  const [rows, setRows] = useState<ReportRow[]>([]);
  const [summary, setSummary] = useState<Summary | null>(null);
  const [monthlyTrends, setMonthlyTrends] = useState<MonthlyTrend[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    let cancelled = false;

    const fetchReports = async () => {
      if (!clinicId) {
        setRows([]);
        setLoading(false);
        return;
      }

      setLoading(true);
      setError(null);

      try {
        const res = await api.dailyReports.get(clinicId);
        const data = (res as any)?.data as any;
        if (isSuccessResponse(res) && data?.reports) {
          const mapped: ReportRow[] = data.reports.map(
            (r: any, idx: number) => ({
              id: r.id || idx,
              date: r.reportDate,
              patients: r.totalPatients || 0,
              revenue: Number(r.totalRevenue || 0),
