 */
export async function acceptInvite(
  token: string
): Promise<{ success: boolean; error?: string }> {
  const supabase = await getServerClient();

  try {
    // ç¾åœ¨ã®ãƒ¦ãƒ¼ã‚¶ãƒ¼ã‚’ç¢ºèª
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser();

    if (userError || !user) {
      return { success: false, error: 'ãƒ­ã‚°ã‚¤ãƒ³ãŒå¿…è¦ã§ã™' };
    }

    // accept_invite RPC ã‚’å‘¼ã³å‡ºã—
    const { data, error } = await supabase.rpc('accept_invite', {
      invite_token: token,
    });

    if (error) {
      console.error('[Invite] accept_invite error:', error);
      return { success: false, error: 'æ‹›å¾…ã®å—è«¾ã«å¤±æ•—ã—ã¾ã—ãŸ' };
    }

    if (!data?.success) {
      return {
        success: false,
        error: data?.error || 'æ‹›å¾…ã®å—è«¾ã«å¤±æ•—ã—ã¾ã—ãŸ',
      };
    }

    console.info('[Auth] Invite accepted:', {
      userId: user.id,
      clinicId: data.clinic_id,
      timestamp: new Date().toISOString(),
    });

    revalidatePath('/', 'layout');
    return { success: true };
  } catch (error) {
    console.error('[Invite] acceptInvite error:', error);
    return { success: false, error: GENERIC_AUTH_ERROR_MESSAGE };
  }
}

/**
