 */
export async function signupAndAcceptInvite(
  _: AuthResponse,
  formData: FormData
): Promise<AuthResponse> {
  const supabase = await getServerClient();

  try {
    const { email, password, token } = extractAuthFormValues(formData);

    // 1. ãƒãƒªãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³
    const parsed = signupSchema.safeParse({ email, password });
    if (!parsed.success) {
      const { fieldErrors } = parsed.error.flatten();
      return {
        success: false,
        errors: fieldErrors,
      };
    }

    if (!token) {
      return {
        success: false,
        errors: { _form: ['æ‹›å¾…ãƒˆãƒ¼ã‚¯ãƒ³ãŒå¿…è¦ã§ã™'] },
      };
    }

    const sanitizedEmail = sanitizeAuthInput(parsed.data.email).toLowerCase();
    const sanitizedPassword = sanitizeAuthInput(parsed.data.password);

    // 2. ã‚µã‚¤ãƒ³ã‚¢ãƒƒãƒ—
    const { error: signupError, data: signupData } = await supabase.auth.signUp(
      {
        email: sanitizedEmail,
        password: sanitizedPassword,
        options: {
          emailRedirectTo: `${process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000'}/invite?token=${token}`,
        },
      }
    );

    if (signupError) {
      console.error('[Invite] Signup error:', signupError);
      const errorMessage = signupError.message.includes('already registered')
        ? 'ã“ã®ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹ã¯æ—¢ã«ç™»éŒ²ã•ã‚Œã¦ã„ã¾ã™ã€‚ãƒ­ã‚°ã‚¤ãƒ³ã—ã¦ãã ã•ã„ã€‚'
        : 'ã‚¢ã‚«ã‚¦ãƒ³ãƒˆã®ä½œæˆã«å¤±æ•—ã—ã¾ã—ãŸ';

      return {
        success: false,
        errors: { _form: [errorMessage] },
      };
    }

    // 3. ãƒ—ãƒ­ãƒ•ã‚¡ã‚¤ãƒ«ä½œæˆã‚’å¾…ã¤ï¼ˆãƒˆãƒªã‚¬ãƒ¼ã§è‡ªå‹•ä½œæˆã•ã‚Œã‚‹å ´åˆï¼‰
    // ã‚‚ã—ãã¯æ‰‹å‹•ã§ä½œæˆ
    if (signupData.user) {
      // profiles ã«ãƒ¬ã‚³ãƒ¼ãƒ‰ãŒãªã„å ´åˆã¯ä½œæˆ
      const { data: existingProfile } = await supabase
        .from('profiles')
        .select('id')
        .eq('user_id', signupData.user.id)
        .single();

      if (!existingProfile) {
        await supabase.from('profiles').insert({
          user_id: signupData.user.id,
          email: sanitizedEmail,
          full_name: sanitizedEmail.split('@')[0],
          role: 'staff',
          is_active: true,
        });
      }

      // 4. æ‹›å¾…ã‚’å—è«¾
      const { data: acceptData, error: acceptError } = await supabase.rpc(
        'accept_invite',
        { invite_token: token }
      );

      if (acceptError || !acceptData?.success) {
        console.error(
          '[Invite] Accept invite after signup error:',
          acceptError
        );
        // ã‚µã‚¤ãƒ³ã‚¢ãƒƒãƒ—ã¯æˆåŠŸã—ã¦ã„ã‚‹ã®ã§ã€å¾Œã§æ‹›å¾…ã‚’å—è«¾ã§ãã‚‹ã‚ˆã†ã«ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã‚’è¿”ã™
        return {
          success: true,
          message:
            'ã‚¢ã‚«ã‚¦ãƒ³ãƒˆã‚’ä½œæˆã—ã¾ã—ãŸã€‚ãƒ¡ãƒ¼ãƒ«ã‚’ç¢ºèªã—ã¦ã‹ã‚‰å†åº¦æ‹›å¾…ãƒªãƒ³ã‚¯ã«ã‚¢ã‚¯ã‚»ã‚¹ã—ã¦ãã ã•ã„ã€‚',
        };
      }

      console.info('[Auth] Signup and invite accepted:', {
        userId: signupData.user.id,
        email: sanitizedEmail,
        clinicId: acceptData.clinic_id,
        timestamp: new Date().toISOString(),
      });

      revalidatePath('/', 'layout');
      redirect('/dashboard');
    }

    return {
      success: true,
      message:
        'ç¢ºèªãƒ¡ãƒ¼ãƒ«ã‚’é€ä¿¡ã—ã¾ã—ãŸã€‚ãƒ¡ãƒ¼ãƒ«ã‚’ç¢ºèªã—ã¦ã‹ã‚‰ãƒ­ã‚°ã‚¤ãƒ³ã—ã¦ãã ã•ã„ã€‚',
    };
  } catch (error) {
    if (isRedirectLikeError(error)) {
      throw error;
    }
    console.error('[Invite] signupAndAcceptInvite error:', error);
    return {
      success: false,
