}

/**
 * æ‹›å¾…å—è«¾ï¼‹ãƒ­ã‚°ã‚¤ãƒ³ï¼ˆæ—¢å­˜ãƒ¦ãƒ¼ã‚¶ãƒ¼ç”¨ï¼‰
 */
export async function loginAndAcceptInvite(
  _: AuthResponse,
  formData: FormData
): Promise<AuthResponse> {
  const supabase = await getServerClient();
  const headerList = await headers();
  const { ipAddress, userAgent } = getRequestInfoFromHeaders(headerList);

  try {
    const { email, password, token } = extractAuthFormValues(formData);

    // 1. ãƒãƒªãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³
    const parsed = loginSchema.safeParse({ email, password });
    if (!parsed.success) {
      const { fieldErrors } = parsed.error.flatten();
      return {
        success: false,
        errors: fieldErrors,
      };
    }

    if (!token) {
      return {
        success: false,
        errors: { _form: ['æ‹›å¾…ãƒˆãƒ¼ã‚¯ãƒ³ãŒå¿…è¦ã§ã™'] },
      };
    }

    const sanitizedEmail = sanitizeAuthInput(parsed.data.email).toLowerCase();
    const sanitizedPassword = sanitizeAuthInput(parsed.data.password);

    // 2. ãƒ­ã‚°ã‚¤ãƒ³
    const { error: loginError, data: loginData } =
      await supabase.auth.signInWithPassword({
        email: sanitizedEmail,
        password: sanitizedPassword,
      });

    if (loginError) {
      console.error('[Invite] Login error:', loginError);
      await AuditLogger.logFailedLogin(
        sanitizedEmail,
        ipAddress,
        userAgent,
        loginError.message
      );
      return {
        success: false,
        errors: {
          password: ['ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹ã¾ãŸã¯ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰ãŒæ­£ã—ãã‚ã‚Šã¾ã›ã‚“'],
          _form: ['ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹ã¾ãŸã¯ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰ãŒæ­£ã—ãã‚ã‚Šã¾ã›ã‚“'],
        },
      };
    }

    if (!loginData.user) {
      return {
        success: false,
        errors: { _form: ['ãƒ­ã‚°ã‚¤ãƒ³ã«å¤±æ•—ã—ã¾ã—ãŸ'] },
      };
    }

    await AuditLogger.logLogin(
      loginData.user.id,
      sanitizedEmail,
      ipAddress,
      userAgent
    );

    // 3. æ‹›å¾…ã‚’å—è«¾
    const { data: acceptData, error: acceptError } = await supabase.rpc(
      'accept_invite',
      { invite_token: token }
    );

    if (acceptError || !acceptData?.success) {
      console.error('[Invite] Accept invite after login error:', acceptError);
      return {
        success: false,
        errors: { _form: [acceptData?.error || 'æ‹›å¾…ã®å—è«¾ã«å¤±æ•—ã—ã¾ã—ãŸ'] },
      };
    }

    // 4. last_login_at ã‚’æ›´æ–°
    await supabase
      .from('profiles')
      .update({ last_login_at: new Date().toISOString() })
      .eq('user_id', loginData.user.id);

    console.info('[Auth] Login and invite accepted:', {
      userId: loginData.user.id,
      email: sanitizedEmail,
      clinicId: acceptData.clinic_id,
      timestamp: new Date().toISOString(),
    });

    revalidatePath('/', 'layout');
    redirect('/dashboard');
  } catch (error) {
    if (isRedirectLikeError(error)) {
      throw error;
    }
    console.error('[Invite] loginAndAcceptInvite error:', error);
    return {
      success: false,
      errors: { _form: [GENERIC_AUTH_ERROR_MESSAGE] },
    };
  }
}

