function coerceValue(type: string, raw: string): unknown {
  switch (type) {
    case 'number':
      return Number(raw);
    case 'boolean':
      return raw === 'true';
    case 'json':
    case 'array':
      return JSON.parse(raw || 'null');
    default:
      return raw;
  }
}

