export const SecurityDashboard: React.FC<SecurityDashboardProps> = ({
  clinicId,
}) => {
  const [metrics, setMetrics] = useState<SecurityMetrics>({
    totalUsers: 0,
    mfaEnabledUsers: 0,
    activeSessions: 0,
    recentThreats: 0,
    blockedAttempts: 0,
    successfulLogins: 0,
