  });

  const [securityEvents, setSecurityEvents] = useState<SecurityEvent[]>([]);
  const [activeSessions, setActiveSessions] = useState<SessionInfo[]>([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);

  // ã‚¤ãƒ™ãƒ³ãƒˆæ›´æ–°ç”¨ãƒ¢ãƒ¼ãƒ€ãƒ«
  const [selectedEvent, setSelectedEvent] = useState<SecurityEvent | null>(
    null
  );
  const [showEventModal, setShowEventModal] = useState(false);
  const [eventStatus, setEventStatus] = useState<string>('');
  const [resolutionNotes, setResolutionNotes] = useState('');
  const [updating, setUpdating] = useState(false);

  // ãƒ•ã‚£ãƒ«ã‚¿ãƒ¼
  const [statusFilter, setStatusFilter] = useState<string>('all');

  // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ¡ãƒˆãƒªã‚¯ã‚¹å–å¾—
  const fetchSecurityMetrics = useCallback(async () => {
    try {
      const response = await fetch(
        `/api/admin/security/metrics?clinic_id=${clinicId}`
      );
      if (response.ok) {
        const data = await response.json();
        setMetrics(data.data || data);
      }
    } catch (error) {
      console.error('ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ¡ãƒˆãƒªã‚¯ã‚¹å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
    }
  }, [clinicId]);

  // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã‚¤ãƒ™ãƒ³ãƒˆå–å¾—
  const fetchSecurityEvents = useCallback(async () => {
    try {
      let url = `/api/admin/security/events?clinic_id=${clinicId}`;
      if (statusFilter && statusFilter !== 'all') {
        url += `&status=${statusFilter}`;
      }
      const response = await fetch(url);
      if (response.ok) {
        const data = await response.json();
        setSecurityEvents(data.events || data.data?.events || []);
      }
    } catch (error) {
      console.error('ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã‚¤ãƒ™ãƒ³ãƒˆå–å¾—ã‚¨ãƒ©ãƒ¼:', error);
    }
  }, [clinicId, statusFilter]);

  // ã‚¢ã‚¯ãƒ†ã‚£ãƒ–ã‚»ãƒƒã‚·ãƒ§ãƒ³å–å¾—
  const fetchActiveSessions = useCallback(async () => {
    try {
      const response = await fetch(
        `/api/admin/security/sessions?clinic_id=${clinicId}`
      );
      if (response.ok) {
        const data = await response.json();
        setActiveSessions(data.sessions || data.data?.sessions || []);
      }
    } catch (error) {
      console.error('ã‚¢ã‚¯ãƒ†ã‚£ãƒ–ã‚»ãƒƒã‚·ãƒ§ãƒ³å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
    }
  }, [clinicId]);

  // ãƒ‡ãƒ¼ã‚¿ãƒªãƒ•ãƒ¬ãƒƒã‚·ãƒ¥
  const handleRefresh = async () => {
    setRefreshing(true);
    await Promise.all([
      fetchSecurityMetrics(),
      fetchSecurityEvents(),
      fetchActiveSessions(),
    ]);
    setRefreshing(false);
  };

  // ã‚¤ãƒ™ãƒ³ãƒˆã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹æ›´æ–°
  const handleUpdateEventStatus = async () => {
    if (!selectedEvent) return;

    setUpdating(true);
    try {
      const response = await fetch('/api/admin/security/events', {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          id: selectedEvent.id,
          status: eventStatus,
          resolution_notes: resolutionNotes,
        }),
      });

      if (response.ok) {
        await fetchSecurityEvents();
        setShowEventModal(false);
        setSelectedEvent(null);
        setEventStatus('');
        setResolutionNotes('');
      }
    } catch (error) {
      console.error('ã‚¤ãƒ™ãƒ³ãƒˆæ›´æ–°ã‚¨ãƒ©ãƒ¼:', error);
    } finally {
      setUpdating(false);
    }
  };

  // ã‚¤ãƒ™ãƒ³ãƒˆç·¨é›†ãƒ¢ãƒ¼ãƒ€ãƒ«ã‚’é–‹ã
  const openEventModal = (event: SecurityEvent) => {
    setSelectedEvent(event);
    setEventStatus(event.status);
    setResolutionNotes(event.resolution_notes || '');
    setShowEventModal(true);
  };

  // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ¬ãƒãƒ¼ãƒˆãƒ€ã‚¦ãƒ³ãƒ­ãƒ¼ãƒ‰
  const handleDownloadReport = () => {
    const csvContent = [
      'ã‚¤ãƒ™ãƒ³ãƒˆã‚¿ã‚¤ãƒ—,é‡è¦åº¦,èª¬æ˜,IPã‚¢ãƒ‰ãƒ¬ã‚¹,æ—¥æ™‚,ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹,è§£æ±ºãƒ¡ãƒ¢',
      ...securityEvents.map(
        event =>
          `${event.event_type},${event.severity_level},"${event.event_description}",${event.ip_address || ''},${new Date(event.created_at).toLocaleString()},${event.status},"${event.resolution_notes || ''}"`
      ),
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = `security_report_${new Date().toISOString().split('T')[0]}.csv`;
    link.click();
  };

  // ã‚»ãƒƒã‚·ãƒ§ãƒ³å¼·åˆ¶çµ‚äº†
  const handleTerminateSession = async (sessionId: string) => {
    if (!confirm('ã“ã®ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’å¼·åˆ¶çµ‚äº†ã—ã¾ã™ã‹ï¼Ÿ')) {
      return;
    }

    try {
      const response = await fetch('/api/admin/security/sessions/terminate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ sessionId }),
      });

      if (response.ok) {
        await fetchActiveSessions();
        await fetchSecurityMetrics();
      }
    } catch (error) {
      console.error('ã‚»ãƒƒã‚·ãƒ§ãƒ³çµ‚äº†ã‚¨ãƒ©ãƒ¼:', error);
    }
  };

  // é‡è¦åº¦åˆ¥ã‚¹ã‚¿ã‚¤ãƒ«å–å¾—
  const getSeverityStyle = (severity: string) => {
    switch (severity) {
      case 'critical':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'error':
        return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'warning':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'info':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  // ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹åˆ¥ã‚¹ã‚¿ã‚¤ãƒ«å–å¾—
  const getStatusStyle = (status: string) => {
    switch (status) {
      case 'new':
        return 'bg-red-100 text-red-700';
      case 'investigating':
        return 'bg-yellow-100 text-yellow-700';
      case 'resolved':
        return 'bg-green-100 text-green-700';
      case 'false_positive':
        return 'bg-gray-100 text-gray-700';
      default:
        return 'bg-gray-100 text-gray-700';
    }
  };

  // ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ãƒ©ãƒ™ãƒ«
  const getStatusLabel = (status: string) => {
    const labels: Record<string, string> = {
      new: 'æ–°è¦',
      investigating: 'èª¿æŸ»ä¸­',
      resolved: 'è§£æ±ºæ¸ˆã¿',
      false_positive: 'èª¤æ¤œçŸ¥',
    };
    return labels[status] || status;
  };

  // ãƒªã‚¹ã‚¯ãƒ¬ãƒ™ãƒ«åˆ¥ã‚¹ã‚¿ã‚¤ãƒ«å–å¾—
  const getRiskLevelStyle = (riskLevel: string) => {
    switch (riskLevel) {
      case 'high':
        return 'bg-red-100 text-red-700';
      case 'medium':
        return 'bg-yellow-100 text-yellow-700';
      case 'low':
        return 'bg-green-100 text-green-700';
      default:
        return 'bg-gray-100 text-gray-700';
    }
  };

  useEffect(() => {
    const initializeDashboard = async () => {
      setLoading(true);
      await Promise.all([
        fetchSecurityMetrics(),
        fetchSecurityEvents(),
        fetchActiveSessions(),
      ]);
      setLoading(false);
    };

    initializeDashboard();

    // 30ç§’ã”ã¨ã«è‡ªå‹•æ›´æ–°
