'use client';

import React, { useState, useEffect, useMemo } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
  DialogFooter,
} from '@/components/ui/dialog';

import { Textarea } from '@/components/ui/textarea';
import { Switch } from '@/components/ui/switch';
import { Save, X } from 'lucide-react';
import { DataFormDialogProps, FormField } from '@/types/admin';

export const DataFormDialog: React.FC<DataFormDialogProps> = ({
  open,
  mode,
  formData,
  config,
  loading,
  onSubmit,
  onClose,
  onFieldChange,
}) => {
  const [errors, setErrors] = useState<Record<string, string>>({});

  // ãƒ•ã‚©ãƒ¼ãƒ ãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰ã®ç”Ÿæˆ
  const formFields: FormField[] = useMemo(() => {
    if (!config?.columns) return [];

    return Object.entries(config.columns)
      .filter(([key, columnConfig]) => {
        // èª­ã¿å–ã‚Šå°‚ç”¨ãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰ã¯ç·¨é›†æ™‚ã®ã¿è¡¨ç¤º
        if (columnConfig.readonly && mode === 'create') return false;
        // created_at, updated_atã¯é™¤å¤–
        if (['created_at', 'updated_at'].includes(key)) return false;
        return true;
      })
      .map(([key, columnConfig]) => ({
        name: key,
        type: columnConfig.type,
        label: columnConfig.label || key,
        required: columnConfig.required || false,
        readonly: columnConfig.readonly || false,
        value:
          formData[key] ||
          getDefaultValue(columnConfig.type, columnConfig.default),
        maxLength: columnConfig.maxLength,
        min: columnConfig.min,
        max: columnConfig.max,
      }));
  }, [config, formData, mode]);

  // ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆå€¤ã®å–å¾—
  function getDefaultValue(type: string, defaultValue?: any) {
    if (defaultValue !== undefined) return defaultValue;

    switch (type) {
      case 'boolean':
        return false;
      case 'integer':
      case 'decimal':
        return 0;
      case 'string':
      case 'text':
      case 'uuid':
      case 'timestamp':
      default:
        return '';
    }
  }

  // ãƒãƒªãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³
  const validateField = (field: FormField): string | null => {
    const { name, type, required, value, maxLength, min, max } = field;

    // å¿…é ˆãƒã‚§ãƒƒã‚¯
    if (required && (value === '' || value === null || value === undefined)) {
      return `${field.label}ã¯å¿…é ˆé …ç›®ã§ã™`;
    }

    // å€¤ãŒç©ºã®å ´åˆã¯ä»¥é™ã®ãƒã‚§ãƒƒã‚¯ã‚’ã‚¹ã‚­ãƒƒãƒ—
    if (value === '' || value === null || value === undefined) {
      return null;
    }

    // å‹ãƒã‚§ãƒƒã‚¯
    switch (type) {
      case 'integer':
        if (!Number.isInteger(Number(value))) {
          return `${field.label}ã¯æ•´æ•°ã§å…¥åŠ›ã—ã¦ãã ã•ã„`;
        }
        break;
      case 'decimal':
        if (isNaN(Number(value))) {
          return `${field.label}ã¯æ•°å€¤ã§å…¥åŠ›ã—ã¦ãã ã•ã„`;
        }
        break;
      case 'uuid':
        const uuidRegex =
          /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
        if (typeof value === 'string' && value && !uuidRegex.test(value)) {
          return `${field.label}ã¯æ­£ã—ã„UUIDå½¢å¼ã§å…¥åŠ›ã—ã¦ãã ã•ã„`;
        }
        break;
    }

    // é•·ã•ãƒã‚§ãƒƒã‚¯
    if (maxLength && typeof value === 'string' && value.length > maxLength) {
      return `${field.label}ã¯${maxLength}æ–‡å­—ä»¥å†…ã§å…¥åŠ›ã—ã¦ãã ã•ã„`;
    }

    // ç¯„å›²ãƒã‚§ãƒƒã‚¯
    if (min !== undefined && Number(value) < min) {
      return `${field.label}ã¯${min}ä»¥ä¸Šã§å…¥åŠ›ã—ã¦ãã ã•ã„`;
    }
    if (max !== undefined && Number(value) > max) {
      return `${field.label}ã¯${max}ä»¥ä¸‹ã§å…¥åŠ›ã—ã¦ãã ã•ã„`;
    }

    return null;
  };

  // ãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰å€¤å¤‰æ›´å‡¦ç†
  const handleFieldChange = (name: string, value: any) => {
    onFieldChange(name, value);

    // ã‚¨ãƒ©ãƒ¼ã‚’ã‚¯ãƒªã‚¢
    if (errors[name]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[name];
        return newErrors;
      });
    }
  };

  // ãƒ•ã‚©ãƒ¼ãƒ é€ä¿¡å‡¦ç†
  const handleSubmit = () => {
    // ãƒãƒªãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³å®Ÿè¡Œ
    const newErrors: Record<string, string> = {};
    formFields.forEach(field => {
      const error = validateField(field);
      if (error) {
        newErrors[field.name] = error;
      }
    });

    setErrors(newErrors);

    // ã‚¨ãƒ©ãƒ¼ãŒã‚ã‚‹å ´åˆã¯é€ä¿¡ã—ãªã„
    if (Object.keys(newErrors).length > 0) {
      return;
    }

    onSubmit(formData);
  };

  // ãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰ãƒ¬ãƒ³ãƒ€ãƒªãƒ³ã‚°
  const renderField = (field: FormField) => {
    const { name, type, label, required, readonly, value } = field;
    const error = errors[name];

    if (readonly && mode === 'edit') {
      return (
        <div key={name} className='space-y-2'>
          <Label htmlFor={name}>
            {label} {required && <span className='text-red-500'>*</span>}
          </Label>
          <Input
            id={name}
            value={String(value || '')}
            readOnly
            className='bg-muted'
          />
        </div>
      );
    }

    switch (type) {
      case 'boolean':
        return (
          <div key={name} className='flex items-center space-x-2'>
            <Switch
              id={name}
              checked={Boolean(value)}
              onCheckedChange={checked => handleFieldChange(name, checked)}
              disabled={readonly}
            />
            <Label htmlFor={name}>
              {label} {required && <span className='text-red-500'>*</span>}
            </Label>
          </div>
        );

      case 'text':
        return (
          <div key={name} className='space-y-2'>
            <Label htmlFor={name}>
              {label} {required && <span className='text-red-500'>*</span>}
            </Label>
            <Textarea
              id={name}
              value={String(value || '')}
              onChange={e => handleFieldChange(name, e.target.value)}
              maxLength={field.maxLength}
              disabled={readonly}
              className={error ? 'border-red-500' : ''}
            />
            {error && <p className='text-sm text-red-500'>{error}</p>}
          </div>
        );

      case 'integer':
      case 'decimal':
        return (
          <div key={name} className='space-y-2'>
            <Label htmlFor={name}>
              {label} {required && <span className='text-red-500'>*</span>}
            </Label>
            <Input
              id={name}
              type='number'
              value={String(value || '')}
              onChange={e => {
                const val =
                  type === 'integer'
                    ? parseInt(e.target.value) || 0
                    : parseFloat(e.target.value) || 0;
                handleFieldChange(name, val);
              }}
              min={field.min}
              max={field.max}
              step={type === 'integer' ? 1 : 0.01}
              disabled={readonly}
              className={error ? 'border-red-500' : ''}
            />
            {error && <p className='text-sm text-red-500'>{error}</p>}
          </div>
        );

      default:
        return (
          <div key={name} className='space-y-2'>
            <Label htmlFor={name}>
              {label} {required && <span className='text-red-500'>*</span>}
            </Label>
            <Input
              id={name}
              type={type === 'timestamp' ? 'datetime-local' : 'text'}
              value={String(value || '')}
              onChange={e => handleFieldChange(name, e.target.value)}
              maxLength={field.maxLength}
              disabled={readonly}
              className={error ? 'border-red-500' : ''}
            />
            {error && <p className='text-sm text-red-500'>{error}</p>}
          </div>
        );
    }
  };

  // ãƒ€ã‚¤ã‚¢ãƒ­ã‚°ãŒé–‰ã˜ã‚‰ã‚ŒãŸæ™‚ã«ã‚¨ãƒ©ãƒ¼ã‚’ã‚¯ãƒªã‚¢
  useEffect(() => {
    if (!open) {
      setErrors({});
    }
  }, [open]);

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className='max-w-2xl max-h-[80vh] overflow-y-auto'>
        <DialogHeader>
          <DialogTitle>
            {mode === 'create' ? 'æ–°è¦ä½œæˆ' : 'ç·¨é›†'} - {config?.name}
          </DialogTitle>
          <DialogDescription>
            {mode === 'create'
              ? 'æ–°ã—ã„ãƒ¬ã‚³ãƒ¼ãƒ‰ã‚’ä½œæˆã—ã¾ã™ã€‚å¿…é ˆé …ç›®ã¯å¿…ãšå…¥åŠ›ã—ã¦ãã ã•ã„ã€‚'
              : 'é¸æŠã—ãŸãƒ¬ã‚³ãƒ¼ãƒ‰ã‚’ç·¨é›†ã—ã¾ã™ã€‚å¤‰æ›´ã—ãŸã„é …ç›®ã‚’ä¿®æ­£ã—ã¦ãã ã•ã„ã€‚'}
          </DialogDescription>
        </DialogHeader>

        <div className='grid gap-4 py-4'>{formFields.map(renderField)}</div>

        <DialogFooter>
          <Button variant='outline' onClick={onClose} disabled={loading}>
            <X className='h-4 w-4 mr-2' />
            ã‚­ãƒ£ãƒ³ã‚»ãƒ«
          </Button>
          <Button onClick={handleSubmit} disabled={loading}>
            <Save className='h-4 w-4 mr-2' />
            {loading ? 'ä¿å­˜ä¸­...' : 'ä¿å­˜'}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
};

