'use client';

import React, { useState, useMemo } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  Select,
  SelectContent,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Pencil,
  Trash2,
  Search,
  ChevronLeft,
  ChevronRight,
  Eye,
  EyeOff,
  ArrowUpDown,
  ArrowUp,
  ArrowDown,
} from 'lucide-react';
import { DataTableProps } from '@/types/admin';

export const DataTable: React.FC<DataTableProps> = ({
  data,
  config,
  loading,
  pagination,
  sortState,
  onEdit,
  onDelete,
  onPageChange,
  onSort,
  onSearch,
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [visibleColumns, setVisibleColumns] = useState<Record<string, boolean>>(
    {}
  );

  // ã‚«ãƒ©ãƒ è¡¨ç¤ºè¨­å®šã®åˆæœŸåŒ–
  React.useEffect(() => {
    if (config?.columns && Object.keys(visibleColumns).length === 0) {
      const initialVisibility = Object.keys(config.columns).reduce(
        (acc, key) => {
          acc[key] = !['created_at', 'updated_at'].includes(key);
          return acc;
        },
        {} as Record<string, boolean>
      );
      setVisibleColumns(initialVisibility);
    }
  }, [config, visibleColumns]);

  // è¡¨ç¤ºå¯¾è±¡ã®ã‚«ãƒ©ãƒ 
  const displayColumns = useMemo(() => {
    if (!config?.columns) return [];
    return Object.entries(config.columns)
      .filter(([key]) => visibleColumns[key])
      .map(([key, columnConfig]) => ({
        key,
        label: columnConfig.label || key,
        type: columnConfig.type,
        readonly: columnConfig.readonly,
      }));
  }, [config, visibleColumns]);

  // å€¤ã®è¡¨ç¤ºå½¢å¼ã‚’æ•´ãˆã‚‹
  const formatCellValue = (value: unknown, type: string): React.ReactNode => {
    if (value === null || value === undefined) return '-';

    switch (type) {
      case 'boolean':
        return value ? 'æœ‰åŠ¹' : 'ç„¡åŠ¹';
      case 'timestamp':
        if (typeof value === 'string') {
          try {
            return new Date(value).toLocaleString('ja-JP');
          } catch {
            return value;
          }
        }
        return String(value);
      case 'decimal':
        if (typeof value === 'number') {
          return value.toLocaleString();
        }
        return String(value);
      default:
        return String(value);
    }
  };

  // æ¤œç´¢å‡¦ç†
  const handleSearch = (term: string) => {
    setSearchTerm(term);
    onSearch(term);
  };

  // ã‚½ãƒ¼ãƒˆå‡¦ç†
  const handleSort = (column: string) => {
    onSort(column);
  };

  // ã‚½ãƒ¼ãƒˆã‚¢ã‚¤ã‚³ãƒ³å–å¾—
  const getSortIcon = (column: string) => {
    if (sortState.sortBy !== column) {
      return <ArrowUpDown className='h-4 w-4' />;
    }
    return sortState.sortOrder === 'asc' ? (
      <ArrowUp className='h-4 w-4' />
    ) : (
      <ArrowDown className='h-4 w-4' />
    );
  };

  // ã‚«ãƒ©ãƒ è¡¨ç¤ºåˆ‡ã‚Šæ›¿ãˆ
  const toggleColumnVisibility = (columnKey: string) => {
    setVisibleColumns(prev => ({
      ...prev,
      [columnKey]: !prev[columnKey],
    }));
  };

  if (!config) {
    return (
      <Card>
        <CardContent className='text-center py-8'>
          <p className='text-muted-foreground'>ãƒ†ãƒ¼ãƒ–ãƒ«ã‚’é¸æŠã—ã¦ãã ã•ã„</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className='w-full'>
      <CardHeader>
        <div className='flex flex-col md:flex-row md:items-center md:justify-between gap-4'>
          <CardTitle className='flex items-center gap-2'>
            {config.name} ({pagination.total}ä»¶)
          </CardTitle>

          <div className='flex flex-col md:flex-row gap-2'>
            {/* æ¤œç´¢ */}
            <div className='relative'>
              <Search className='absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground' />
              <Input
                placeholder='æ¤œç´¢...'
                value={searchTerm}
                onChange={e => handleSearch(e.target.value)}
                className='pl-10 w-full md:w-64'
              />
            </div>

            {/* ã‚«ãƒ©ãƒ è¡¨ç¤ºåˆ‡ã‚Šæ›¿ãˆ */}
            <Select>
              <SelectTrigger className='w-full md:w-auto'>
                <SelectValue placeholder='è¡¨ç¤ºã‚«ãƒ©ãƒ ' />
              </SelectTrigger>
              <SelectContent>
                {Object.entries(config.columns).map(([key, columnConfig]) => (
                  <div
                    key={key}
                    className='flex items-center space-x-2 px-2 py-1 cursor-pointer hover:bg-accent'
                    onClick={() => toggleColumnVisibility(key)}
                  >
                    {visibleColumns[key] ? (
                      <Eye className='h-4 w-4' />
                    ) : (
                      <EyeOff className='h-4 w-4' />
                    )}
                    <span className='text-sm'>{columnConfig.label || key}</span>
                  </div>
                ))}
              </SelectContent>
            </Select>
          </div>
        </div>
      </CardHeader>

      <CardContent>
        {loading ? (
          <div className='flex items-center justify-center py-8'>
            <div className='animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600'></div>
          </div>
        ) : (
          <>
            {/* ãƒ†ãƒ¼ãƒ–ãƒ« */}
            <div className='rounded-md border overflow-x-auto'>
              <Table>
                <TableHeader>
                  <TableRow>
                    {displayColumns.map(column => (
                      <TableHead
                        key={column.key}
                        className='cursor-pointer hover:bg-muted/50'
                        onClick={() => handleSort(column.key)}
                      >
                        <div className='flex items-center gap-2'>
                          {column.label}
                          {getSortIcon(column.key)}
                        </div>
                      </TableHead>
                    ))}
                    <TableHead className='text-right'>æ“ä½œ</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {data.length === 0 ? (
                    <TableRow>
                      <TableCell
                        colSpan={displayColumns.length + 1}
                        className='text-center py-8 text-muted-foreground'
                      >
                        ãƒ‡ãƒ¼ã‚¿ãŒã‚ã‚Šã¾ã›ã‚“
                      </TableCell>
                    </TableRow>
                  ) : (
                    data.map(item => (
                      <TableRow key={item.id}>
                        {displayColumns.map(column => (
                          <TableCell key={column.key}>
                            {formatCellValue(item[column.key], column.type)}
                          </TableCell>
                        ))}
                        <TableCell className='text-right'>
                          <div className='flex items-center justify-end gap-2'>
                            <Button
                              variant='outline'
                              size='sm'
                              onClick={() => onEdit(item)}
                            >
                              <Pencil className='h-4 w-4' />
                            </Button>
                            <Button
                              variant='outline'
                              size='sm'
                              onClick={() =>
                                onDelete(item.id, String(item.name || item.id))
                              }
                            >
                              <Trash2 className='h-4 w-4' />
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </div>

            {/* ãƒšãƒ¼ã‚¸ãƒãƒ¼ã‚·ãƒ§ãƒ³ */}
            {pagination.total_pages > 1 && (
              <div className='flex items-center justify-between mt-4'>
                <p className='text-sm text-muted-foreground'>
                  {pagination.total}ä»¶ä¸­{' '}
                  {(pagination.page - 1) * pagination.limit + 1}-
                  {Math.min(
                    pagination.page * pagination.limit,
                    pagination.total
                  )}
                  ä»¶ã‚’è¡¨ç¤º
                </p>

                <div className='flex items-center gap-2'>
                  <Button
                    variant='outline'
                    size='sm'
                    onClick={() => onPageChange(pagination.page - 1)}
                    disabled={pagination.page <= 1}
                  >
                    <ChevronLeft className='h-4 w-4' />
                  </Button>

                  <span className='text-sm'>
                    {pagination.page} / {pagination.total_pages}
                  </span>

                  <Button
                    variant='outline'
                    size='sm'
                    onClick={() => onPageChange(pagination.page + 1)}
                    disabled={pagination.page >= pagination.total_pages}
                  >
                    <ChevronRight className='h-4 w-4' />
                  </Button>
                </div>
              </div>
            )}
          </>
        )}
      </CardContent>
    </Card>
  );
};

'use client';

import React, { useState, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Plus } from 'lucide-react';
import { useTableManager } from '@/hooks/useTableManager';
import { TableSelector } from './table-selector';
import { DataTable } from './data-table';
import { DataFormDialog } from './data-form-dialog';
import { DeleteConfirmationDialog } from './delete-confirmation-dialog';
import { TableData, FormMode } from '@/types/admin';

interface ImprovedTableEditorProps {
  onTableChange?: (tableName: string) => void;
}

export const ImprovedTableEditor: React.FC<ImprovedTableEditorProps> = ({
  onTableChange,
}) => {
  const {
    tableData,
    tableList,
    tableConfig,
    currentTable,
    loading,
    error,
    pagination,
    sortState,
    filterState,
    setCurrentTable,
    fetchTableData,
    createTableData,
    updateTableData,
    deleteTableData,
    setSearch,
    setSortState,
    setPage,
  } = useTableManager();

  // ãƒ•ã‚©ãƒ¼ãƒ çŠ¶æ…‹
  const [showForm, setShowForm] = useState(false);
  const [formMode, setFormMode] = useState<FormMode>('create');
  const [formData, setFormData] = useState<Record<string, unknown>>({});
  const [editingItem, setEditingItem] = useState<TableData | null>(null);

  // å‰Šé™¤ç¢ºèªãƒ€ã‚¤ã‚¢ãƒ­ã‚°çŠ¶æ…‹
  const [deleteDialog, setDeleteDialog] = useState<{
    open: boolean;
    id: string;
    name: string;
  }>({
    open: false,
    id: '',
    name: '',
  });

  // ãƒ†ãƒ¼ãƒ–ãƒ«é¸æŠå‡¦ç†
  const handleTableSelect = useCallback(
    async (tableName: string) => {
      setCurrentTable(tableName);
      await fetchTableData(tableName);
      onTableChange?.(tableName);
    },
    [setCurrentTable, fetchTableData, onTableChange]
  );

  // æ–°è¦ä½œæˆå‡¦ç†
  const handleCreate = useCallback(() => {
    setFormMode('create');
    setFormData({});
    setEditingItem(null);
    setShowForm(true);
  }, []);

  // ç·¨é›†å‡¦ç†
  const handleEdit = useCallback((item: TableData) => {
    setFormMode('edit');
    setFormData({ ...item });
    setEditingItem(item);
    setShowForm(true);
  }, []);

  // å‰Šé™¤å‡¦ç†ï¼ˆç¢ºèªãƒ€ã‚¤ã‚¢ãƒ­ã‚°è¡¨ç¤ºï¼‰
  const handleDelete = useCallback((id: string, name: string) => {
    setDeleteDialog({
      open: true,
      id,
      name,
    });
  }, []);

  // å‰Šé™¤ç¢ºå®šå‡¦ç†
  const handleDeleteConfirm = useCallback(async () => {
    try {
      const success = await deleteTableData(deleteDialog.id);
      if (success) {
        setDeleteDialog({ open: false, id: '', name: '' });
      }
    } catch (error) {
      console.error('å‰Šé™¤ã‚¨ãƒ©ãƒ¼:', error);
    }
  }, [deleteTableData, deleteDialog.id]);

  // ãƒ•ã‚©ãƒ¼ãƒ é€ä¿¡å‡¦ç†
  const handleFormSubmit = useCallback(
    async (data: Record<string, unknown>) => {
      try {
        let success = false;

        if (formMode === 'create') {
          success = await createTableData(data);
        } else if (editingItem) {
          success = await updateTableData(editingItem.id, data);
        }

        if (success) {
          setShowForm(false);
          setFormData({});
          setEditingItem(null);
        }
      } catch (error) {
        console.error('ãƒ•ã‚©ãƒ¼ãƒ é€ä¿¡ã‚¨ãƒ©ãƒ¼:', error);
      }
    },
    [formMode, editingItem, createTableData, updateTableData]
  );

  // ãƒ•ã‚©ãƒ¼ãƒ ãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰å¤‰æ›´å‡¦ç†
  const handleFieldChange = useCallback((name: string, value: unknown) => {
    setFormData(prev => ({ ...prev, [name]: value }));
  }, []);

  // ãƒ•ã‚©ãƒ¼ãƒ ã‚¯ãƒ­ãƒ¼ã‚ºå‡¦ç†
  const handleFormClose = useCallback(() => {
    setShowForm(false);
    setFormData({});
    setEditingItem(null);
  }, []);

  // æ¤œç´¢å‡¦ç†
  const handleSearch = useCallback(
    (term: string) => {
      setSearch(term);
    },
    [setSearch]
  );

  // ã‚½ãƒ¼ãƒˆå‡¦ç†
  const handleSort = useCallback(
    (column: string) => {
      const newOrder =
        sortState.sortBy === column && sortState.sortOrder === 'asc'
          ? 'desc'
          : 'asc';
      setSortState(column, newOrder);
    },
    [sortState, setSortState]
  );

  // ãƒšãƒ¼ã‚¸å¤‰æ›´å‡¦ç†
  const handlePageChange = useCallback(
    (page: number) => {
      setPage(page);
    },
    [setPage]
  );

  return (
    <div className='space-y-6'>
      {/* ã‚¨ãƒ©ãƒ¼è¡¨ç¤º */}
      {error && (
        <div className='bg-red-50 border border-red-200 rounded-md p-4'>
          <p className='text-red-800 text-sm'>{error}</p>
        </div>
      )}

      {/* ãƒ†ãƒ¼ãƒ–ãƒ«é¸æŠ */}
      <TableSelector
        tableList={tableList}
        selectedTable={currentTable}
        onTableSelect={handleTableSelect}
        loading={loading}
      />

      {/* ãƒ‡ãƒ¼ã‚¿ãƒ†ãƒ¼ãƒ–ãƒ« */}
      {currentTable && (
        <>
          <div className='flex justify-between items-center'>
            <h2 className='text-lg font-semibold'>
              {tableConfig?.name || currentTable}ã®ç®¡ç†
            </h2>
            <Button onClick={handleCreate} disabled={loading}>
              <Plus className='h-4 w-4 mr-2' />
              æ–°è¦ä½œæˆ
            </Button>
          </div>

          <DataTable
            data={tableData}
            config={tableConfig}
            loading={loading}
            pagination={pagination}
            sortState={sortState}
            onEdit={handleEdit}
            onDelete={handleDelete}
            onPageChange={handlePageChange}
            onSort={handleSort}
            onSearch={handleSearch}
          />
        </>
      )}

      {/* ãƒ•ã‚©ãƒ¼ãƒ ãƒ€ã‚¤ã‚¢ãƒ­ã‚° */}
      <DataFormDialog
        open={showForm}
        mode={formMode}
        formData={formData}
        config={tableConfig}
        loading={loading}
        onSubmit={handleFormSubmit}
        onClose={handleFormClose}
        onFieldChange={handleFieldChange}
