'use client';

import React, { useEffect, useState } from 'react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
  DialogDescription,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import type { Patient } from '@/hooks/usePatientsList';

interface PatientFormData {
  name: string;
  phone: string;
  email: string;
  notes: string;
  customAttributes?: Record<string, unknown>;
}

interface PatientFormState {
  name: string;
  phone: string;
  email: string;
  notes: string;
  customAttributesInput: string;
}

interface FormErrors {
  name?: string;
  phone?: string;
  email?: string;
  customAttributesInput?: string;
}

interface PatientModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSave: (data: PatientFormData) => Promise<void>;
  patient?: Patient | null;
  mode: 'create' | 'edit';
}

export function PatientModal({
  isOpen,
  onClose,
  onSave,
  patient,
  mode,
}: PatientModalProps) {
  const [formData, setFormData] = useState<PatientFormState>({
    name: '',
    phone: '',
    email: '',
    notes: '',
    customAttributesInput: '',
  });
  const [errors, setErrors] = useState<FormErrors>({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  // ç·¨é›†ãƒ¢ãƒ¼ãƒ‰ã®å ´åˆã€æ‚£è€…ãƒ‡ãƒ¼ã‚¿ã‚’ãƒ•ã‚©ãƒ¼ãƒ ã«åæ˜ 
  useEffect(() => {
    if (mode === 'edit' && patient) {
      setFormData({
        name: patient.name,
        phone: patient.phone,
        email: patient.email ?? '',
        notes: patient.notes ?? '',
        customAttributesInput: patient.customAttributes
          ? JSON.stringify(patient.customAttributes, null, 2)
          : '',
      });
    } else if (mode === 'create') {
      setFormData({
        name: '',
        phone: '',
        email: '',
        notes: '',
        customAttributesInput: '',
      });
    }
    setErrors({});
  }, [mode, patient, isOpen]);

  const validate = (): boolean => {
    const newErrors: FormErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'æ°åã¯å¿…é ˆã§ã™';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'é›»è©±ç•ªå·ã¯å¿…é ˆã§ã™';
    }

    if (formData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹ã®å½¢å¼ãŒæ­£ã—ãã‚ã‚Šã¾ã›ã‚“';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const parseCustomAttributesInput = (): {
    value?: Record<string, unknown>;
    error?: string;
  } => {
    const raw = formData.customAttributesInput.trim();
    if (!raw) {
      return {};
    }

    try {
      const parsed = JSON.parse(raw);
      if (!parsed || typeof parsed !== 'object' || Array.isArray(parsed)) {
        return {
          error:
            'JSON\u306f\u30aa\u30d6\u30b8\u30a7\u30af\u30c8\u306e\u307f\u5bfe\u5fdc\u3057\u3066\u3044\u307e\u3059',
        };
      }
      return { value: parsed as Record<string, unknown> };
    } catch {
      return {
        error:
          'JSON\u5f62\u5f0f\u304c\u6b63\u3057\u304f\u3042\u308a\u307e\u305b\u3093',
      };
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validate()) return;

    const customAttributesResult = parseCustomAttributesInput();
    if (customAttributesResult.error) {
      setErrors(prev => ({
        ...prev,
        customAttributesInput: customAttributesResult.error,
      }));
      return;
    }

    setIsSubmitting(true);
    try {
      await onSave({
        name: formData.name,
        phone: formData.phone,
        email: formData.email,
        notes: formData.notes,
        customAttributes: customAttributesResult.value,
      });
      onClose();
    } catch (error) {
      console.error('ä¿å­˜ã«å¤±æ•—ã—ã¾ã—ãŸ', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    // å…¥åŠ›æ™‚ã«ã‚¨ãƒ©ãƒ¼ã‚’ã‚¯ãƒªã‚¢
    if (errors[name as keyof FormErrors]) {
      setErrors(prev => ({ ...prev, [name]: undefined }));
    }
  };

  const title = mode === 'create' ? 'æ‚£è€…æ–°è¦ç™»éŒ²' : 'æ‚£è€…æƒ…å ±ç·¨é›†';
  const submitLabel = mode === 'create' ? 'ç™»éŒ²' : 'ä¿å­˜';

  return (
    <Dialog open={isOpen} onOpenChange={open => !open && onClose()}>
      <DialogContent className='sm:max-w-[425px]'>
        <DialogHeader>
          <DialogTitle>{title}</DialogTitle>
          <DialogDescription>
            {mode === 'create'
              ? 'æ–°ã—ã„æ‚£è€…ã‚’ç™»éŒ²ã—ã¾ã™ã€‚æ°åã¨é›»è©±ç•ªå·ã¯å¿…é ˆã§ã™ã€‚'
              : 'æ‚£è€…æƒ…å ±ã‚’ç·¨é›†ã—ã¾ã™ã€‚'}
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit}>
          <div className='grid gap-4 py-4'>
            {/* æ°å */}
            <div className='grid gap-2'>
              <Label htmlFor='name'>
                æ°å <span className='text-red-500'>*</span>
              </Label>
              <Input
                id='name'
                name='name'
                value={formData.name}
                onChange={handleChange}
                placeholder='å±±ç”° å¤ªéƒ'
                className={errors.name ? 'border-red-500' : ''}
              />
              {errors.name && (
                <p className='text-sm text-red-500'>{errors.name}</p>
              )}
            </div>

            {/* é›»è©±ç•ªå· */}
            <div className='grid gap-2'>
              <Label htmlFor='phone'>
                é›»è©±ç•ªå· <span className='text-red-500'>*</span>
              </Label>
              <Input
                id='phone'
                name='phone'
                value={formData.phone}
                onChange={handleChange}
                placeholder='090-1234-5678'
                className={errors.phone ? 'border-red-500' : ''}
              />
              {errors.phone && (
                <p className='text-sm text-red-500'>{errors.phone}</p>
              )}
            </div>

            {/* ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹ */}
            <div className='grid gap-2'>
              <Label htmlFor='email'>ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹</Label>
              <Input
                id='email'
                name='email'
                type='email'
                value={formData.email}
                onChange={handleChange}
                placeholder='example@email.com'
                className={errors.email ? 'border-red-500' : ''}
              />
              {errors.email && (
                <p className='text-sm text-red-500'>{errors.email}</p>
              )}
            </div>

            {/* ãƒ¡ãƒ¢ */}
            <div className='grid gap-2'>
              <Label htmlFor='notes'>ãƒ¡ãƒ¢</Label>
              <Textarea
                id='notes'
                name='notes'
                value={formData.notes}
                onChange={handleChange}
                placeholder='ç‰¹è¨˜äº‹é …ãŒã‚ã‚Œã°å…¥åŠ›ã—ã¦ãã ã•ã„'
                rows={3}
              />
            </div>
            {/* Custom Attributes (JSON) */}
            <div className='grid gap-2'>
              <Label htmlFor='customAttributesInput'>
                {'\u30ab\u30b9\u30bf\u30e0\u5c5e\u6027 (JSON)'}
              </Label>
              <Textarea
                id='customAttributesInput'
                name='customAttributesInput'
                value={formData.customAttributesInput}
                onChange={handleChange}
                placeholder='{"symptom":"\u8170\u75db","visitReason":"\u518d\u8a3a"}'
                className={errors.customAttributesInput ? 'border-red-500' : ''}
                rows={4}
              />
              {errors.customAttributesInput && (
                <p className='text-sm text-red-500'>
                  {errors.customAttributesInput}
                </p>
              )}
            </div>
          </div>

          <DialogFooter>
            <Button
              type='button'
              variant='outline'
              onClick={onClose}
              disabled={isSubmitting}
            >
              ã‚­ãƒ£ãƒ³ã‚»ãƒ«
            </Button>
            <Button type='submit' disabled={isSubmitting}>
              {isSubmitting ? 'å‡¦ç†ä¸­...' : submitLabel}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

