const cva = (base: string, options?: any) => {
  return ({ ...args }: any) => {
    const variant =
      args.variant || options?.defaultVariants?.variant || 'default';
    const variantClass = options?.variants?.variant?.[variant] || '';
    return `${base} ${variantClass}`;
  };
};

