'use client';

import { useCallback, useEffect, useState } from 'react';

export interface AdminChatMessage {
  id: string;
  content: string;
  role: 'user' | 'assistant';
  createdAt: string;
}

interface ChatState {
  messages: AdminChatMessage[];
  isLoading: boolean;
  error: string | null;
}

interface ChatApiResponse<T> {
  success: boolean;
  data?: T;
  error?: string;
}

export const useAdminChat = () => {
  const [state, setState] = useState<ChatState>({
    messages: [],
    isLoading: false,
    error: null,
  });

  const fetchMessages = useCallback(async () => {
    setState(prev => ({ ...prev, isLoading: true, error: null }));
    try {
      const response = await fetch('/api/chat', {
        method: 'GET',
        credentials: 'include',
      });

      const payload = (await response.json()) as ChatApiResponse<
        Array<{
          id: string;
          chat_messages?: Array<{
            id: string;
            sender: 'user' | 'ai';
            message_text: string;
            created_at: string;
          }>;
        }>
      >;

      if (!response.ok || !payload.success || !payload.data) {
        throw new Error(payload.error || 'ãƒãƒ£ãƒƒãƒˆå±¥æ­´ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ');
      }

      const messages = payload.data
        .flatMap(session => session.chat_messages ?? [])
        .sort(
          (a, b) =>
            new Date(a.created_at).getTime() - new Date(b.created_at).getTime()
        )
        .map<AdminChatMessage>(message => ({
          id: message.id,
          content: message.message_text,
          role: message.sender === 'ai' ? 'assistant' : 'user',
          createdAt: message.created_at,
        }));

      setState({ messages, isLoading: false, error: null });
    } catch (error) {
      console.error(error);
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: 'ãƒãƒ£ãƒƒãƒˆå±¥æ­´ã®èª­ã¿è¾¼ã¿ã«å¤±æ•—ã—ã¾ã—ãŸ',
      }));
    }
  }, []);

  const sendMessage = useCallback(async (content: string) => {
    if (!content.trim()) return;

    setState(prev => ({ ...prev, isLoading: true, error: null }));
    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: content }),
      });

      const payload = (await response.json()) as ChatApiResponse<{
        session_id: string;
        user_message: {
          id: string;
          message_text: string;
          created_at: string;
        };
        ai_message: {
          id: string;
          message_text: string;
          created_at: string;
        };
      }>;

      if (!response.ok || !payload.success || !payload.data) {
        throw new Error(payload.error || 'ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸é€ä¿¡ã«å¤±æ•—ã—ã¾ã—ãŸ');
      }

      setState(prev => ({
        ...prev,
        isLoading: false,
        messages: [
          ...prev.messages,
          {
            id: payload.data.user_message.id,
            content: payload.data.user_message.message_text,
            role: 'user',
            createdAt: payload.data.user_message.created_at,
          },
          {
            id: payload.data.ai_message.id,
            content: payload.data.ai_message.message_text,
            role: 'assistant',
            createdAt: payload.data.ai_message.created_at,
          },
        ],
      }));
    } catch (error) {
      console.error(error);
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: 'ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã®é€ä¿¡ã«å¤±æ•—ã—ã¾ã—ãŸ',
      }));
    }
  }, []);

  const exportChat = useCallback(() => {
    const blob = new Blob([JSON.stringify(state.messages, null, 2)], {
      type: 'application/json',
    });
    const url = URL.createObjectURL(blob);
    const anchor = document.createElement('a');
    anchor.href = url;
    anchor.download = `admin-chat_${new Date().toISOString()}.json`;
    anchor.click();
    URL.revokeObjectURL(url);
  }, [state.messages]);

  useEffect(() => {
    fetchMessages();
  }, [fetchMessages]);

  return {
    messages: state.messages,
    isLoading: state.isLoading,
    error: state.error,
    sendMessage,
    exportChat,
  };
};

'use client';

import { useMemo, useState } from 'react';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import {
  fetchAdminDashboard,
  type AggregatedClinicData,
  type AdminDashboardPayload,
} from '@/lib/api/admin/dashboard-client';

interface SortState {
  sortBy: keyof AggregatedClinicData | 'name';
  order: 'asc' | 'desc';
}

interface UseAdminDashboardReturn {
  clinicsData: AggregatedClinicData[];
  overallKpis: AdminDashboardPayload['overallKpis'] | null;
  loading: boolean;
  error: string | null;
  setSort: (sortBy: SortState['sortBy'], order: SortState['order']) => void;
  setClinicFilter: (clinicId: string | null) => void;
  refreshData: () => Promise<void>;
  isRefreshing: boolean;
}

export default function useAdminDashboard(): UseAdminDashboardReturn {
  const queryClient = useQueryClient();
  const [sort, setSortState] = useState<SortState>({
    sortBy: 'totalRevenue',
    order: 'desc',
  });
  const [clinicFilter, setClinicFilter] = useState<string | null>(null);

  const query = useQuery({
    queryKey: ['admin-dashboard', clinicFilter ?? 'all'],
    queryFn: () =>
      fetchAdminDashboard(
        clinicFilter ? { clinic_id: clinicFilter } : undefined
      ),
  });

  const clinicsData = query.data?.clinicsData ?? [];

  const sortedData = useMemo(() => {
    const dataCopy = [...clinicsData];
    dataCopy.sort((a, b) => {
      const valueA = a[sort.sortBy as keyof AggregatedClinicData];
      const valueB = b[sort.sortBy as keyof AggregatedClinicData];

      if (typeof valueA === 'number' && typeof valueB === 'number') {
        return sort.order === 'asc' ? valueA - valueB : valueB - valueA;
      }

      const stringA = String(valueA);
      const stringB = String(valueB);
      return sort.order === 'asc'
        ? stringA.localeCompare(stringB)
        : stringB.localeCompare(stringA);
    });
    return dataCopy;
  }, [clinicsData, sort]);

  const refreshMutation = useMutation({
    mutationFn: async () => {
      await queryClient.invalidateQueries({
        queryKey: ['admin-dashboard'],
      });
      await query.refetch();
    },
  });

  return {
    clinicsData: sortedData,
    overallKpis: query.data?.overallKpis ?? null,
    loading: query.isLoading,
    error: query.error instanceof Error ? query.error.message : null,
    setSort: (sortBy, order) => setSortState({ sortBy, order }),
    setClinicFilter,
    refreshData: () => refreshMutation.mutateAsync(),
    isRefreshing: query.isFetching || refreshMutation.isPending,
  };
}

