  };

  const importMasterData = async (items: MasterData[]) => {
    if (!systemSettings.importMasterData) {
      throw new Error('ã‚¤ãƒ³ãƒãƒ¼ãƒˆæ©Ÿèƒ½ãŒåˆ©ç”¨ã§ãã¾ã›ã‚“');
