        systemSettings.error || 'ãƒ‡ãƒ¼ã‚¿ã®ã‚¤ãƒ³ãƒãƒ¼ãƒˆã«å¤±æ•—ã—ã¾ã—ãŸ'
      );
    }
    return true;
  };

  const rollbackMasterData = async () => {
    if (!systemSettings.rollbackMasterData) {
      throw new Error('ãƒ­ãƒ¼ãƒ«ãƒãƒƒã‚¯æ©Ÿèƒ½ãŒåˆ©ç”¨ã§ãã¾ã›ã‚“');
    }
    const success = await systemSettings.rollbackMasterData();
    if (!success) {
      throw new Error(systemSettings.error || 'ãƒ­ãƒ¼ãƒ«ãƒãƒƒã‚¯ã«å¤±æ•—ã—ã¾ã—ãŸ');
    }
    return true;
  };

  const createTableData = async (
    tableName: string,
    data: Partial<TableData>
  ) => {
    if (tableName !== tableManager.currentTable) {
      tableManager.setCurrentTable(tableName);
    }
    const success = await tableManager.createTableData(
      data as Record<string, unknown>
    );
    if (!success) {
      throw new Error(tableManager.error || 'ãƒ‡ãƒ¼ã‚¿ã®ä½œæˆã«å¤±æ•—ã—ã¾ã—ãŸ');
    }
    return data;
  };

  const updateTableData = async (
