      throw new Error(tableManager.error || 'ãƒ‡ãƒ¼ã‚¿ã®æ›´æ–°ã«å¤±æ•—ã—ã¾ã—ãŸ');
    }
    return { id, ...updates };
  };

  const deleteTableData = async (tableName: string, id: string) => {
    if (tableName !== tableManager.currentTable) {
