import { useState, useEffect, useCallback } from 'react';
import { api, isSuccessResponse, handleApiError } from '@/lib/api-client';

/**
 * ãƒãƒ£ãƒƒãƒˆãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã®å‹å®šç¾©
 */
export interface Message {
  id: string;
  content: string;
  role: 'user' | 'assistant';
  timestamp: number;
  session_id?: string;
  response_data?: Record<string, unknown>;
}

/**
 * ãƒãƒ£ãƒƒãƒˆã‚»ãƒƒã‚·ãƒ§ãƒ³ã®å‹å®šç¾©
 */
interface ChatSession {
  id: string;
  user_id: string;
  clinic_id?: string;
  created_at: string;
  chat_messages: {
    id: string;
    sender: 'user' | 'ai';
    message_text: string;
    response_data?: Record<string, unknown>;
    created_at: string;
  }[];
}

/**
 * ãƒãƒ£ãƒƒãƒˆçŠ¶æ…‹ã®å‹å®šç¾©
 */
interface ChatState {
  messages: Message[];
  isLoading: boolean;
  error: string | null;
  isEnabled: boolean;
  currentSessionId: string | null;
  sessions: ChatSession[];
}

/**
 * APIå¿œç­”ã‹ã‚‰å†…éƒ¨ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸å½¢å¼ã«å¤‰æ›
 */
function convertToMessages(sessions: ChatSession[]): Message[] {
  const messages: Message[] = [];

  for (const session of sessions) {
    for (const msg of session.chat_messages || []) {
      messages.push({
        id: msg.id,
        content: msg.message_text,
        role: msg.sender === 'user' ? 'user' : 'assistant',
        timestamp: new Date(msg.created_at).getTime(),
        session_id: session.id,
        response_data: msg.response_data,
      });
    }
  }

  // æ™‚é–“é †ã§ã‚½ãƒ¼ãƒˆ
  return messages.sort((a, b) => a.timestamp - b.timestamp);
}

/**
 * useChat Hook - APIé€£æºç‰ˆ
 *
 * MVPãƒãƒ£ãƒƒãƒˆæ©Ÿèƒ½ã®ãƒ•ãƒƒã‚¯
 * - ãƒ­ãƒ¼ã‚«ãƒ«ä¿å­˜ã¯å»ƒæ­¢
 * - APIçµŒç”±ã§é€ä¿¡/å±¥æ­´å–å¾—
 */
export const useChat = (clinicId: string | null) => {
  const [state, setState] = useState<ChatState>({
    messages: [],
    isLoading: true,
    error: null,
    isEnabled: true,
    currentSessionId: null,
    sessions: [],
  });

  /**
   * ãƒãƒ£ãƒƒãƒˆå±¥æ­´ã‚’å–å¾—
   */
  const fetchHistory = useCallback(async () => {
    // clinicIdãŒnullã®å ´åˆã¯APIã‚’å‘¼ã³å‡ºã•ãªã„
    if (!clinicId) {
      setState(prev => ({ ...prev, isLoading: false }));
      return;
    }

    setState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      const response = await api.chat.getHistory(clinicId);

      if (isSuccessResponse(response)) {
        const sessions = response.data as ChatSession[];
        const messages = convertToMessages(sessions);
        const currentSessionId = sessions.length > 0 ? sessions[0].id : null;

        setState(prev => ({
          ...prev,
          sessions,
          messages,
          currentSessionId,
          isLoading: false,
          error: null,
        }));
      } else {
        const errorMessage = response.error
          ? handleApiError(response.error, 'å±¥æ­´ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ')
          : 'å±¥æ­´ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ';

        setState(prev => ({
          ...prev,
          isLoading: false,
          error: errorMessage,
        }));
      }
    } catch (error) {
      console.error('Failed to fetch chat history:', error);
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: 'å±¥æ­´ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ',
      }));
    }
  }, [clinicId]);

  /**
   * åˆå›ãƒ­ãƒ¼ãƒ‰æ™‚ã«å±¥æ­´ã‚’å–å¾—
   */
  useEffect(() => {
    fetchHistory();
  }, [fetchHistory]);

  /**
   * ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã‚’é€ä¿¡
   */
  const sendMessage = useCallback(
    async (content: string) => {
      if (!content.trim()) {
        return;
      }

      if (!state.isEnabled) {
        return;
      }

      // æ¥½è¦³çš„æ›´æ–°: ãƒ¦ãƒ¼ã‚¶ãƒ¼ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã‚’å³åº§ã«è¿½åŠ 
      const tempUserMessageId = `temp-user-${Date.now()}`;
      const userMessage: Message = {
        id: tempUserMessageId,
        content,
        role: 'user',
        timestamp: Date.now(),
        session_id: state.currentSessionId || undefined,
      };

      setState(prev => ({
        ...prev,
        messages: [...prev.messages, userMessage],
        isLoading: true,
        error: null,
      }));

      try {
        const response = await api.chat.sendMessage({
          message: content,
          clinic_id: clinicId,
          session_id: state.currentSessionId,
        });

        if (isSuccessResponse(response)) {
          const data = response.data as {
            session_id: string;
            user_message: {
              id: string;
              sender: string;
              message_text: string;
            };
            ai_message: {
              id: string;
              sender: string;
              message_text: string;
              response_data?: Record<string, unknown>;
            };
          };

          // ãƒ¦ãƒ¼ã‚¶ãƒ¼ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã‚’æ­£å¼ãªIDã§æ›´æ–°ã—ã€AIå¿œç­”ã‚’è¿½åŠ 
          const updatedUserMessage: Message = {
            id: data.user_message.id,
            content: data.user_message.message_text,
            role: 'user',
            timestamp: Date.now(),
            session_id: data.session_id,
          };

          const aiMessage: Message = {
            id: data.ai_message.id,
            content: data.ai_message.message_text,
            role: 'assistant',
            timestamp: Date.now() + 1,
            session_id: data.session_id,
            response_data: data.ai_message.response_data,
          };

          setState(prev => ({
            ...prev,
            messages: [
              ...prev.messages.filter(m => m.id !== tempUserMessageId),
              updatedUserMessage,
              aiMessage,
            ],
            currentSessionId: data.session_id,
            isLoading: false,
            error: null,
          }));
        } else {
          const errorMessage = response.error
            ? handleApiError(response.error, 'ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã®é€ä¿¡ã«å¤±æ•—ã—ã¾ã—ãŸ')
            : 'ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã®é€ä¿¡ã«å¤±æ•—ã—ã¾ã—ãŸ';

          setState(prev => ({
            ...prev,
            isLoading: false,
            error: errorMessage,
          }));
        }
      } catch (error) {
        console.error('Failed to send message:', error);
        setState(prev => ({
          ...prev,
          isLoading: false,
          error: 'ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã®é€ä¿¡ã«å¤±æ•—ã—ã¾ã—ãŸ',
        }));
      }
    },
    [clinicId, state.currentSessionId, state.isEnabled]
  );

  /**
   * ãƒãƒ£ãƒƒãƒˆã®æœ‰åŠ¹/ç„¡åŠ¹ã‚’åˆ‡ã‚Šæ›¿ãˆ
   */
  const toggleChat = useCallback(() => {
    setState(prev => ({
      ...prev,
      isEnabled: !prev.isEnabled,
    }));
  }, []);

  /**
   * æ–°ã—ã„ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’é–‹å§‹
   */
  const startNewSession = useCallback(() => {
    setState(prev => ({
      ...prev,
      currentSessionId: null,
      messages: [],
      error: null,
    }));
  }, []);

  /**
   * ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã‚’ã‚¯ãƒªã‚¢ï¼ˆç¾åœ¨ã®ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®ã¿ï¼‰
   */
  const clearMessages = useCallback(() => {
    setState(prev => ({
      ...prev,
      messages: [],
      error: null,
    }));
  }, []);

  /**
   * å±¥æ­´ã‚’å†å–å¾—
   */
  const refetch = useCallback(async () => {
    await fetchHistory();
  }, [fetchHistory]);

  /**
   * ç‰¹å®šã®ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’é¸æŠ
   */
  const selectSession = useCallback((sessionId: string) => {
    setState(prev => {
      const session = prev.sessions.find(s => s.id === sessionId);
      if (!session) return prev;

      const messages = convertToMessages([session]);
      return {
        ...prev,
        currentSessionId: sessionId,
        messages,
      };
    });
  }, []);

  /**
   * éŸ³å£°å…¥åŠ›ã‚’é–‹å§‹ï¼ˆãƒ–ãƒ©ã‚¦ã‚¶äº’æ›æ€§ãƒã‚§ãƒƒã‚¯ï¼‰
   */
  const startVoiceInput = useCallback(() => {
    if (typeof window === 'undefined') {
      setState(prev => ({
        ...prev,
        error: 'éŸ³å£°å…¥åŠ›ã¯ã“ã®ç’°å¢ƒã§ã‚µãƒãƒ¼ãƒˆã•ã‚Œã¦ã„ã¾ã›ã‚“ã€‚',
      }));
      return;
    }

    const SpeechRecognitionConstructor =
      (window as any).SpeechRecognition ||
      (window as any).webkitSpeechRecognition;

    if (!SpeechRecognitionConstructor) {
      setState(prev => ({
        ...prev,
        error: 'éŸ³å£°å…¥åŠ›ã¯ã“ã®ãƒ–ãƒ©ã‚¦ã‚¶ã§ã‚µãƒãƒ¼ãƒˆã•ã‚Œã¦ã„ã¾ã›ã‚“ã€‚',
      }));
      return;
    }

    const recognition = new SpeechRecognitionConstructor();
    recognition.lang = 'ja-JP';
    recognition.continuous = false;
    recognition.interimResults = false;

    recognition.onresult = (event: any) => {
      const transcript = event.results[0][0].transcript;
