      }));
    };

    recognition.start();
  }, [sendMessage]);

  return {
    messages: state.messages,
    isLoading: state.isLoading,
    error: state.error,
    isEnabled: state.isEnabled,
    currentSessionId: state.currentSessionId,
    sessions: state.sessions,
    sendMessage,
    toggleChat,
    startNewSession,
    clearMessages,
    refetch,
    selectSession,
    startVoiceInput,
  };
};

