'use client';

import { useState, useEffect, useCallback } from 'react';
import { DashboardData } from '../types/api';
import {
  api,
  isSuccessResponse,
  isErrorResponse,
  handleApiError,
} from '../lib/api-client';

interface UseDashboardReturn {
  dashboardData: DashboardData | null;
  loading: boolean;
  error: string | null;
  handleQuickAction: (action: string) => void;
  refetch: () => Promise<void>;
}

const useDashboard = (clinicId?: string | null): UseDashboardReturn => {
  const [dashboardData, setDashboardData] = useState<DashboardData | null>(
    null
  );
  const [loading, setLoading] = useState<boolean>(Boolean(clinicId));
  const [error, setError] = useState<string | null>(null);

  const fetchData = useCallback(async (): Promise<void> => {
    if (!clinicId) {
      setDashboardData(null);
      setError(null);
      setLoading(false);
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await api.dashboard.get(clinicId);

      if (isSuccessResponse(response)) {
        setDashboardData(response.data as DashboardData);
        setError(null);
      } else if (isErrorResponse(response)) {
        setDashboardData(null);
        setError(handleApiError(response.error));
      }
    } catch (err) {
      console.error('Failed to fetch dashboard data:', err);
      setDashboardData(null);
      setError('ãƒ€ãƒƒã‚·ãƒ¥ãƒœãƒ¼ãƒ‰ãƒ‡ãƒ¼ã‚¿ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ');
    } finally {
      setLoading(false);
    }
  }, [clinicId]);

  useEffect(() => {
    if (!clinicId) {
      setLoading(false);
      setDashboardData(null);
      return;
    }

    fetchData();

    // 5åˆ†ã”ã¨ã«ãƒ‡ãƒ¼ã‚¿ã‚’æ›´æ–°ï¼ˆãƒªã‚¢ãƒ«ã‚¿ã‚¤ãƒ æ€§ã‚’å‘ä¸Šï¼‰
    const updateTimer = setInterval(fetchData, 300000);

    // ãƒšãƒ¼ã‚¸ã®å¯è¦–æ€§ãŒå¤‰ã‚ã£ãŸæ™‚ã®å‡¦ç†
    const handleVisibilityChange = () => {
      if (!document.hidden) {
        fetchData();
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      clearInterval(updateTimer);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [fetchData]);

  const handleQuickAction = useCallback((action: string): void => {
    // ã‚¯ã‚¤ãƒƒã‚¯ã‚¢ã‚¯ã‚·ãƒ§ãƒ³ã®å‡¦ç†ã‚’å®Ÿè£…
    try {
      switch (action) {
        case 'daily-report':
          window.location.href = '/daily-reports';
          break;
        case 'appointments':
          window.location.href = '/reservations';
          break;
        case 'ai-chat':
          window.location.href = '/chat';
          break;
        default:
          console.warn('Unknown quick action:', action);
      }
    } catch (err) {
      console.error('Failed to handle quick action:', err);
    }
  }, []);

  return {
    dashboardData,
    loading,
    error,
    handleQuickAction,
    refetch: fetchData,
  };
};

export default useDashboard;

'use client';

import { useMemo, useState } from 'react';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import {
  listMasterData,
  createMasterData,
  updateMasterData,
  deleteMasterData,
  type MasterDataItem,
} from '@/lib/api/admin/master-data-client';

export interface UseMasterDataOptions {
  clinicId?: string | null;
  category?: string;
}

export interface MasterDataGroupedResult {
  [category: string]: MasterDataItem[];
}

export interface UseMasterDataReturn {
  data: MasterDataGroupedResult;
  items: MasterDataItem[];
  loading: boolean;
  error: string | null;
  searchQuery: string;
  setSearchQuery: (value: string) => void;
  createItem: (
    payload: Omit<MasterDataItem, 'id' | 'updated_at' | 'updated_by'>
  ) => Promise<void>;
  updateItem: (
    id: string,
    payload: Partial<Omit<MasterDataItem, 'id'>>
  ) => Promise<void>;
  deleteItem: (id: string) => Promise<void>;
  importData: (file: File) => Promise<void>;
  exportData: () => void;
  refetch: () => Promise<void>;
  isMutating: boolean;
}

export function useMasterData(
  options: UseMasterDataOptions = {}
): UseMasterDataReturn {
  const queryClient = useQueryClient();
  const [searchQuery, setSearchQuery] = useState('');

  const query = useQuery({
    queryKey: [
      'admin-master-data',
      options.clinicId ?? 'global',
      options.category ?? 'all',
    ],
    queryFn: () =>
      listMasterData({
        clinic_id: options.clinicId ?? undefined,
        category: options.category,
      }),
  });

  const items = query.data?.items ?? [];

  const filteredItems = useMemo(() => {
    if (!searchQuery) return items;
    const lowerSearch = searchQuery.toLowerCase();
    return items.filter(item => {
      return (
        item.name.toLowerCase().includes(lowerSearch) ||
        item.category.toLowerCase().includes(lowerSearch) ||
        (typeof item.value === 'string'
          ? item.value.toLowerCase().includes(lowerSearch)
          : false)
      );
    });
  }, [items, searchQuery]);

  const groupedData = useMemo(() => {
    return filteredItems.reduce<MasterDataGroupedResult>((acc, item) => {
      const key = item.category || 'uncategorized';
      if (!acc[key]) {
        acc[key] = [];
      }
      acc[key].push(item);
      return acc;
    }, {});
  }, [filteredItems]);

  const invalidate = async () => {
    await queryClient.invalidateQueries({
      queryKey: ['admin-master-data'],
    });
  };

  const createMutation = useMutation({
    mutationFn: createMasterData,
    onSuccess: invalidate,
  });

  const updateMutation = useMutation({
    mutationFn: ({
      id,
      payload,
    }: {
      id: string;
      payload: Partial<Omit<MasterDataItem, 'id'>>;
    }) => updateMasterData(id, payload),
    onSuccess: invalidate,
  });

  const deleteMutation = useMutation({
    mutationFn: deleteMasterData,
    onSuccess: invalidate,
  });

  const createItem = async (
    payload: Omit<MasterDataItem, 'id' | 'updated_at' | 'updated_by'>
  ) => {
    await createMutation.mutateAsync(payload);
  };

  const updateItem = async (
    id: string,
    payload: Partial<Omit<MasterDataItem, 'id'>>
  ) => {
    await updateMutation.mutateAsync({ id, payload });
  };

  const deleteItem = async (id: string) => {
    await deleteMutation.mutateAsync(id);
  };

  const importData = async (file: File) => {
    try {
      const text = await file.text();
      const parsed: MasterDataItem[] = JSON.parse(text);

      await Promise.all(
        parsed.map(item =>
          createItem({
            clinic_id: item.clinic_id,
            name: item.name,
            category: item.category,
            value: item.value,
            data_type: item.data_type,
            description: item.description ?? null,
            is_editable: item.is_editable,
            is_public: item.is_public,
          })
        )
      );
    } catch (error) {
      console.error(error);
      throw new Error(
        'ã‚¤ãƒ³ãƒãƒ¼ãƒˆã«å¤±æ•—ã—ã¾ã—ãŸã€‚ãƒ•ã‚¡ã‚¤ãƒ«å½¢å¼ã‚’ç¢ºèªã—ã¦ãã ã•ã„ã€‚'
      );
    }
  };

  const exportData = () => {
    const jsonString = JSON.stringify(items, null, 2);
    const blob = new Blob([jsonString], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const anchor = document.createElement('a');
    anchor.href = url;
    anchor.download = `master-data_${new Date().toISOString()}.json`;
    anchor.click();
    URL.revokeObjectURL(url);
  };

  const refetch = async () => {
    await queryClient.invalidateQueries({
      queryKey: ['admin-master-data'],
    });
    await query.refetch();
  };

  return {
    data: groupedData,
    items,
    loading: query.isLoading,
    error: query.error instanceof Error ? query.error.message : null,
    searchQuery,
    setSearchQuery,
    createItem,
    updateItem,
    deleteItem,
    importData,
    exportData,
    refetch,
    isMutating:
      createMutation.isPending ||
      updateMutation.isPending ||
      deleteMutation.isPending,
  };
}

'use client';

import { useCallback, useMemo, useState } from 'react';
import { API_ENDPOINTS, ERROR_MESSAGES } from '@/lib/constants';

export interface ClinicKPI {
  revenue: number;
  patients: number;
  staff_performance_score: number | null;
}

export interface ClinicWithKPI {
  id: string;
  name: string;
  address?: string | null;
  phone_number?: string | null;
  is_active: boolean;
  created_at?: string | null;
  kpi?: ClinicKPI;
}

type SortDirection = 'asc' | 'desc';

export function useMultiStore() {
  const [clinics, setClinics] = useState<ClinicWithKPI[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const fetchClinicsWithKPI = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const params = new URLSearchParams();
      params.set('include_kpi', 'true');

      const response = await fetch(
        `${API_ENDPOINTS.ADMIN.TENANTS}?${params.toString()}`,
        {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );
      const result = await response.json();

      if (!result.success) {
        throw new Error(result.error || ERROR_MESSAGES.SERVER_ERROR);
      }

      setClinics(result.data?.items ?? []);
    } catch (err) {
      const message =
        err instanceof Error ? err.message : ERROR_MESSAGES.NETWORK_ERROR;
      setError(message);
      console.error('Failed to fetch clinics with KPI', err);
    } finally {
      setLoading(false);
    }
  }, []);

  const sortByRevenue = useCallback((direction: SortDirection) => {
    setClinics(prev =>
      [...prev].sort((a, b) => {
        const aRevenue = a.kpi?.revenue ?? 0;
        const bRevenue = b.kpi?.revenue ?? 0;
        return direction === 'desc' ? bRevenue - aRevenue : aRevenue - bRevenue;
      })
    );
  }, []);

  const sortByPatients = useCallback((direction: SortDirection) => {
    setClinics(prev =>
      [...prev].sort((a, b) => {
        const aPatients = a.kpi?.patients ?? 0;
        const bPatients = b.kpi?.patients ?? 0;
        return direction === 'desc'
          ? bPatients - aPatients
          : aPatients - bPatients;
      })
    );
  }, []);

  const sortByPerformance = useCallback((direction: SortDirection) => {
    setClinics(prev =>
      [...prev].sort((a, b) => {
        const aScore = a.kpi?.staff_performance_score ?? 0;
        const bScore = b.kpi?.staff_performance_score ?? 0;
        return direction === 'desc' ? bScore - aScore : aScore - bScore;
      })
    );
  }, []);

  const totalRevenue = useMemo(() => {
    return clinics.reduce((sum, clinic) => sum + (clinic.kpi?.revenue ?? 0), 0);
  }, [clinics]);

  const totalPatients = useMemo(() => {
    return clinics.reduce(
      (sum, clinic) => sum + (clinic.kpi?.patients ?? 0),
      0
    );
  }, [clinics]);

  const averagePerformanceScore = useMemo(() => {
    const scores = clinics
      .map(c => c.kpi?.staff_performance_score)
      .filter((s): s is number => s !== null && s !== undefined);
    if (scores.length === 0) return null;
    return scores.reduce((sum, s) => sum + s, 0) / scores.length;
  }, [clinics]);

  return {
    clinics,
    loading,
    error,
    fetchClinicsWithKPI,
    sortByRevenue,
    sortByPatients,
    sortByPerformance,
    totalRevenue,
    totalPatients,
    averagePerformanceScore,
  };
}

export default useMultiStore;

