const DEBOUNCE_MS = 300;
const MAX_PATIENTS = 50;

export function usePatientsList(): UsePatientsListResult {
  const { profile, loading: profileLoading } = useUserProfileContext();
  const clinicId = profile?.clinicId;

  const [patients, setPatients] = useState<Patient[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchQuery, setSearchQueryInternal] = useState('');
  const [debouncedQuery, setDebouncedQuery] = useState('');

  const debounceTimerRef = useRef<NodeJS.Timeout | null>(null);

  // ãƒ‡ãƒã‚¦ãƒ³ã‚¹å‡¦ç†
  const setSearchQuery = useCallback((query: string) => {
    setSearchQueryInternal(query);

    if (debounceTimerRef.current) {
      clearTimeout(debounceTimerRef.current);
    }

    debounceTimerRef.current = setTimeout(() => {
      setDebouncedQuery(query);
    }, DEBOUNCE_MS);
  }, []);

  // ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
  useEffect(() => {
    return () => {
      if (debounceTimerRef.current) {
        clearTimeout(debounceTimerRef.current);
      }
    };
  }, []);

  // æ‚£è€…ä¸€è¦§ã‚’å–å¾—
  const fetchPatients = useCallback(async () => {
    if (!clinicId) return;

    setIsLoading(true);
    setError(null);

    try {
      const params = new URLSearchParams({ clinic_id: clinicId });
      if (debouncedQuery) {
        params.append('q', debouncedQuery);
      }

      const response = await fetch(`/api/customers?${params.toString()}`, {
        credentials: 'include',
      });

      if (!response.ok) {
        const text = await response.text();
        throw new Error(text || 'æ‚£è€…ä¸€è¦§ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ');
      }

      const json = await response.json();
      const incoming = (json.data ?? []).slice(0, MAX_PATIENTS);
      setPatients(incoming);
    } catch (err) {
      console.error('fetchPatients error', err);
      setError(err instanceof Error ? err.message : 'ä¸æ˜ãªã‚¨ãƒ©ãƒ¼');
    } finally {
      setIsLoading(false);
    }
  }, [clinicId, debouncedQuery]);

  useEffect(() => {
    if (profileLoading) {
      setIsLoading(true);
      return;
    }

    if (!clinicId) {
      setIsLoading(false);
      setError(
        '\u30af\u30ea\u30cb\u30c3\u30af\u60c5\u5831\u304c\u898b\u3064\u304b\u308a\u307e\u305b\u3093'
      );
      setPatients([]);
      return;
    }

    fetchPatients();
  }, [profileLoading, clinicId, fetchPatients]);

  // æ–°è¦æ‚£è€…ã‚’ä½œæˆ
  const createPatient = useCallback(
    async (data: PatientInsertDTO): Promise<Patient> => {
      if (!clinicId) {
        throw new Error('ã‚¯ãƒªãƒ‹ãƒƒã‚¯IDãŒã‚ã‚Šã¾ã›ã‚“');
      }

      const response = await fetch('/api/customers', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({
          clinic_id: clinicId,
          name: data.name,
          phone: data.phone,
          email: data.email,
          notes: data.notes,
          customAttributes: data.customAttributes,
        }),
      });

      if (!response.ok) {
        const json = await response.json().catch(() => ({}));
        throw new Error(json.message || 'æ‚£è€…ã®ç™»éŒ²ã«å¤±æ•—ã—ã¾ã—ãŸ');
      }

      const json = await response.json();
      const newPatient: Patient = {
        id: json.data.id,
        name: json.data.name,
        phone: json.data.phone,
        email: json.data.email ?? undefined,
        notes: json.data.notes ?? undefined,
        customAttributes: json.data.custom_attributes ?? undefined,
      };

      // ä¸€è¦§ã‚’æ›´æ–°ï¼ˆå…ˆé ­ã«è¿½åŠ ï¼‰
      setPatients(prev => [newPatient, ...prev].slice(0, MAX_PATIENTS));

      return newPatient;
    },
    [clinicId]
  );

  // æ‚£è€…ã‚’æ›´æ–°
  const updatePatient = useCallback(
    async (data: PatientUpdateDTO): Promise<Patient> => {
      if (!clinicId) {
        throw new Error('ã‚¯ãƒªãƒ‹ãƒƒã‚¯IDãŒã‚ã‚Šã¾ã›ã‚“');
      }

      const response = await fetch('/api/customers', {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({
          clinic_id: clinicId,
          id: data.id,
          name: data.name,
          phone: data.phone,
          email: data.email,
          notes: data.notes,
          customAttributes: data.customAttributes,
        }),
      });

      if (!response.ok) {
        const json = await response.json().catch(() => ({}));
        throw new Error(json.message || 'æ‚£è€…ã®æ›´æ–°ã«å¤±æ•—ã—ã¾ã—ãŸ');
      }

      const json = await response.json();
      const updatedPatient: Patient = {
        id: json.data.id,
        name: json.data.name,
        phone: json.data.phone,
        email: json.data.email ?? undefined,
        notes: json.data.notes ?? undefined,
        customAttributes: json.data.custom_attributes ?? undefined,
      };

      // ä¸€è¦§ã‚’æ›´æ–°
      setPatients(prev =>
        prev.map(p => (p.id === updatedPatient.id ? updatedPatient : p))
      );

      return updatedPatient;
    },
    [clinicId]
  );

  return {
    patients,
    isLoading,
    error,
    searchQuery,
    setSearchQuery,
    createPatient,
    updatePatient,
    refetch: fetchPatients,
  };
}

import { useEffect, useState } from 'react';
import { runIntegrationTests, TestResult } from '@/lib/integration-tests';

interface QualityMetrics {
  performanceScore?: number;
  accessibilityScore?: number;
  wcag22Compliant?: boolean;
  coreWebVitalsPass?: boolean;
  lastTestRun?: Date;
}

export const useQualityAssurance = (autoRun: boolean = false) => {
  const [metrics, setMetrics] = useState<QualityMetrics>({});
  const [isRunning, setIsRunning] = useState(false);
  const [results, setResults] = useState<TestResult[]>([]);

  const runTests = async () => {
    if (typeof window === 'undefined') return;

    setIsRunning(true);

    try {
      const testResults = await runIntegrationTests();
      setResults(testResults);

      // ãƒ¡ãƒˆãƒªã‚¯ã‚¹è¨ˆç®—
      const newMetrics: QualityMetrics = {
        performanceScore: calculatePerformanceScore(testResults),
        accessibilityScore: calculateAccessibilityScore(testResults),
        wcag22Compliant: checkWCAG22Compliance(testResults),
        coreWebVitalsPass: checkCoreWebVitals(testResults),
        lastTestRun: new Date(),
      };

      setMetrics(newMetrics);
    } catch (error) {
      console.error('Quality assurance tests failed:', error);
    } finally {
      setIsRunning(false);
    }
  };

  useEffect(() => {
    if (autoRun && process.env.NODE_ENV === 'development') {
      // é–‹ç™ºç’°å¢ƒã§ã®ã¿è‡ªå‹•å®Ÿè¡Œ
      const timer = setTimeout(runTests, 2000);
      return () => clearTimeout(timer);
    }
  }, [autoRun]);

  return {
    metrics,
    results,
    isRunning,
    runTests,
    // å“è³ªã‚¹ã‚³ã‚¢ã®ç·åˆè©•ä¾¡
    overallQuality: calculateOverallQuality(metrics),
  };
};

function calculatePerformanceScore(results: TestResult[]): number {
  const perfResult = results.find(r => r.name === 'Performance Test');
  if (!perfResult || perfResult.status === 'fail') return 0;

  const evaluation = perfResult.details?.evaluation;
  if (!evaluation) return 50;

  let score = 0;
  if (evaluation.lcp === 'good') score += 35;
  else if (evaluation.lcp === 'needs-improvement') score += 20;

  if (evaluation.fid === 'good') score += 30;
  else if (evaluation.fid === 'needs-improvement') score += 15;

  if (evaluation.cls === 'good') score += 35;
  else if (evaluation.cls === 'needs-improvement') score += 20;

  return Math.min(score, 100);
}

function calculateAccessibilityScore(results: TestResult[]): number {
  const a11yResult = results.find(r => r.name === 'Accessibility Test');
  if (!a11yResult) return 0;

  const { errors = 0, warnings = 0 } = a11yResult.details || {};

  if (errors === 0 && warnings === 0) return 100;
  if (errors === 0) return 85; // è­¦å‘Šã®ã¿
  if (errors <= 2) return 70; // è»½å¾®ãªã‚¨ãƒ©ãƒ¼
  if (errors <= 5) return 50; // ä¸­ç¨‹åº¦ã®ã‚¨ãƒ©ãƒ¼
  return 25; // é‡å¤§ãªã‚¨ãƒ©ãƒ¼
}

function checkWCAG22Compliance(results: TestResult[]): boolean {
  const a11yResult = results.find(r => r.name === 'Accessibility Test');
  return a11yResult?.details?.errors === 0;
}

function checkCoreWebVitals(results: TestResult[]): boolean {
  const perfResult = results.find(r => r.name === 'Performance Test');
  if (!perfResult) return false;

  const evaluation = perfResult.details?.evaluation;
  return (
    evaluation?.lcp === 'good' &&
    evaluation?.fid === 'good' &&
    evaluation?.cls === 'good'
  );
}

function calculateOverallQuality(metrics: QualityMetrics): {
  score: number;
  grade: 'A' | 'B' | 'C' | 'D' | 'F';
  status: 'excellent' | 'good' | 'fair' | 'poor' | 'critical';
} {
  const {
    performanceScore = 0,
    accessibilityScore = 0,
    wcag22Compliant = false,
    coreWebVitalsPass = false,
  } = metrics;

  let score = (performanceScore + accessibilityScore) / 2;

  // ãƒœãƒ¼ãƒŠã‚¹ç‚¹
  if (wcag22Compliant) score += 5;
  if (coreWebVitalsPass) score += 5;

  score = Math.min(score, 100);

  let grade: 'A' | 'B' | 'C' | 'D' | 'F';
  let status: 'excellent' | 'good' | 'fair' | 'poor' | 'critical';

  if (score >= 90) {
    grade = 'A';
    status = 'excellent';
  } else if (score >= 80) {
    grade = 'B';
    status = 'good';
  } else if (score >= 70) {
    grade = 'C';
    status = 'fair';
  } else if (score >= 60) {
    grade = 'D';
    status = 'poor';
  } else {
    grade = 'F';
    status = 'critical';
  }

  return { score, grade, status };
}

// å“è³ªä¿è¨¼ãƒ€ãƒƒã‚·ãƒ¥ãƒœãƒ¼ãƒ‰ç”¨ã®Hook
export const useQualityDashboard = () => {
  const qa = useQualityAssurance(true);
  const [history, setHistory] = useState<QualityMetrics[]>([]);

  useEffect(() => {
    if (qa.metrics.lastTestRun) {
      setHistory(prev => [...prev, qa.metrics].slice(-10)); // æœ€æ–°10ä»¶ã‚’ä¿æŒ
    }
  }, [qa.metrics]);

  const trend = useMemo(() => {
    if (history.length < 2) return 'stable';

    const current = qa.overallQuality.score;
    const previous = history[history.length - 2];
    const prevScore = calculateOverallQuality(previous).score;

    if (current > prevScore + 5) return 'improving';
    if (current < prevScore - 5) return 'declining';
    return 'stable';
  }, [history, qa.overallQuality.score]);

  return {
    ...qa,
    history,
    trend,
    recommendations: generateRecommendations(qa.results, qa.overallQuality),
  };
};

function generateRecommendations(
  results: TestResult[],
  quality: ReturnType<typeof calculateOverallQuality>
): string[] {
  const recommendations: string[] = [];

  if (quality.status === 'excellent') {
    recommendations.push(
      'å“è³ªã¯ excellent ã§ã™ï¼ç¾åœ¨ã®æ°´æº–ã‚’ç¶­æŒã—ã¦ãã ã•ã„ã€‚'
    );
    return recommendations;
  }

  const perfResult = results.find(r => r.name === 'Performance Test');
  if (perfResult?.status !== 'pass') {
    recommendations.push(
      'ãƒ‘ãƒ•ã‚©ãƒ¼ãƒãƒ³ã‚¹ã®æ”¹å–„ãŒå¿…è¦ã§ã™ã€‚ãƒãƒ³ãƒ‰ãƒ«ã‚µã‚¤ã‚ºã®æœ€é©åŒ–ã‚„ç”»åƒã®æœ€é©åŒ–ã‚’æ¤œè¨ã—ã¦ãã ã•ã„ã€‚'
    );
  }

  const a11yResult = results.find(r => r.name === 'Accessibility Test');
  if (a11yResult?.details?.errors > 0) {
    recommendations.push(
      'ã‚¢ã‚¯ã‚»ã‚·ãƒ“ãƒªãƒ†ã‚£ã‚¨ãƒ©ãƒ¼ã‚’ä¿®æ­£ã—ã¦ãã ã•ã„ã€‚WCAG 2.2ã‚¬ã‚¤ãƒ‰ãƒ©ã‚¤ãƒ³ã‚’å‚ç…§ã—ã¦ãã ã•ã„ã€‚'
    );
  }

  if (quality.score < 70) {
    recommendations.push(
      'å…¨ä½“çš„ãªå“è³ªæ”¹å–„ãŒæ€¥å‹™ã§ã™ã€‚é–‹ç™ºãƒãƒ¼ãƒ ã§ã®å“è³ªå‘ä¸Šã®å–ã‚Šçµ„ã¿ã‚’å¼·åŒ–ã—ã¦ãã ã•ã„ã€‚'
    );
  }

  return recommendations;
}

