    let isMounted = true;

    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        const res = await api.revenue.getAnalysis(clinicId);

        if (!isMounted) return;

        if (isSuccessResponse(res)) {
          const d = res.data as any;
