          setData({
            dailyRevenue: Number(d.dailyRevenue || 0),
            weeklyRevenue: Number(d.weeklyRevenue || 0),
            monthlyRevenue: Number(d.monthlyRevenue || 0),
            insuranceRevenue: Number(d.insuranceRevenue || 0),
