        } else {
          // APIã‚¨ãƒ©ãƒ¼æ™‚ã¯ã‚µãƒ³ãƒ—ãƒ«å€¤ã«ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯ã›ãšã‚¨ãƒ©ãƒ¼çŠ¶æ…‹ã«ã™ã‚‹
          const errorMessage =
            res.error?.message || 'åç›Šãƒ‡ãƒ¼ã‚¿ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ';
          setError(errorMessage);
          setData(INITIAL_DATA);
        }
      } catch (e) {
        if (isMounted) {
          console.error('useRevenue error:', e);
          setError('åç›Šãƒ‡ãƒ¼ã‚¿ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ');
          setData(INITIAL_DATA);
        }
      } finally {
        if (isMounted) {
          setLoading(false);
        }
      }
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [clinicId]);

  return {
    ...data,
    loading,
    error,
  };
};

