import { useSessionTimeout } from '@/lib/session-timeout';

interface SessionManagementConfig {
  enableCustomSession: boolean;
  enableTimeout: boolean;
  enableDeviceTracking: boolean;
  timeoutMinutes?: number;
}

interface SessionInfo {
  isAuthenticated: boolean;
  userId?: string;
  clinicId?: string;
  customSessionId?: string;
  supabaseSession?: any;
}

export function useSessionManagement(
  config: SessionManagementConfig = {
    enableCustomSession: true,
    enableTimeout: true,
    enableDeviceTracking: true,
    timeoutMinutes: 30,
  }
) {
  const router = useRouter();
  const [sessionInfo, setSessionInfo] = useState<SessionInfo>({
    isAuthenticated: false,
  });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Session Manager ã‚¤ãƒ³ã‚¹ã‚¿ãƒ³ã‚¹
  const [sessionManager] = useState(() => new SessionManager());

  // Supabase ã‚¯ãƒ©ã‚¤ã‚¢ãƒ³ãƒˆ
  const [supabase] = useState(() =>
    createBrowserClient(
      process.env.NEXT_PUBLIC_SUPABASE_URL!,
      process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
    )
  );

  // ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚¿ã‚¤ãƒ ã‚¢ã‚¦ãƒˆï¼ˆè¨­å®šã«å¿œã˜ã¦ï¼‰
  const sessionTimeout = useSessionTimeout({
    idleMinutes: config.timeoutMinutes || 30,
    warningMinutes: 5,
  });

  // åˆæœŸåŒ–ã¨ã‚»ãƒƒã‚·ãƒ§ãƒ³ç¢ºèª
  useEffect(() => {
    initializeSession();
  }, []);

  // Supabaseã‚»ãƒƒã‚·ãƒ§ãƒ³å¤‰æ›´ã®ç›£è¦–
  useEffect(() => {
    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange(async (event, session) => {
      console.log('Auth state changed:', event, session?.user?.id);

      if (event === 'SIGNED_IN' && session) {
        await handleLogin(session);
      } else if (event === 'SIGNED_OUT') {
        await handleLogout();
      }
    });

    return () => subscription.unsubscribe();
  }, [supabase.auth]);

  /**
   * ã‚»ãƒƒã‚·ãƒ§ãƒ³åˆæœŸåŒ–
   */
  const initializeSession = async () => {
    setIsLoading(true);
    setError(null);

    try {
      // Supabaseã‚»ãƒƒã‚·ãƒ§ãƒ³ã®ç¢ºèª
      const {
        data: { session },
        error,
      } = await supabase.auth.getSession();

      if (error) {
        console.error('Session check error:', error);
        setError('ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®ç¢ºèªã«å¤±æ•—ã—ã¾ã—ãŸ');
        return;
      }

      if (session?.user) {
        await handleLogin(session);
      } else {
        setSessionInfo({ isAuthenticated: false });
      }
    } catch (err) {
      console.error('Session initialization error:', err);
      setError('ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®åˆæœŸåŒ–ã«å¤±æ•—ã—ã¾ã—ãŸ');
    } finally {
      setIsLoading(false);
    }
  };

  /**
   * ãƒ­ã‚°ã‚¤ãƒ³å‡¦ç†
   */
  const handleLogin = async (session: any) => {
    try {
      const user = session.user;

      // ãƒ—ãƒ­ãƒ•ã‚¡ã‚¤ãƒ«æƒ…å ±ã®å–å¾—
      const { data: profile } = await supabase
        .from('profiles')
        .select('clinic_id, role')
        .eq('user_id', user.id)
        .single();

      if (!profile) {
        throw new Error('ãƒ—ãƒ­ãƒ•ã‚¡ã‚¤ãƒ«æƒ…å ±ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
      }

      // ã‚«ã‚¹ã‚¿ãƒ ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®ä½œæˆï¼ˆè¨­å®šãŒæœ‰åŠ¹ãªå ´åˆï¼‰
      let customSessionId;
      if (config.enableCustomSession) {
        customSessionId = await createCustomSession(user.id, profile.clinic_id);
      }

      // ã‚»ãƒƒã‚·ãƒ§ãƒ³æƒ…å ±ã®æ›´æ–°
      setSessionInfo({
        isAuthenticated: true,
        userId: user.id,
        clinicId: profile.clinic_id,
        customSessionId,
        supabaseSession: session,
      });

      // ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚¿ã‚¤ãƒ ã‚¢ã‚¦ãƒˆé–‹å§‹ï¼ˆè¨­å®šãŒæœ‰åŠ¹ãªå ´åˆï¼‰
      if (config.enableTimeout) {
        sessionTimeout.manager.start();
      }
    } catch (err) {
      console.error('Login handling error:', err);
      setError(err instanceof Error ? err.message : 'ãƒ­ã‚°ã‚¤ãƒ³å‡¦ç†ã‚¨ãƒ©ãƒ¼');
    }
  };

  /**
   * ãƒ­ã‚°ã‚¢ã‚¦ãƒˆå‡¦ç†
   */
  const handleLogout = async () => {
    try {
      // ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚¿ã‚¤ãƒ ã‚¢ã‚¦ãƒˆåœæ­¢
      if (config.enableTimeout) {
        sessionTimeout.manager.stop();
      }

      // ã‚«ã‚¹ã‚¿ãƒ ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®ç„¡åŠ¹åŒ–
      if (sessionInfo.customSessionId) {
        await sessionManager.revokeSession(
          sessionInfo.customSessionId,
          'manual_logout'
        );
      }

      // ã‚«ã‚¹ã‚¿ãƒ ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚¯ãƒƒã‚­ãƒ¼ã®ã‚¯ãƒªã‚¢
      document.cookie =
        'session-token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';

      // ã‚»ãƒƒã‚·ãƒ§ãƒ³æƒ…å ±ã®ãƒªã‚»ãƒƒãƒˆ
      setSessionInfo({ isAuthenticated: false });
    } catch (err) {
      console.error('Logout handling error:', err);
    }
  };

  /**
   * ã‚«ã‚¹ã‚¿ãƒ ã‚»ãƒƒã‚·ãƒ§ãƒ³ä½œæˆ
   */
  const createCustomSession = async (
    userId: string,
    clinicId: string
  ): Promise<string> => {
    try {
      // ãƒ‡ãƒã‚¤ã‚¹æƒ…å ±ã®å–å¾—
      const userAgent = navigator.userAgent;
      const deviceInfo = parseUserAgent(userAgent);

      // IPæƒ…å ±ã®å–å¾—ï¼ˆç°¡æ˜“ç‰ˆï¼‰
      const ipAddress = await getCurrentUserIP();

      // ã‚»ãƒƒã‚·ãƒ§ãƒ³ä½œæˆ
      const { session, token } = await sessionManager.createSession(
        userId,
        clinicId,
        {
          deviceInfo,
          ipAddress,
          userAgent,
          rememberDevice: false, // å¿…è¦ã«å¿œã˜ã¦è¨­å®š
        }
      );

      // ã‚»ãƒƒã‚·ãƒ§ãƒ³ãƒˆãƒ¼ã‚¯ãƒ³ã‚’ã‚¯ãƒƒã‚­ãƒ¼ã«ä¿å­˜
      const expires = new Date(session.expires_at);
      document.cookie = `session-token=${token}; expires=${expires.toUTCString()}; path=/; secure; samesite=strict`;

      return session.id;
    } catch (error) {
      console.error('Custom session creation error:', error);
      throw new Error('ã‚«ã‚¹ã‚¿ãƒ ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®ä½œæˆã«å¤±æ•—ã—ã¾ã—ãŸ');
    }
  };

  /**
   * æ‰‹å‹•ãƒ­ã‚°ã‚¢ã‚¦ãƒˆ
   */
  const logout = async () => {
    setIsLoading(true);
    try {
      await supabase.auth.signOut();
      await handleLogout();
      router.push('/admin/login');
    } catch (err) {
      console.error('Manual logout error:', err);
      setError('ãƒ­ã‚°ã‚¢ã‚¦ãƒˆã«å¤±æ•—ã—ã¾ã—ãŸ');
    } finally {
      setIsLoading(false);
    }
  };

  /**
   * ã‚»ãƒƒã‚·ãƒ§ãƒ³å»¶é•·
   */
  const extendSession = async (minutes: number = 30) => {
    try {
      if (config.enableTimeout) {
        sessionTimeout.extendSession(minutes);
      }

      // ã‚«ã‚¹ã‚¿ãƒ ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚‚å»¶é•·
      if (sessionInfo.customSessionId && config.enableCustomSession) {
        // ã‚«ã‚¹ã‚¿ãƒ ã‚»ãƒƒã‚·ãƒ§ãƒ³å»¶é•·ã®ãƒ­ã‚¸ãƒƒã‚¯ã‚’å®Ÿè£…
        console.log('Extending custom session:', minutes);
      }
    } catch (err) {
      console.error('Session extension error:', err);
      setError('ã‚»ãƒƒã‚·ãƒ§ãƒ³å»¶é•·ã«å¤±æ•—ã—ã¾ã—ãŸ');
    }
  };

  /**
   * ã‚»ãƒƒã‚·ãƒ§ãƒ³æƒ…å ±ã®æ›´æ–°
   */
  const refreshSession = async () => {
    try {
      await initializeSession();
    } catch (err) {
      console.error('Session refresh error:', err);
      setError('ã‚»ãƒƒã‚·ãƒ§ãƒ³æ›´æ–°ã«å¤±æ•—ã—ã¾ã—ãŸ');
    }
  };

  return {
    // ã‚»ãƒƒã‚·ãƒ§ãƒ³çŠ¶æ…‹
    sessionInfo,
    isLoading,
    error,

    // ã‚¿ã‚¤ãƒ ã‚¢ã‚¦ãƒˆæƒ…å ±
    timeoutState: config.enableTimeout ? sessionTimeout.state : null,

    // ã‚¢ã‚¯ã‚·ãƒ§ãƒ³
    logout,
    extendSession,
    refreshSession,

    // ãƒ¦ãƒ¼ãƒ†ã‚£ãƒªãƒ†ã‚£
    clearError: () => setError(null),
  };
}

/**
 * ç¾åœ¨ã®ãƒ¦ãƒ¼ã‚¶ãƒ¼IPã‚¢ãƒ‰ãƒ¬ã‚¹ã‚’å–å¾—ï¼ˆç°¡æ˜“ç‰ˆï¼‰
 */
async function getCurrentUserIP(): Promise<string | undefined> {
  try {
    // å®Ÿéš›ã®å®Ÿè£…ã§ã¯å¤–éƒ¨APIã¾ãŸã¯ã‚µãƒ¼ãƒãƒ¼ã‚µã‚¤ãƒ‰ã§å–å¾—
    return 'unknown';
  } catch (error) {
    console.error('IP address fetch error:', error);
    return undefined;
  }
}

/**
 * ãƒšãƒ¼ã‚¸ãƒ¬ãƒ™ãƒ«ã§ã®ã‚»ãƒƒã‚·ãƒ§ãƒ³ä¿è­·ãƒ•ãƒƒã‚¯
 */
export function useSessionProtection(requiredRole?: string) {
  const sessionManagement = useSessionManagement();
  const router = useRouter();

  useEffect(() => {
    if (!sessionManagement.isLoading) {
      if (!sessionManagement.sessionInfo.isAuthenticated) {
        router.push('/admin/login');
        return;
      }

      // å½¹å‰²ãƒã‚§ãƒƒã‚¯ï¼ˆå®Ÿè£…ã™ã‚‹å ´åˆï¼‰
      if (requiredRole) {
        // TODO: å½¹å‰²ãƒã‚§ãƒƒã‚¯ãƒ­ã‚¸ãƒƒã‚¯ã‚’è¿½åŠ 
      }
    }
  }, [
    sessionManagement.isLoading,
    sessionManagement.sessionInfo.isAuthenticated,
    requiredRole,
    router,
  ]);

  return sessionManagement;
}

import { useState, useCallback } from 'react';
import { API_ENDPOINTS, ERROR_MESSAGES } from '@/lib/constants';
import {
  MasterDataDetail,
  FilterState,
  UseSystemSettingsReturn,
  ApiResponse,
} from '@/types/admin';

export const useSystemSettings = (): UseSystemSettingsReturn => {
  const [masterData, setMasterData] = useState<MasterDataDetail[]>([]);
  const [categories, setCategories] = useState<string[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [filterState, setFilterState] = useState<FilterState>({
    search: '',
    category: '',
    clinicId: '',
    isPublic: false,
  });

  // ã‚¨ãƒ©ãƒ¼ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã®ãƒ•ã‚©ãƒ¼ãƒãƒƒãƒˆ
  const formatErrorMessage = (error: any): string => {
    if (error.details && Array.isArray(error.details)) {
      return error.details
        .map((detail: any) => `${detail.path?.join('.')}: ${detail.message}`)
        .join(', ');
    }
    return error.message || ERROR_MESSAGES.SERVER_ERROR;
  };

  // ãƒã‚¹ã‚¿ãƒ¼ãƒ‡ãƒ¼ã‚¿ã®å–å¾—
  const fetchMasterData = useCallback(
    async (filters?: Partial<FilterState>) => {
      try {
        setLoading(true);
        setError(null);

        const params = new URLSearchParams();
        const currentFilters = { ...filterState, ...filters };

        if (currentFilters.category)
          params.append('category', currentFilters.category);
        if (currentFilters.clinicId)
          params.append('clinic_id', currentFilters.clinicId);
        if (currentFilters.isPublic !== undefined) {
          params.append('is_public', currentFilters.isPublic.toString());
        }

        const response = await fetch(
          `${API_ENDPOINTS.ADMIN.MASTER_DATA}?${params.toString()}`
        );
        const result: ApiResponse<MasterDataDetail[]> = await response.json();

        if (!result.success) {
          throw new Error(result.error || ERROR_MESSAGES.SERVER_ERROR);
        }

        const data = result.data || [];
        setMasterData(data);

        // ã‚«ãƒ†ã‚´ãƒªã‚’æŠ½å‡º
        const uniqueCategories = Array.from(
          new Set(data.map(item => item.category))
        ).sort();
        setCategories(uniqueCategories);

        if (filters) {
          setFilterState(prev => ({ ...prev, ...filters }));
        }
      } catch (err) {
        const errorMessage =
          err instanceof Error ? err.message : ERROR_MESSAGES.NETWORK_ERROR;
        setError(errorMessage);
        console.error('ãƒã‚¹ã‚¿ãƒ¼ãƒ‡ãƒ¼ã‚¿å–å¾—ã‚¨ãƒ©ãƒ¼:', err);
      } finally {
        setLoading(false);
      }
    },
    [filterState]
  );

  // ãƒã‚¹ã‚¿ãƒ¼ãƒ‡ãƒ¼ã‚¿ã®ä½œæˆ
  const createMasterData = useCallback(
    async (data: Partial<MasterDataDetail>): Promise<boolean> => {
      try {
        setLoading(true);
        setError(null);

        const response = await fetch(API_ENDPOINTS.ADMIN.MASTER_DATA, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(data),
        });

        const result: ApiResponse<MasterDataDetail> = await response.json();

        if (!result.success) {
          throw { message: result.error, details: result.details };
        }

        if (result.data) {
          setMasterData(prev => [...prev, result.data!]);

          // ã‚«ãƒ†ã‚´ãƒªæ›´æ–°
          if (
            result.data.category &&
            !categories.includes(result.data.category)
          ) {
            setCategories(prev => [...prev, result.data!.category].sort());
          }
        }

        return true;
      } catch (err: any) {
        const errorMessage = formatErrorMessage(err);
        setError(errorMessage);
        console.error('ãƒã‚¹ã‚¿ãƒ¼ãƒ‡ãƒ¼ã‚¿ä½œæˆã‚¨ãƒ©ãƒ¼:', err);
        return false;
      } finally {
        setLoading(false);
      }
    },
    [categories]
  );

  // ãƒã‚¹ã‚¿ãƒ¼ãƒ‡ãƒ¼ã‚¿ã®æ›´æ–°
  const updateMasterData = useCallback(
    async (
      id: string,
      updates: Partial<MasterDataDetail>
    ): Promise<boolean> => {
      try {
        setLoading(true);
        setError(null);

        const response = await fetch(API_ENDPOINTS.ADMIN.MASTER_DATA, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ id, ...updates }),
        });

        const result: ApiResponse<MasterDataDetail> = await response.json();

        if (!result.success) {
          throw { message: result.error, details: result.details };
        }

        if (result.data) {
          setMasterData(prev =>
            prev.map(item => (item.id === id ? result.data! : item))
          );

          // ã‚«ãƒ†ã‚´ãƒªæ›´æ–°
          if (
            result.data.category &&
            !categories.includes(result.data.category)
          ) {
            setCategories(prev => [...prev, result.data!.category].sort());
          }
        }

        return true;
      } catch (err: any) {
        const errorMessage = formatErrorMessage(err);
        setError(errorMessage);
        console.error('ãƒã‚¹ã‚¿ãƒ¼ãƒ‡ãƒ¼ã‚¿æ›´æ–°ã‚¨ãƒ©ãƒ¼:', err);
        return false;
      } finally {
        setLoading(false);
      }
    },
    [categories]
  );

  // ãƒã‚¹ã‚¿ãƒ¼ãƒ‡ãƒ¼ã‚¿ã®å‰Šé™¤
  const deleteMasterData = useCallback(async (id: string): Promise<boolean> => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(
        `${API_ENDPOINTS.ADMIN.MASTER_DATA}?id=${id}`,
        {
          method: 'DELETE',
        }
      );

      const result: ApiResponse = await response.json();

      if (!result.success) {
        throw new Error(result.error || ERROR_MESSAGES.SERVER_ERROR);
      }

      setMasterData(prev => prev.filter(item => item.id !== id));
      return true;
    } catch (err) {
      const errorMessage =
        err instanceof Error ? err.message : ERROR_MESSAGES.NETWORK_ERROR;
      setError(errorMessage);
      console.error('ãƒã‚¹ã‚¿ãƒ¼ãƒ‡ãƒ¼ã‚¿å‰Šé™¤ã‚¨ãƒ©ãƒ¼:', err);
      return false;
    } finally {
      setLoading(false);
    }
  }, []);

  // ãƒ•ã‚£ãƒ«ã‚¿ãƒ¼è¨­å®š
  const setFilter = useCallback((filter: Partial<FilterState>) => {
    setFilterState(prev => ({ ...prev, ...filter }));
  }, []);

  // ãƒ•ã‚£ãƒ«ã‚¿ãƒ¼ãƒªã‚»ãƒƒãƒˆ
  const resetFilter = useCallback(() => {
    setFilterState({
      search: '',
      category: '',
      clinicId: '',
      isPublic: false,
    });
  }, []);

  const exportMasterData = useCallback(
    async (filters?: Partial<FilterState>) => {
      try {
        setLoading(true);
        setError(null);

        const params = new URLSearchParams();
        const currentFilters = { ...filterState, ...filters };

        if (currentFilters.clinicId) {
          params.append('clinic_id', currentFilters.clinicId);
        }

        const response = await fetch(
          `${API_ENDPOINTS.ADMIN.MASTER_DATA_EXPORT}?${params.toString()}`
        );
        const result: ApiResponse<{
          items: MasterDataDetail[];
          snapshot_key: string;
        }> = await response.json();

        if (!result.success) {
          throw new Error(result.error || ERROR_MESSAGES.SERVER_ERROR);
        }

        return result.data ?? null;
      } catch (err) {
        const errorMessage =
          err instanceof Error ? err.message : ERROR_MESSAGES.NETWORK_ERROR;
        setError(errorMessage);
        console.error('ãƒã‚¹ã‚¿ãƒ¼ãƒ‡ãƒ¼ã‚¿ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆã‚¨ãƒ©ãƒ¼:', err);
        return null;
      } finally {
        setLoading(false);
      }
    },
    [filterState]
  );

  const importMasterData = useCallback(
    async (items: MasterDataDetail[], clinicId?: string | null) => {
      try {
        setLoading(true);
        setError(null);

        const response = await fetch(API_ENDPOINTS.ADMIN.MASTER_DATA_IMPORT, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            items,
            clinic_id: clinicId ?? (filterState.clinicId || undefined),
          }),
        });

        const result: ApiResponse<{ imported: number }> = await response.json();

        if (!result.success) {
          throw new Error(result.error || ERROR_MESSAGES.SERVER_ERROR);
        }

        return true;
      } catch (err) {
        const errorMessage =
          err instanceof Error ? err.message : ERROR_MESSAGES.NETWORK_ERROR;
        setError(errorMessage);
        console.error('ãƒã‚¹ã‚¿ãƒ¼ãƒ‡ãƒ¼ã‚¿ã‚¤ãƒ³ãƒãƒ¼ãƒˆã‚¨ãƒ©ãƒ¼:', err);
        return false;
      } finally {
        setLoading(false);
      }
    },
    [filterState.clinicId]
  );

  const rollbackMasterData = useCallback(
    async (clinicId?: string | null) => {
      try {
        setLoading(true);
        setError(null);

        const response = await fetch(API_ENDPOINTS.ADMIN.MASTER_DATA_ROLLBACK, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            clinic_id: clinicId ?? (filterState.clinicId || undefined),
          }),
        });

        const result: ApiResponse<{ restored: number }> = await response.json();

        if (!result.success) {
          throw new Error(result.error || ERROR_MESSAGES.SERVER_ERROR);
        }

        return true;
      } catch (err) {
        const errorMessage =
          err instanceof Error ? err.message : ERROR_MESSAGES.NETWORK_ERROR;
        setError(errorMessage);
        console.error('ãƒã‚¹ã‚¿ãƒ¼ãƒ‡ãƒ¼ã‚¿ãƒ­ãƒ¼ãƒ«ãƒãƒƒã‚¯ã‚¨ãƒ©ãƒ¼:', err);
        return false;
      } finally {
        setLoading(false);
      }
    },
    [filterState.clinicId]
  );

  return {
    // ãƒ‡ãƒ¼ã‚¿çŠ¶æ…‹
    masterData,
    categories,

    // UIçŠ¶æ…‹
    loading,
    error,
    filters: filterState,

    // ã‚¢ã‚¯ã‚·ãƒ§ãƒ³
    fetchMasterData,
    createMasterData,
    updateMasterData,
    deleteMasterData,
    exportMasterData,
    importMasterData,
    rollbackMasterData,

    // ãƒ•ã‚£ãƒ«ã‚¿ãƒ¼
    updateFilters: setFilter,
    resetFilters: resetFilter,
  };
};

