/**
 * ã‚·ã‚¹ãƒ†ãƒ è¨­å®šç®¡ç†ãƒ•ãƒƒã‚¯ v2 (React Queryç‰ˆ)
 *
 * æ—§ç‰ˆ(useSystemSettings)ã¨ã®é•ã„:
 * - React Queryã«ã‚ˆã‚‹è‡ªå‹•ã‚­ãƒ£ãƒƒã‚·ãƒ³ã‚°
 * - æ¥½è¦³çš„æ›´æ–°ã§UXå‘ä¸Š
 * - è‡ªå‹•çš„ãªã‚¨ãƒ©ãƒ¼ãƒãƒ³ãƒ‰ãƒªãƒ³ã‚°ãƒ»å†è©¦è¡Œ
 * - ãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰ã§ã®ãƒ‡ãƒ¼ã‚¿æ›´æ–°
 */
export function useSystemSettingsV2(): UseSystemSettingsReturn {
  // ãƒ•ã‚£ãƒ«ã‚¿ãƒ¼çŠ¶æ…‹ç®¡ç†
  const [filters, setFilters] = useState<FilterState>({
    search: '',
    category: '',
    clinicId: '',
    isPublic: false,
  });

  // React Query ãƒ•ãƒƒã‚¯
  const { data, isLoading, error, isFetching, isRefetching } =
    useSystemSettingsQuery(filters, {
      // ãƒ•ã‚£ãƒ«ã‚¿ãƒ¼ãŒç©ºã®å ´åˆã¯è‡ªå‹•å®Ÿè¡Œã—ãªã„
      enabled:
        Object.values(filters).some(
          value => value !== '' && value !== undefined
        ) || true, // é–‹ç™ºæ™‚ã¯å¸¸ã«æœ‰åŠ¹
    });

  const createMutation = useCreateSystemSettingMutation({
    onSuccess: () => {
      // æˆåŠŸæ™‚ã®è¿½åŠ å‡¦ç†ãŒã‚ã‚Œã°è¨˜è¿°
    },
    onError: error => {
      console.error('ä½œæˆã‚¨ãƒ©ãƒ¼:', error.message);
    },
  });

  const updateMutation = useUpdateSystemSettingMutation({
    onSuccess: () => {
      // æˆåŠŸæ™‚ã®è¿½åŠ å‡¦ç†ãŒã‚ã‚Œã°è¨˜è¿°
    },
    onError: error => {
      console.error('æ›´æ–°ã‚¨ãƒ©ãƒ¼:', error.message);
    },
  });

  const deleteMutation = useDeleteSystemSettingMutation({
    onSuccess: () => {
      // æˆåŠŸæ™‚ã®è¿½åŠ å‡¦ç†ãŒã‚ã‚Œã°è¨˜è¿°
    },
    onError: error => {
      console.error('å‰Šé™¤ã‚¨ãƒ©ãƒ¼:', error.message);
    },
  });

  const refreshData = useRefreshSystemSettings();

  // ã‚³ãƒ¼ãƒ«ãƒãƒƒã‚¯é–¢æ•°
  const updateFilters = useCallback((newFilters: Partial<FilterState>) => {
    setFilters(prev => ({ ...prev, ...newFilters }));
  }, []);

  const resetFilters = useCallback(() => {
    setFilters({
      search: '',
      category: '',
      clinicId: '',
      isPublic: false,
    });
  }, []);

  const createMasterData = useCallback(
    async (
      data: Partial<MasterDataDetail>
    ): Promise<Partial<MasterDataDetail>> => {
      try {
        const result = await createMutation.mutateAsync(data);
        return result;
      } catch (error) {
        throw error;
      }
    },
    [createMutation]
  );

  const updateMasterData = useCallback(
    async (id: string, updates: Partial<MasterDataDetail>): Promise<void> => {
      try {
        await updateMutation.mutateAsync({ id, data: updates });
      } catch (error) {
        throw error;
      }
    },
    [updateMutation]
  );

  const deleteMasterData = useCallback(
    async (id: string): Promise<void> => {
      try {
        await deleteMutation.mutateAsync(id);
      } catch (error) {
        throw error;
      }
    },
    [deleteMutation]
  );

  // ã‚¨ãƒ©ãƒ¼ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã®ãƒ•ã‚©ãƒ¼ãƒãƒƒãƒˆ
  const formatErrorMessage = useCallback((error: unknown): string => {
    if (error instanceof Error) {
      return error.message;
    }
    if (typeof error === 'string') {
      return error;
    }
    return 'ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ';
  }, []);

  // ãƒ¬ã‚¹ãƒãƒ³ã‚¹å‹ã«åˆã‚ã›ã¦ãƒ‡ãƒ¼ã‚¿ã‚’è¿”ã™
  return {
    // ãƒ‡ãƒ¼ã‚¿
    masterData: data?.items || [],

    // çŠ¶æ…‹
    loading: isLoading,
    error: error ? formatErrorMessage(error) : null,

    // ãƒ•ã‚£ãƒ«ã‚¿ãƒ¼
    filters,

    // ã‚¢ã‚¯ã‚·ãƒ§ãƒ³
    fetchMasterData: refreshData,
    createMasterData,
    updateMasterData,
    deleteMasterData,
    updateFilters,
    resetFilters,

    // è¿½åŠ çŠ¶æ…‹ï¼ˆReact Queryç‰¹æœ‰ï¼‰
    isFetching, // ãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰ãƒ•ã‚§ãƒƒãƒä¸­
    isRefetching, // æ‰‹å‹•ãƒªãƒ•ãƒ¬ãƒƒã‚·ãƒ¥ä¸­

    // ãƒŸãƒ¥ãƒ¼ãƒ†ãƒ¼ã‚·ãƒ§ãƒ³çŠ¶æ…‹
    isCreating: createMutation.isPending,
    isUpdating: updateMutation.isPending,
    isDeleting: deleteMutation.isPending,

    // ãƒ‡ãƒ¼ã‚¿çµ±è¨ˆ
    total: data?.total || 0,
  };
}

