export { useSystemSettingsV2 as useSystemSettings };

import { useState, useCallback, useEffect } from 'react';
import { logger } from '@/lib/logger';
import { API_ENDPOINTS, ERROR_MESSAGES, PAGINATION } from '@/lib/constants';
import {
  TableData,
  TableListItem,
  TableConfig,
  PaginationState,
  SortState,
  FilterState,
  SortOrder,
  UseTableManagerReturn,
  ApiResponse,
} from '@/types/admin';

export const useTableManager = (): UseTableManagerReturn => {
  // ãƒ‡ãƒ¼ã‚¿çŠ¶æ…‹
  const [tableData, setTableData] = useState<TableData[]>([]);
  const [tableList, setTableList] = useState<TableListItem[]>([]);
  const [tableConfig, setTableConfig] = useState<TableConfig | null>(null);
  const [currentTable, setCurrentTableState] = useState<string>('');

  // UIçŠ¶æ…‹
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [pagination, setPagination] = useState<PaginationState>({
    page: 1,
    limit: PAGINATION.DEFAULT_PAGE_SIZE,
    total: 0,
    total_pages: 0,
  });
  const [sortState, setSortState] = useState<SortState>({
    sortBy: 'created_at',
    sortOrder: 'desc',
  });
  const [filterState, setFilterState] = useState<FilterState>({
    search: '',
    category: '',
    clinicId: '',
    isPublic: false,
  });

  // ã‚¨ãƒ©ãƒ¼ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã®ãƒ•ã‚©ãƒ¼ãƒãƒƒãƒˆ
  const formatErrorMessage = (error: unknown): string => {
    const err = error as {
      details?: Array<{ path?: string[]; message?: string }>;
      message?: string;
    };
    if (err.details && Array.isArray(err.details)) {
      return err.details
        .map(detail => `${(detail.path || []).join('.')}: ${detail.message}`)
        .join(', ');
    }
    return err.message || ERROR_MESSAGES.SERVER_ERROR;
  };

  // ãƒ†ãƒ¼ãƒ–ãƒ«ä¸€è¦§ã®å–å¾—
  const fetchTableList = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(API_ENDPOINTS.ADMIN.TABLES);
      const result: ApiResponse<TableListItem[]> = await response.json();

      if (!result.success) {
        throw new Error(result.error || ERROR_MESSAGES.SERVER_ERROR);
      }

      setTableList(result.data || []);
    } catch (err) {
      const errorMessage =
        err instanceof Error ? err.message : ERROR_MESSAGES.NETWORK_ERROR;
      setError(errorMessage);
      logger.error('ãƒ†ãƒ¼ãƒ–ãƒ«ä¸€è¦§å–å¾—ã‚¨ãƒ©ãƒ¼:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // ãƒ†ãƒ¼ãƒ–ãƒ«ãƒ‡ãƒ¼ã‚¿ã®å–å¾—
  const fetchTableData = useCallback(
    async (tableName?: string) => {
      const targetTable = tableName || currentTable;
      if (!targetTable) return;

      try {
        setLoading(true);
        setError(null);

        const params = new URLSearchParams({
          table: targetTable,
          page: pagination.page.toString(),
          limit: pagination.limit.toString(),
          sort_by: sortState.sortBy,
          sort_order: sortState.sortOrder,
        });

        if (filterState.search) {
          params.append('search', filterState.search);
        }

        const response = await fetch(
          `${API_ENDPOINTS.ADMIN.TABLES}?${params.toString()}`
        );
        const result: ApiResponse<{
          data: TableData[];
          table_config: TableConfig;
          pagination: PaginationState;
        }> = await response.json();

        if (!result.success) {
          throw new Error(result.error || ERROR_MESSAGES.SERVER_ERROR);
        }

        if (result.data) {
          setTableData(result.data.data || []);
          setTableConfig(result.data.table_config || null);
          setPagination(result.data.pagination || pagination);
        }

        if (tableName) {
          setCurrentTableState(tableName);
        }
      } catch (err) {
        const errorMessage =
          err instanceof Error ? err.message : ERROR_MESSAGES.NETWORK_ERROR;
        setError(errorMessage);
        console.error('ãƒ†ãƒ¼ãƒ–ãƒ«ãƒ‡ãƒ¼ã‚¿å–å¾—ã‚¨ãƒ©ãƒ¼:', err);
      } finally {
        setLoading(false);
      }
    },
    [
      currentTable,
      pagination.page,
      pagination.limit,
      sortState,
      filterState.search,
    ]
  );

  // ãƒ†ãƒ¼ãƒ–ãƒ«ãƒ‡ãƒ¼ã‚¿ã®ä½œæˆ
  const createTableData = useCallback(
    async (data: Record<string, unknown>): Promise<boolean> => {
      if (!currentTable) return false;

      try {
        setLoading(true);
        setError(null);

        const response = await fetch(API_ENDPOINTS.ADMIN.TABLES, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ table_name: currentTable, data }),
        });

        const result: ApiResponse<TableData> = await response.json();

        if (!result.success) {
          throw { message: result.error, details: result.details };
        }

        if (result.data) {
          setTableData(prev => [result.data!, ...prev]);
          // ãƒšãƒ¼ã‚¸ãƒãƒ¼ã‚·ãƒ§ãƒ³æƒ…å ±ã‚’æ›´æ–°
          setPagination(prev => ({ ...prev, total: prev.total + 1 }));
        }

        return true;
      } catch (err: unknown) {
        const errorMessage = formatErrorMessage(err);
        setError(errorMessage);
        logger.error('ãƒ†ãƒ¼ãƒ–ãƒ«ãƒ‡ãƒ¼ã‚¿ä½œæˆã‚¨ãƒ©ãƒ¼:', err);
        return false;
      } finally {
        setLoading(false);
      }
    },
    [currentTable]
  );

  // ãƒ†ãƒ¼ãƒ–ãƒ«ãƒ‡ãƒ¼ã‚¿ã®æ›´æ–°
  const updateTableData = useCallback(
    async (id: string, data: Record<string, unknown>): Promise<boolean> => {
      if (!currentTable) return false;

      try {
        setLoading(true);
        setError(null);

        const response = await fetch(API_ENDPOINTS.ADMIN.TABLES, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ table_name: currentTable, id, data }),
        });

        const result: ApiResponse<TableData> = await response.json();

        if (!result.success) {
          throw { message: result.error, details: result.details };
        }

        if (result.data) {
          setTableData(prev =>
            prev.map(item => (item.id === id ? result.data! : item))
          );
        }

        return true;
      } catch (err: unknown) {
        const errorMessage = formatErrorMessage(err);
        setError(errorMessage);
        logger.error('ãƒ†ãƒ¼ãƒ–ãƒ«ãƒ‡ãƒ¼ã‚¿æ›´æ–°ã‚¨ãƒ©ãƒ¼:', err);
        return false;
      } finally {
        setLoading(false);
      }
    },
    [currentTable]
  );

  // ãƒ†ãƒ¼ãƒ–ãƒ«ãƒ‡ãƒ¼ã‚¿ã®å‰Šé™¤
  const deleteTableData = useCallback(
    async (id: string): Promise<boolean> => {
      if (!currentTable) return false;

      try {
        setLoading(true);
        setError(null);

        const response = await fetch(
          `${API_ENDPOINTS.ADMIN.TABLES}?table=${currentTable}&id=${id}`,
          {
            method: 'DELETE',
          }
        );

        const result: ApiResponse = await response.json();

        if (!result.success) {
          throw new Error(result.error || ERROR_MESSAGES.SERVER_ERROR);
        }

        setTableData(prev => prev.filter(item => item.id !== id));
        // ãƒšãƒ¼ã‚¸ãƒãƒ¼ã‚·ãƒ§ãƒ³æƒ…å ±ã‚’æ›´æ–°
        setPagination(prev => ({
          ...prev,
          total: Math.max(0, prev.total - 1),
        }));

        return true;
      } catch (err) {
        const errorMessage =
          err instanceof Error ? err.message : ERROR_MESSAGES.NETWORK_ERROR;
        setError(errorMessage);
        logger.error('ãƒ†ãƒ¼ãƒ–ãƒ«ãƒ‡ãƒ¼ã‚¿å‰Šé™¤ã‚¨ãƒ©ãƒ¼:', err);
        return false;
      } finally {
        setLoading(false);
      }
    },
    [currentTable]
  );

  // ãƒ†ãƒ¼ãƒ–ãƒ«é¸æŠ
  const setCurrentTable = useCallback((tableName: string) => {
    setCurrentTableState(tableName);
    // ãƒšãƒ¼ã‚¸ã‚’ãƒªã‚»ãƒƒãƒˆ
    setPagination(prev => ({ ...prev, page: 1 }));
    setTableData([]);
    setTableConfig(null);
  }, []);

  // æ¤œç´¢è¨­å®š
  const setSearch = useCallback((term: string) => {
    setFilterState(prev => ({ ...prev, search: term }));
    setPagination(prev => ({ ...prev, page: 1 })); // æ¤œç´¢æ™‚ã¯ãƒšãƒ¼ã‚¸ã‚’ãƒªã‚»ãƒƒãƒˆ
  }, []);

  // ã‚½ãƒ¼ãƒˆè¨­å®š
  const setSortStateValue = useCallback(
    (sortBy: string, sortOrder: SortOrder) => {
      setSortState({ sortBy, sortOrder });
      setPagination(prev => ({ ...prev, page: 1 })); // ã‚½ãƒ¼ãƒˆæ™‚ã¯ãƒšãƒ¼ã‚¸ã‚’ãƒªã‚»ãƒƒãƒˆ
    },
    []
  );

  // ãƒšãƒ¼ã‚¸è¨­å®š
  const setPage = useCallback((page: number) => {
    setPagination(prev => ({ ...prev, page }));
  }, []);

  // çŠ¶æ…‹ãƒªã‚»ãƒƒãƒˆ
  const resetState = useCallback(() => {
    setTableData([]);
    setTableConfig(null);
    setCurrentTableState('');
    setError(null);
    setPagination({
      page: 1,
      limit: PAGINATION.DEFAULT_PAGE_SIZE,
      total: 0,
      total_pages: 0,
    });
    setSortState({
      sortBy: 'created_at',
      sortOrder: 'desc',
    });
    setFilterState({
      search: '',
      category: '',
      clinicId: '',
      isPublic: false,
    });
  }, []);

  // åˆæœŸåŒ–æ™‚ã«ãƒ†ãƒ¼ãƒ–ãƒ«ä¸€è¦§ã‚’å–å¾—
  useEffect(() => {
    fetchTableList();
  }, [fetchTableList]);

  // ãƒ†ãƒ¼ãƒ–ãƒ«ã€ãƒšãƒ¼ã‚¸ã€ã‚½ãƒ¼ãƒˆã€æ¤œç´¢ãŒå¤‰ã‚ã£ãŸã‚‰ãƒ‡ãƒ¼ã‚¿ã‚’å†å–å¾—
  useEffect(() => {
    if (currentTable) {
      fetchTableData();
    }
  }, [currentTable, pagination.page, sortState, filterState.search]);

  return {
    // ãƒ‡ãƒ¼ã‚¿çŠ¶æ…‹
    tableData,
    tableList,
    tableConfig,
    currentTable,

    // UIçŠ¶æ…‹
    loading,
    error,
    pagination,
    sortState,
    filterState,

    // ã‚¢ã‚¯ã‚·ãƒ§ãƒ³
    setCurrentTable,
    fetchTableList,
    fetchTableData,
    createTableData,
    updateTableData,
    deleteTableData,

    // ãƒ•ã‚£ãƒ«ã‚¿ãƒ¼ãƒ»ã‚½ãƒ¼ãƒˆ
    setSearch,
    setSortState: setSortStateValue,
    setPage,

    // ãƒªã‚»ãƒƒãƒˆ
    resetState,
  };
};

'use client';

import { useEffect, useState } from 'react';
import type { User } from '@supabase/supabase-js';
import { supabase } from '@/lib/supabase/client';
import { useOptionalUserProfileContext } from '@/providers/user-profile-context';
import type { UserProfile } from '@/types/user-profile';
import { CLINIC_ADMIN_ROLES, type Role } from '@/lib/constants/roles';

interface ProfileState {
  profile: UserProfile | null;
  loading: boolean;
  error: string | null;
}

// Q4æ±ºå®š: isAdmin ã« manager ã‚’å«ã‚ã‚‹ï¼ˆçµ±ä¸€ï¼‰
// @spec docs/stabilization/spec-auth-role-alignment-v0.1.md
// CLINIC_ADMIN_ROLES = admin, clinic_admin, manager
const ADMIN_ROLES = CLINIC_ADMIN_ROLES;
const PROFILE_FETCH_TIMEOUT_MS = 8000;
const SESSION_FETCH_TIMEOUT_MS = 2000;

const resolveRole = (user: User): string | null => {
  const appMeta = user.app_metadata as
    | Record<string, unknown>
    | null
    | undefined;
  const userMeta = user.user_metadata as
    | Record<string, unknown>
    | null
    | undefined;
  const roleCandidate =
    appMeta?.user_role ??
    appMeta?.role ??
    userMeta?.role ??
    userMeta?.user_role ??
    null;

  return typeof roleCandidate === 'string' ? roleCandidate : null;
};

const resolveClinicId = (user: User): string | null => {
  const appMeta = user.app_metadata as
    | Record<string, unknown>
    | null
    | undefined;
  const userMeta = user.user_metadata as
    | Record<string, unknown>
    | null
    | undefined;
  const clinicCandidate = appMeta?.clinic_id ?? userMeta?.clinic_id ?? null;

  return typeof clinicCandidate === 'string' ? clinicCandidate : null;
};

const buildProfileFromUser = (user: User): UserProfile => {
  const role = resolveRole(user);
  const clinicId = resolveClinicId(user);

  return {
    id: user.id,
    email: user.email ?? null,
    role,
    clinicId,
    isActive: true,
    isAdmin: role ? ADMIN_ROLES.has(role as Role) : false,
  };
};

const decodeBase64Url = (value: string): string | null => {
  const normalized = value.replace(/-/g, '+').replace(/_/g, '/');
  const padding = normalized.length % 4;
  const padded = normalized + (padding ? '='.repeat(4 - padding) : '');

  try {
    return typeof atob === 'function' ? atob(padded) : null;
  } catch {
    return null;
  }
};

const getAuthCookieValue = (): string | null => {
  if (typeof document === 'undefined') {
    return null;
  }

  const rawCookies = document.cookie ? document.cookie.split(';') : [];
  const cookies = new Map<string, string>();

  for (const entry of rawCookies) {
    const trimmed = entry.trim();
    if (!trimmed) continue;
    const separatorIndex = trimmed.indexOf('=');
    if (separatorIndex < 0) continue;
    const name = decodeURIComponent(trimmed.slice(0, separatorIndex));
    const value = trimmed.slice(separatorIndex + 1);
    cookies.set(name, value);
  }

  const baseNames = new Set<string>();
  for (const name of cookies.keys()) {
    const baseName = name.split('.')[0];
    if (baseName.startsWith('sb-') && baseName.endsWith('-auth-token')) {
      baseNames.add(baseName);
    }
  }

  for (const baseName of baseNames) {
    const direct = cookies.get(baseName);
    if (direct) {
      return direct;
    }

    const prefix = `${baseName}.`;
    const chunks = Array.from(cookies.entries())
      .filter(([name]) => name.startsWith(prefix))
      .map(([name, value]) => ({
        index: Number(name.slice(prefix.length)),
        value,
      }))
      .filter(chunk => Number.isFinite(chunk.index))
      .sort((a, b) => a.index - b.index);

    if (chunks.length > 0) {
      return chunks.map(chunk => chunk.value).join('');
    }
  }

  return null;
};

const loadProfileFromCookie = (): UserProfile | null => {
  const rawCookie = getAuthCookieValue();
  if (!rawCookie) {
    return null;
  }

  const encoded = rawCookie.startsWith('base64-')
    ? rawCookie.slice('base64-'.length)
    : rawCookie;
  const decoded = decodeBase64Url(encoded);

  if (!decoded) {
    return null;
  }

  try {
    const sessionPayload = JSON.parse(decoded) as { user?: User };
    if (!sessionPayload?.user) {
      return null;
    }
    return buildProfileFromUser(sessionPayload.user);
  } catch {
    return null;
  }
};

export function useUserProfile(): ProfileState {
  const context = useOptionalUserProfileContext();
  const initialProfile = loadProfileFromCookie();
  const [profile, setProfile] = useState<UserProfile | null>(initialProfile);
  const [loading, setLoading] = useState(!initialProfile);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (context) {
      setProfile(context.profile ?? null);
      setLoading(context.loading);
      setError(context.error ?? null);
      return;
    }

    let isMounted = true;
    const abortController = new AbortController();
    const timeoutId = setTimeout(
      () => abortController.abort(),
      PROFILE_FETCH_TIMEOUT_MS
    );
    const hasInitialProfile = Boolean(profile);
    let hasSessionFallback = hasInitialProfile;

    const loadSessionProfile = async () => {
      let timeoutId: ReturnType<typeof setTimeout> | null = null;
      try {
        const sessionPromise = supabase.auth.getSession();
        const timeoutPromise = new Promise<null>(resolve => {
          timeoutId = setTimeout(() => resolve(null), SESSION_FETCH_TIMEOUT_MS);
        });
        const sessionResult = await Promise.race([
          sessionPromise,
          timeoutPromise,
        ]);

        if (!sessionResult) {
          return loadProfileFromCookie();
        }

        const { data, error: sessionError } = sessionResult;
        if (sessionError || !data.session?.user) {
          return loadProfileFromCookie();
        }
        return buildProfileFromUser(data.session.user);
      } catch (err) {
        console.error('useUserProfile session error', err);
        return loadProfileFromCookie();
      } finally {
        if (timeoutId) {
          clearTimeout(timeoutId);
        }
      }
    };

    const fetchProfile = async () => {
      try {
        if (!hasSessionFallback) {
          setLoading(true);
        }
        const res = await fetch('/api/auth/profile', {
          credentials: 'include',
          signal: abortController.signal,
        });

        if (!res.ok) {
          if (res.status === 401) {
            if (isMounted) {
              setProfile(null);
              setError('èªè¨¼ãŒå¿…è¦ã§ã™');
            }
            return;
          }

          const text = await res.text();
          throw new Error(text || 'ãƒ—ãƒ­ãƒ•ã‚£ãƒ¼ãƒ«å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ');
        }

        const json = await res.json();
        if (isMounted) {
          setProfile(json?.data ?? null);
          setError(null);
        }
      } catch (err) {
        if (err instanceof Error && err.name === 'AbortError') {
          if (isMounted && !hasSessionFallback) {
            setError('ãƒ—ãƒ­ãƒ•ã‚£ãƒ¼ãƒ«å–å¾—ãŒã‚¿ã‚¤ãƒ ã‚¢ã‚¦ãƒˆã—ã¾ã—ãŸ');
          }
          return;
        }

        console.error('useUserProfile error', err);
        if (isMounted) {
          setError(err instanceof Error ? err.message : String(err));
          if (!hasSessionFallback) {
            setProfile(null);
          }
        }
      } finally {
        if (isMounted && !hasSessionFallback) {
          setLoading(false);
        }
      }
    };

    const initializeProfile = async () => {
      if (!hasInitialProfile) {
        const sessionProfile = await loadSessionProfile();
        if (isMounted && sessionProfile) {
          setProfile(sessionProfile);
          setError(null);
          setLoading(false);
          hasSessionFallback = true;
        }
      }

      await fetchProfile();
      if (isMounted) {
        setLoading(false);
      }
    };

    initializeProfile();

    return () => {
      isMounted = false;
      clearTimeout(timeoutId);
      abortController.abort();
    };
  }, [context]);

  if (context) {
    return {
      profile: context.profile ?? null,
      loading: context.loading,
      error: context.error ?? null,
    };
  }

  return { profile, loading, error };
}

