import React, { useState, useMemo } from 'react';
import { Appointment } from '../types';
import { RESOURCES, COLORS } from '../constants';
import { ArrowRight, ChevronUp, ChevronDown, ArrowUpDown } from 'lucide-react';

interface Props {
  appointments: Appointment[];
  onSelect: (appointment: Appointment) => void;
}

type SortField = 'time' | 'resource' | 'customer' | 'status';
type SortDirection = 'asc' | 'desc';

export const AppointmentList: React.FC<Props> = ({
  appointments,
  onSelect,
}) => {
  const [sortField, setSortField] = useState<SortField>('time');
  const [sortDirection, setSortDirection] = useState<SortDirection>('asc');

  const getResourceName = (id: string) => {
    return RESOURCES.find(r => r.id === id)?.name || id;
  };

  const handleSort = (field: SortField) => {
    if (sortField === field) {
      setSortDirection(prev => (prev === 'asc' ? 'desc' : 'asc'));
    } else {
      setSortField(field);
      setSortDirection('asc'); // Reset to asc when changing field
    }
  };

  const sortedAppointments = useMemo(() => {
    return [...appointments].sort((a, b) => {
      let comparison = 0;

      switch (sortField) {
        case 'time':
          // Compare start time (minutes from midnight)
          const timeA = a.startHour * 60 + a.startMinute;
          const timeB = b.startHour * 60 + b.startMinute;
          comparison = timeA - timeB;
          break;
        case 'resource':
          const resA = getResourceName(a.resourceId);
          const resB = getResourceName(b.resourceId);
          comparison = resA.localeCompare(resB, 'ja');
          break;
        case 'customer':
          const nameA = a.lastName || a.title;
          const nameB = b.lastName || b.title;
          comparison = nameA.localeCompare(nameB, 'ja');
          break;
        case 'status':
          // Sort by type (normal < holiday < blocked) for example
          comparison = a.type.localeCompare(b.type);
          break;
      }

      return sortDirection === 'asc' ? comparison : -comparison;
    });
  }, [appointments, sortField, sortDirection]);

  const SortIcon = ({ field }: { field: SortField }) => {
    if (sortField !== field)
      return (
        <ArrowUpDown className='w-4 h-4 text-gray-300 opacity-0 group-hover:opacity-50 transition-opacity' />
      );
    return sortDirection === 'asc' ? (
      <ChevronUp className='w-4 h-4 text-sky-600' />
    ) : (
      <ChevronDown className='w-4 h-4 text-sky-600' />
    );
  };

  const renderHeader = (
    field: SortField,
    label: string,
    className: string = ''
  ) => (
    <th
      scope='col'
      onClick={() => handleSort(field)}
      className={`px-6 py-3 text-left text-xs font-bold text-gray-500 uppercase tracking-wider whitespace-nowrap cursor-pointer hover:bg-gray-100 transition-colors group select-none ${className}`}
    >
      <div className='flex items-center gap-1'>
        {label}
        <SortIcon field={field} />
      </div>
    </th>
  );

  return (
    <div className='p-4 sm:p-6 overflow-y-auto h-full'>
      <div className='bg-white shadow rounded-lg border border-gray-200 overflow-hidden'>
        <div className='overflow-x-auto'>
          <table className='min-w-full divide-y divide-gray-200'>
            <thead className='bg-gray-50 border-b border-gray-200'>
              <tr>
                {renderHeader('time', 'æ™‚é–“')}
                {renderHeader('resource', 'æ‹…å½“ã‚¹ã‚¿ãƒƒãƒ•')}
                {renderHeader('customer', 'é¡§å®¢åãƒ»å†…å®¹')}
                {renderHeader('status', 'çŠ¶æ…‹')}
                <th scope='col' className='relative px-6 py-3'>
                  <span className='sr-only'>è©³ç´°</span>
                </th>
              </tr>
            </thead>
            <tbody className='bg-white divide-y divide-gray-200'>
              {sortedAppointments.map(appt => (
                <tr
                  key={appt.id}
                  className='hover:bg-sky-50 cursor-pointer transition-colors group'
                  onClick={() => onSelect(appt)}
                >
                  <td className='px-6 py-4 whitespace-nowrap text-sm text-gray-900 font-mono'>
                    {String(appt.startHour).padStart(2, '0')}:
                    {String(appt.startMinute).padStart(2, '0')}
                    <span className='text-gray-400 mx-1'>-</span>
                    {String(appt.endHour).padStart(2, '0')}:
                    {String(appt.endMinute).padStart(2, '0')}
                  </td>
                  <td className='px-6 py-4 whitespace-nowrap text-sm text-gray-600 font-medium'>
                    {getResourceName(appt.resourceId)}
                  </td>
                  <td className='px-6 py-4 whitespace-nowrap text-sm font-bold text-gray-800'>
                    {appt.title}
                    {appt.subTitle && (
                      <span className='ml-2 font-normal text-xs text-gray-400'>
                        ({appt.subTitle})
                      </span>
                    )}
                  </td>
                  <td className='px-6 py-4 whitespace-nowrap text-sm text-gray-500'>
                    <span
                      className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-bold ${COLORS[appt.color].replace('text-white', 'text-gray-800').replace('bg-', 'bg-opacity-20 bg-')}`}
                    >
                      {appt.type === 'normal'
                        ? 'äºˆç´„'
                        : appt.type === 'holiday'
                          ? 'ä¼‘ã¿'
                          : 'ãƒ–ãƒ­ãƒƒã‚¯'}
                    </span>
                  </td>
                  <td className='px-6 py-4 whitespace-nowrap text-right text-sm font-medium'>
                    <ArrowRight className='w-5 h-5 text-gray-300 group-hover:text-sky-500 transition-colors' />
                  </td>
                </tr>
              ))}
              {sortedAppointments.length === 0 && (
                <tr>
                  <td
                    colSpan={5}
                    className='px-6 py-10 text-center text-gray-500'
                  >
                    è¡¨ç¤ºã™ã‚‹äºˆç´„ãƒ‡ãƒ¼ã‚¿ãŒã‚ã‚Šã¾ã›ã‚“ã€‚
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

// ã‚¢ã‚¯ã‚»ã‚·ãƒ“ãƒªãƒ†ã‚£è‡ªå‹•ãƒ†ã‚¹ãƒˆ
export interface AccessibilityIssue {
  element: Element;
  issue: string;
  severity: 'error' | 'warning' | 'info';
  wcag: string;
}

export class AccessibilityTester {
  private issues: AccessibilityIssue[] = [];

  testPage(): AccessibilityIssue[] {
    this.issues = [];

    if (typeof window === 'undefined') {
      return this.issues;
    }

    this.checkTouchTargets();
    this.checkColorContrast();
    this.checkAriaLabels();
    this.checkFocusManagement();
    this.checkHeadingStructure();
    this.checkFormLabels();

    return this.issues;
  }

  private addIssue(
    element: Element,
    issue: string,
    severity: AccessibilityIssue['severity'],
    wcag: string
  ) {
    this.issues.push({ element, issue, severity, wcag });
  }

  private checkTouchTargets() {
    const interactiveElements = document.querySelectorAll(
      'button, a, input, select, textarea, [role="button"], [role="link"], [tabindex]:not([tabindex="-1"])'
    );

    interactiveElements.forEach(element => {
      const rect = element.getBoundingClientRect();
      const minSize = 24; // WCAG 2.2 minimum

      if (rect.width < minSize || rect.height < minSize) {
        this.addIssue(
          element,
          `Touch target too small: ${rect.width.toFixed(1)}x${rect.height.toFixed(1)}px (minimum: ${minSize}px)`,
          'error',
          'WCAG 2.2 - 2.5.8'
        );
      }
    });
  }

  private checkColorContrast() {
    // åŸºæœ¬çš„ãªã‚³ãƒ³ãƒˆãƒ©ã‚¹ãƒˆãƒã‚§ãƒƒã‚¯ï¼ˆå®Œå…¨ãªå®Ÿè£…ã«ã¯è‰²åˆ†æãƒ©ã‚¤ãƒ–ãƒ©ãƒªãŒå¿…è¦ï¼‰
    const textElements = document.querySelectorAll(
      'p, span, h1, h2, h3, h4, h5, h6, button, a, label'
    );

    textElements.forEach(element => {
      const styles = window.getComputedStyle(element);
      const backgroundColor = styles.backgroundColor;
      const color = styles.color;

      // é€æ˜ã¾ãŸã¯ç¶™æ‰¿ã•ã‚ŒãŸè‰²ã¯ã‚¹ã‚­ãƒƒãƒ—
      if (
        backgroundColor === 'rgba(0, 0, 0, 0)' ||
        backgroundColor === 'transparent'
      ) {
        return;
      }

      // ç°¡æ˜“çš„ãªè­¦å‘Šï¼ˆè©³ç´°ãªè¨ˆç®—ã¯çœç•¥ï¼‰
      if (backgroundColor === color) {
        this.addIssue(
          element,
          'Text and background color are identical',
          'error',
          'WCAG 2.1 - 1.4.3'
        );
      }
    });
  }

  private checkAriaLabels() {
    // aria-label, aria-labelledby, aria-describedby ã®ãƒã‚§ãƒƒã‚¯
    const interactiveElements = document.querySelectorAll(
      'button, a, input, select, textarea, [role="button"], [role="link"]'
    );

    interactiveElements.forEach(element => {
      const hasAriaLabel = element.hasAttribute('aria-label');
      const hasAriaLabelledBy = element.hasAttribute('aria-labelledby');
      const hasTextContent = element.textContent?.trim();
      const hasAltText = element.hasAttribute('alt');
      const tagName = element.tagName.toLowerCase();

      if (
        !hasAriaLabel &&
        !hasAriaLabelledBy &&
        !hasTextContent &&
        !hasAltText
      ) {
        if (tagName === 'button' || tagName === 'a') {
          this.addIssue(
            element,
            `${tagName} element has no accessible name`,
            'error',
            'WCAG 2.1 - 4.1.2'
          );
        }
      }
    });

    // ç”»åƒã®altå±æ€§ãƒã‚§ãƒƒã‚¯
    const images = document.querySelectorAll('img');
    images.forEach(img => {
      if (!img.hasAttribute('alt')) {
        this.addIssue(
          img,
          'Image missing alt attribute',
          'error',
          'WCAG 2.1 - 1.1.1'
        );
      }
    });
  }

  private checkFocusManagement() {
    // ãƒ•ã‚©ãƒ¼ã‚«ã‚¹å¯èƒ½è¦ç´ ã®ãƒã‚§ãƒƒã‚¯
    const focusableElements = document.querySelectorAll(
      'button, a, input, select, textarea, [tabindex]:not([tabindex="-1"])'
    );

    focusableElements.forEach((element, index) => {
      const tabIndex = element.getAttribute('tabindex');

      // æ­£ã® tabindex ã¯é¿ã‘ã‚‹ã¹ã
      if (tabIndex && parseInt(tabIndex) > 0) {
        this.addIssue(
          element,
          'Positive tabindex found - can cause confusing tab order',
          'warning',
          'WCAG 2.1 - 2.4.3'
        );
      }

      // ãƒ•ã‚©ãƒ¼ã‚«ã‚¹è¡¨ç¤ºã®ç¢ºèªï¼ˆã‚¹ã‚¿ã‚¤ãƒ«ãƒ™ãƒ¼ã‚¹ï¼‰
      const styles = window.getComputedStyle(element);
      if (styles.outline === 'none' && !styles.boxShadow.includes('ring')) {
        this.addIssue(
          element,
          'Element may not have visible focus indicator',
          'warning',
          'WCAG 2.1 - 2.4.7'
        );
      }
    });
  }

  private checkHeadingStructure() {
    const headings = document.querySelectorAll('h1, h2, h3, h4, h5, h6');
    let previousLevel = 0;

    headings.forEach(heading => {
      const currentLevel = parseInt(heading.tagName.substring(1));

      if (currentLevel > previousLevel + 1) {
        this.addIssue(
          heading,
          `Heading level jumps from h${previousLevel} to h${currentLevel}`,
          'error',
          'WCAG 2.1 - 1.3.1'
        );
      }

      previousLevel = currentLevel;
    });

    // h1ãŒè¤‡æ•°ã¾ãŸã¯0å€‹ã®å ´åˆ
    const h1Count = document.querySelectorAll('h1').length;
    if (h1Count === 0) {
      this.addIssue(
        document.body,
        'Page should have exactly one h1 element',
        'warning',
        'WCAG 2.1 - 1.3.1'
      );
    } else if (h1Count > 1) {
      this.addIssue(
        document.body,
        'Page has multiple h1 elements',
        'warning',
        'WCAG 2.1 - 1.3.1'
      );
    }
  }

  private checkFormLabels() {
    const formControls = document.querySelectorAll('input, select, textarea');

    formControls.forEach(control => {
      const id = control.getAttribute('id');
      const hasLabel = id && document.querySelector(`label[for="${id}"]`);
      const hasAriaLabel = control.hasAttribute('aria-label');
      const hasAriaLabelledBy = control.hasAttribute('aria-labelledby');

      if (!hasLabel && !hasAriaLabel && !hasAriaLabelledBy) {
        this.addIssue(
          control,
          'Form control has no associated label',
          'error',
          'WCAG 2.1 - 1.3.1'
        );
      }
    });
  }

  generateReport(): string {
    const errorCount = this.issues.filter(i => i.severity === 'error').length;
    const warningCount = this.issues.filter(
      i => i.severity === 'warning'
    ).length;
    const infoCount = this.issues.filter(i => i.severity === 'info').length;

    let report = `ğŸ” Accessibility Test Report\n`;
    report += `Total Issues: ${this.issues.length}\n`;
    report += `Errors: ${errorCount}, Warnings: ${warningCount}, Info: ${infoCount}\n\n`;

    if (this.issues.length === 0) {
      report += 'âœ… No accessibility issues found!\n';
      return report;
    }

    const groupedIssues = this.issues.reduce(
      (groups, issue) => {
        if (!groups[issue.severity]) groups[issue.severity] = [];
        groups[issue.severity].push(issue);
        return groups;
      },
      {} as Record<string, AccessibilityIssue[]>
    );

    Object.entries(groupedIssues).forEach(([severity, issues]) => {
      report += `${severity.toUpperCase()} (${issues.length}):\n`;
      issues.forEach((issue, index) => {
        report += `  ${index + 1}. ${issue.issue} (${issue.wcag})\n`;
      });
      report += '\n';
    });

    return report;
  }
}

// é–‹ç™ºç”¨ã®ãƒ˜ãƒ«ãƒ‘ãƒ¼é–¢æ•°
export const runAccessibilityTest = () => {
  const tester = new AccessibilityTester();
  const issues = tester.testPage();
  const report = tester.generateReport();

  console.log(report);
  return { issues, report };
};

const REQUIRED_ENV_VARS = [
  'NEXT_PUBLIC_SUPABASE_URL',
  'NEXT_PUBLIC_SUPABASE_ANON_KEY',
  'SUPABASE_SERVICE_ROLE_KEY',
] as const;

const missing = REQUIRED_ENV_VARS.filter(name => {
  const value = process.env[name];
  return value === undefined || value.length === 0;
});

if (missing.length > 0 && process.env.NODE_ENV !== 'test') {
  throw new Error(
    `Missing required environment variables: ${missing.join(', ')}`
  );
}

export const env = {
  NEXT_PUBLIC_SUPABASE_URL: process.env.NEXT_PUBLIC_SUPABASE_URL ?? '',
  NEXT_PUBLIC_SUPABASE_ANON_KEY:
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY ?? '',
  SUPABASE_SERVICE_ROLE_KEY: process.env.SUPABASE_SERVICE_ROLE_KEY ?? '',
} as const;

export function assertEnv(name: keyof typeof env) {
  const value = env[name];
  if (!value) {
    throw new Error(`Environment variable ${name} is not set`);
  }
  return value;
}

/**
 * å¼·åŒ–ã•ã‚ŒãŸã‚¨ãƒ©ãƒ¼ãƒãƒ³ãƒ‰ãƒªãƒ³ã‚°ã‚·ã‚¹ãƒ†ãƒ 
 * ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ»ã‚»ãƒƒã‚·ãƒ§ãƒ³ç®¡ç†ç”¨ã®åŒ…æ‹¬çš„ã‚¨ãƒ©ãƒ¼å‡¦ç†
 */

import { SecurityMonitor } from '@/lib/security-monitor';
import { logger } from '@/lib/logger';

export interface ErrorContext {
  userId?: string;
  clinicId?: string;
  ipAddress?: string;
  userAgent?: string;
  requestPath?: string;
  sessionId?: string;
  timestamp: Date;
}

export interface SecurityError extends Error {
  code: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  context?: ErrorContext;
  shouldLogout?: boolean;
  shouldAlert?: boolean;
}

/**
 * ã‚¨ãƒ©ãƒ¼åˆ†é¡ã¨ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒªã‚¹ã‚¯è©•ä¾¡
 */
export class SecurityErrorHandler {
  private securityMonitor: SecurityMonitor;

  constructor() {
    this.securityMonitor = new SecurityMonitor();
  }

  /**
   * ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£é–¢é€£ã‚¨ãƒ©ãƒ¼ã®åŒ…æ‹¬çš„å‡¦ç†
   */
  async handleSecurityError(
    error: Error | SecurityError,
    context: ErrorContext
  ): Promise<{
    shouldTerminate: boolean;
    userMessage: string;
    logLevel: 'info' | 'warn' | 'error' | 'critical';
  }> {
    const errorData = await this.analyzeError(error, context);

    // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã‚¤ãƒ™ãƒ³ãƒˆã¨ã—ã¦è¨˜éŒ²
    await this.logSecurityEvent(error, context, errorData);

    return this.determineResponseAction(errorData);
  }

  /**
   * ã‚¨ãƒ©ãƒ¼åˆ†æã¨ãƒªã‚¹ã‚¯è©•ä¾¡
   */
  private async analyzeError(
    error: Error | SecurityError,
    context: ErrorContext
  ): Promise<{
    category: string;
    severity: 'low' | 'medium' | 'high' | 'critical';
    riskScore: number;
    shouldTerminate: boolean;
    isSecurityThreat: boolean;
  }> {
    const errorMessage = error.message.toLowerCase();
    const errorStack = error.stack || '';

    // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£é–¢é€£ã‚¨ãƒ©ãƒ¼ãƒ‘ã‚¿ãƒ¼ãƒ³ã®æ¤œå‡º
    const securityPatterns = [
      {
        pattern: /invalid.*(token|session)/i,
        category: 'authentication',
        severity: 'medium' as const,
      },
      {
        pattern: /unauthorized|forbidden/i,
        category: 'authorization',
        severity: 'high' as const,
      },
      {
        pattern: /sql.*injection/i,
        category: 'injection',
        severity: 'critical' as const,
      },
      {
        pattern: /xss|script.*injection/i,
        category: 'xss',
        severity: 'high' as const,
      },
      {
        pattern: /csrf|cross.*site/i,
        category: 'csrf',
        severity: 'high' as const,
      },
      {
        pattern: /brute.*force|too.*many.*attempts/i,
        category: 'brute_force',
        severity: 'high' as const,
      },
      {
        pattern: /session.*hijack/i,
        category: 'session_hijacking',
        severity: 'critical' as const,
      },
      {
        pattern: /rate.*limit/i,
        category: 'rate_limiting',
        severity: 'medium' as const,
      },
    ];

    const detectedPattern = securityPatterns.find(p =>
      p.pattern.test(errorMessage)
    );

    // ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆå€¤
    let category = 'general';
    let severity: 'low' | 'medium' | 'high' | 'critical' = 'low';
    let isSecurityThreat = false;

    if (detectedPattern) {
      category = detectedPattern.category;
      severity = detectedPattern.severity;
      isSecurityThreat = true;
    }

    // SecurityErrorã‚¤ãƒ³ã‚¿ãƒ¼faceå¯¾å¿œ
    if ('code' in error && 'severity' in error) {
      severity = error.severity;
      isSecurityThreat = true;
    }

    // ãƒªã‚¹ã‚¯ã‚¹ã‚³ã‚¢è¨ˆç®—
    const riskScore = this.calculateRiskScore(
      severity,
      context,
      isSecurityThreat
    );

    return {
      category,
      severity,
      riskScore,
      shouldTerminate: severity === 'critical' || riskScore > 80,
      isSecurityThreat,
    };
  }

  /**
   * ãƒªã‚¹ã‚¯ã‚¹ã‚³ã‚¢è¨ˆç®—ï¼ˆ0-100ï¼‰
   */
  private calculateRiskScore(
    severity: 'low' | 'medium' | 'high' | 'critical',
    context: ErrorContext,
    isSecurityThreat: boolean
  ): number {
    let score = 0;

    // åŸºæœ¬é‡è¦åº¦ã‚¹ã‚³ã‚¢
    const severityScores = {
      low: 20,
      medium: 40,
      high: 70,
      critical: 90,
    };
    score += severityScores[severity];

    // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£è„…å¨åŠ ç®—
    if (isSecurityThreat) {
      score += 20;
    }

    // ç®¡ç†è€…ãƒ«ãƒ¼ãƒˆã§ã®ã‚¨ãƒ©ãƒ¼åŠ ç®—
    if (context.requestPath?.startsWith('/admin')) {
      score += 15;
    }

    // æ™‚é–“å¸¯ã«ã‚ˆã‚‹èª¿æ•´ï¼ˆå–¶æ¥­æ™‚é–“å¤–ã®ã‚¢ã‚¯ãƒ†ã‚£ãƒ“ãƒ†ã‚£ï¼‰
    const hour = new Date().getHours();
    if (hour < 6 || hour > 22) {
      score += 10;
    }

    return Math.min(score, 100);
  }

  /**
   * ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã‚¤ãƒ™ãƒ³ãƒˆãƒ­ã‚°è¨˜éŒ²
   */
  private async logSecurityEvent(
    error: Error | SecurityError,
    context: ErrorContext,
    errorData: any
  ): Promise<void> {
    try {
      await this.securityMonitor.logSecurityEvent({
        event_type: 'system_error',
        event_category: 'security_violation',
        severity_level:
          errorData.severity === 'critical'
            ? 'critical'
            : errorData.severity === 'high'
              ? 'error'
              : 'warning',
        event_description: error.message,
        user_id: context.userId || 'anonymous',
        clinic_id: context.clinicId || 'unknown',
        ip_address: context.ipAddress || 'unknown',
        user_agent: context.userAgent || 'unknown',
        source_component: 'error_handler',
        event_data: {
          errorMessage: error.message,
          errorStack: error.stack?.substring(0, 1000),
          errorCategory: errorData.category,
          severity: errorData.severity,
          riskScore: errorData.riskScore,
          requestPath: context.requestPath,
          sessionId: context.sessionId,
          timestamp: context.timestamp.toISOString(),
          isSecurityThreat: errorData.isSecurityThreat,
        },
      });
    } catch (logError) {
      logger.error('ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã‚¤ãƒ™ãƒ³ãƒˆãƒ­ã‚°è¨˜éŒ²å¤±æ•—:', logError);
    }
  }

  /**
   * ãƒ¬ã‚¹ãƒãƒ³ã‚¹ã‚¢ã‚¯ã‚·ãƒ§ãƒ³æ±ºå®š
   */
  private determineResponseAction(errorData: any): {
    shouldTerminate: boolean;
    userMessage: string;
    logLevel: 'info' | 'warn' | 'error' | 'critical';
  } {
    const { severity, category, shouldTerminate } = errorData;

    // ã‚«ãƒ†ã‚´ãƒªåˆ¥ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸
    const categoryMessages = {
      authentication:
        'ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®èªè¨¼ã«å•é¡ŒãŒç™ºç”Ÿã—ã¾ã—ãŸã€‚å†ãƒ­ã‚°ã‚¤ãƒ³ã—ã¦ãã ã•ã„ã€‚',
      authorization: 'æ¨©é™ãŒä¸è¶³ã—ã¦ã„ã‚‹ã‹ã€ã‚¢ã‚¯ã‚»ã‚¹æ¨©é™ã«å•é¡ŒãŒã‚ã‚Šã¾ã™ã€‚',
      injection:
        'ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ä¸Šã®å•é¡ŒãŒæ¤œå‡ºã•ã‚Œã¾ã—ãŸã€‚ç®¡ç†è€…ã«é€£çµ¡ã—ã¦ãã ã•ã„ã€‚',
      xss: 'ã‚¹ã‚¯ãƒªãƒ—ãƒˆã®å®Ÿè¡ŒãŒæ¤œå‡ºã•ã‚Œã¾ã—ãŸã€‚ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã®ãŸã‚ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’çµ‚äº†ã—ã¾ã™ã€‚',
      csrf: 'ãƒªã‚¯ã‚¨ã‚¹ãƒˆã®æ¤œè¨¼ã«å¤±æ•—ã—ã¾ã—ãŸã€‚ãƒšãƒ¼ã‚¸ã‚’å†èª­ã¿è¾¼ã¿ã—ã¦ãã ã•ã„ã€‚',
      brute_force:
        'ä¸æ­£ãªã‚¢ã‚¯ã‚»ã‚¹è©¦è¡ŒãŒæ¤œå‡ºã•ã‚Œã¾ã—ãŸã€‚ä¸€æ™‚çš„ã«ã‚¢ã‚¯ã‚»ã‚¹ã‚’åˆ¶é™ã—ã¾ã™ã€‚',
      session_hijacking:
        'ã‚»ãƒƒã‚·ãƒ§ãƒ³ã«ç•°å¸¸ãŒæ¤œå‡ºã•ã‚Œã¾ã—ãŸã€‚ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã®ãŸã‚å¼·åˆ¶ãƒ­ã‚°ã‚¢ã‚¦ãƒˆã—ã¾ã™ã€‚',
      rate_limiting:
        'ã‚¢ã‚¯ã‚»ã‚¹é »åº¦ãŒé«˜ã™ãã¾ã™ã€‚ã—ã°ã‚‰ãå¾…ã£ã¦ã‹ã‚‰å†è©¦è¡Œã—ã¦ãã ã•ã„ã€‚',
      general:
        'ã‚·ã‚¹ãƒ†ãƒ ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸã€‚ã—ã°ã‚‰ãå¾…ã£ã¦ã‹ã‚‰å†è©¦è¡Œã—ã¦ãã ã•ã„ã€‚',
    };

    const userMessage =
      categoryMessages[category as keyof typeof categoryMessages] ||
      categoryMessages.general;

    // ãƒ­ã‚°ãƒ¬ãƒ™ãƒ«æ±ºå®š
    let logLevel: 'info' | 'warn' | 'error' | 'critical' = 'error';
    if (severity === 'critical') {
      logLevel = 'critical';
    } else if (severity === 'high') {
      logLevel = 'error';
    } else if (severity === 'medium') {
      logLevel = 'warn';
    } else {
      logLevel = 'info';
    }

    return {
      shouldTerminate,
      userMessage,
      logLevel,
    };
  }

  /**
   * é–‹ç™ºç’°å¢ƒç”¨ã®è©³ç´°ã‚¨ãƒ©ãƒ¼è¡¨ç¤º
   */
  getDevelopmentErrorDetails(
    error: Error,
    context: ErrorContext
  ): {
    error: string;
    stack: string;
    context: ErrorContext;
    suggestions: string[];
  } {
    const suggestions = this.generateDebuggingSuggestions(error);

    return {
      error: error.message,
      stack: error.stack || 'No stack trace available',
      context,
      suggestions,
    };
  }

  /**
   * ãƒ‡ãƒãƒƒã‚°ç”¨ã®æ”¹å–„ææ¡ˆç”Ÿæˆ
   */
  private generateDebuggingSuggestions(error: Error): string[] {
    const suggestions: string[] = [];
    const message = error.message.toLowerCase();

    if (message.includes('database') || message.includes('supabase')) {
      suggestions.push('ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æ¥ç¶šã‚’ç¢ºèªã—ã¦ãã ã•ã„');
      suggestions.push(
        'Supabaseç’°å¢ƒå¤‰æ•°ãŒæ­£ã—ãè¨­å®šã•ã‚Œã¦ã„ã‚‹ã‹ç¢ºèªã—ã¦ãã ã•ã„'
      );
    }

    if (message.includes('session') || message.includes('token')) {
      suggestions.push('ã‚»ãƒƒã‚·ãƒ§ãƒ³ç®¡ç†ã‚·ã‚¹ãƒ†ãƒ ã®ãƒ­ã‚°ã‚’ç¢ºèªã—ã¦ãã ã•ã„');
      suggestions.push(
        'ã‚«ã‚¹ã‚¿ãƒ ã‚»ãƒƒã‚·ãƒ§ãƒ³ãƒ†ãƒ¼ãƒ–ãƒ«ãŒæ­£ã—ãä½œæˆã•ã‚Œã¦ã„ã‚‹ã‹ç¢ºèªã—ã¦ãã ã•ã„'
      );
    }

    if (message.includes('permission') || message.includes('unauthorized')) {
      suggestions.push('RLSï¼ˆRow Level Securityï¼‰è¨­å®šã‚’ç¢ºèªã—ã¦ãã ã•ã„');
      suggestions.push('ãƒ¦ãƒ¼ã‚¶ãƒ¼ã®ãƒ­ãƒ¼ãƒ«ã¨æ¨©é™ã‚’ç¢ºèªã—ã¦ãã ã•ã„');
    }

    if (suggestions.length === 0) {
      suggestions.push('ãƒ­ã‚°ãƒ•ã‚¡ã‚¤ãƒ«ã§è©³ç´°ãªã‚¨ãƒ©ãƒ¼æƒ…å ±ã‚’ç¢ºèªã—ã¦ãã ã•ã„');
      suggestions.push(
        'é–‹ç™ºãƒ„ãƒ¼ãƒ«ã®ãƒãƒƒãƒˆãƒ¯ãƒ¼ã‚¯ã‚¿ãƒ–ã§APIãƒ¬ã‚¹ãƒãƒ³ã‚¹ã‚’ç¢ºèªã—ã¦ãã ã•ã„'
      );
    }

    return suggestions;
  }
}

/**
 * ã‚°ãƒ­ãƒ¼ãƒãƒ«ã‚¨ãƒ©ãƒ¼ãƒãƒ³ãƒ‰ãƒ©ãƒ¼
 * ã‚¢ãƒ—ãƒªã‚±ãƒ¼ã‚·ãƒ§ãƒ³å…¨ä½“ã®ã‚¨ãƒ©ãƒ¼ã‚’ä¸€å…ƒç®¡ç†
 */
export class GlobalErrorHandler {
  private static instance: GlobalErrorHandler;
  private securityErrorHandler: SecurityErrorHandler;

  private constructor() {
    this.securityErrorHandler = new SecurityErrorHandler();
  }

  static getInstance(): GlobalErrorHandler {
    if (!GlobalErrorHandler.instance) {
      GlobalErrorHandler.instance = new GlobalErrorHandler();
    }
    return GlobalErrorHandler.instance;
  }

  /**
   * Next.jsã‚¢ãƒ—ãƒªã‚±ãƒ¼ã‚·ãƒ§ãƒ³ç”¨ã®ã‚¨ãƒ©ãƒ¼ãƒãƒ³ãƒ‰ãƒ©ãƒ¼è¨­å®š
   */
  setupGlobalHandlers(): void {
    // æœªå‡¦ç†ã®Promiseæ‹’å¦ã‚’ã‚­ãƒ£ãƒƒãƒ
    process.on('unhandledRejection', (reason, promise) => {
      console.error('Unhandled Rejection at:', promise, 'reason:', reason);

      if (reason instanceof Error) {
        this.handleError(reason, {
          timestamp: new Date(),
          requestPath: 'unhandled_rejection',
        });
      }
    });

    // æœªå‡¦ç†ã®ä¾‹å¤–ã‚’ã‚­ãƒ£ãƒƒãƒ
    process.on('uncaughtException', error => {
      console.error('Uncaught Exception:', error);

      this.handleError(error, {
        timestamp: new Date(),
        requestPath: 'uncaught_exception',
      });
    });
  }

  /**
   * ã‚¨ãƒ©ãƒ¼å‡¦ç†ã®ã‚¨ãƒ³ãƒˆãƒªãƒ¼ãƒã‚¤ãƒ³ãƒˆ
   */
  async handleError(error: Error, context: ErrorContext): Promise<void> {
    try {
      const result = await this.securityErrorHandler.handleSecurityError(
        error,
        context
      );

      // ãƒ­ã‚°ãƒ¬ãƒ™ãƒ«ã«å¿œã˜ãŸãƒ­ã‚°å‡ºåŠ›
      switch (result.logLevel) {
        case 'critical':
          console.error('ğŸš¨ CRITICAL ERROR:', error.message, context);
          break;
        case 'error':
          console.error('âŒ ERROR:', error.message, context);
          break;
        case 'warn':
          console.warn('âš ï¸ WARNING:', error.message, context);
          break;
        case 'info':
          console.info('â„¹ï¸ INFO:', error.message, context);
          break;
      }

      // é‡è¦ãªã‚¨ãƒ©ãƒ¼ã®å ´åˆã¯ã‚¢ãƒ©ãƒ¼ãƒˆé€šçŸ¥ï¼ˆå°†æ¥çš„ã«ï¼‰
      if (result.logLevel === 'critical') {
        // TODO: Slack, Email, SMSç­‰ã§ã®ã‚¢ãƒ©ãƒ¼ãƒˆé€šçŸ¥
        console.log('ğŸ“§ Critical error alert would be sent here');
      }
    } catch (handlingError) {
      console.error('Error in error handler:', handlingError);
    }
  }
}

/**
 * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ç”Ÿæˆãƒ»ç®¡ç†ã‚·ã‚¹ãƒ†ãƒ 
 * Phase 3B: MFAèªè¨¼ã®ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯æ©Ÿèƒ½
 */

import { createClient } from '@/lib/supabase';
import { z } from 'zod';

// ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰è¨­å®š
export const BACKUP_CODE_CONFIG = {
  COUNT: 10, // ç”Ÿæˆã™ã‚‹ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã®æ•°
  LENGTH: 8, // å„ã‚³ãƒ¼ãƒ‰ã®é•·ã•
  CHARSET: 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789', // ä½¿ç”¨å¯èƒ½æ–‡å­—
  MIN_REMAINING_WARNING: 3, // æ®‹ã‚Šã‚³ãƒ¼ãƒ‰æ•°ã®è­¦å‘Šé–¾å€¤
} as const;

// ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã‚¹ã‚­ãƒ¼ãƒ
const BackupCodeSchema = z.object({
  code: z
    .string()
    .length(
      BACKUP_CODE_CONFIG.LENGTH,
      `ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã¯${BACKUP_CODE_CONFIG.LENGTH}æ¡ã§ã‚ã‚‹å¿…è¦ãŒã‚ã‚Šã¾ã™`
    ),
  isUsed: z.boolean().default(false),
  usedAt: z.date().optional(),
});

const BackupCodeSetSchema = z.object({
  userId: z.string().min(1, 'ãƒ¦ãƒ¼ã‚¶ãƒ¼IDãŒå¿…è¦ã§ã™'),
  clinicId: z.string().min(1, 'ã‚¯ãƒªãƒ‹ãƒƒã‚¯IDãŒå¿…è¦ã§ã™'),
  codes: z.array(BackupCodeSchema).length(BACKUP_CODE_CONFIG.COUNT),
  generatedAt: z.date(),
});

export type BackupCode = z.infer<typeof BackupCodeSchema>;
export type BackupCodeSet = z.infer<typeof BackupCodeSetSchema>;

export interface BackupCodeUsage {
  totalGenerated: number;
  totalUsed: number;
  remainingCount: number;
  lastUsed?: Date;
  generatedAt: Date;
  warningLevel: 'none' | 'low' | 'critical';
}

/**
 * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ç®¡ç†ã‚¯ãƒ©ã‚¹
 * é«˜åº¦ãªã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã‚’æŒã¤ãƒ¯ãƒ³ã‚¿ã‚¤ãƒ ã‚³ãƒ¼ãƒ‰ç”Ÿæˆãƒ»æ¤œè¨¼ãƒ»ç®¡ç†
 */
export class BackupCodeManager {
  private supabase;

  constructor() {
    this.supabase = createClient();
  }

  /**
   * æ–°ã—ã„ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã‚»ãƒƒãƒˆç”Ÿæˆ
   * æš—å·å­¦çš„ã«å®‰å…¨ãªä¹±æ•°ã‚’ä½¿ç”¨ã—ãŸé«˜å“è³ªãªã‚³ãƒ¼ãƒ‰ç”Ÿæˆ
   */
  generateBackupCodes(): string[] {
    const codes: string[] = [];
    const charset = BACKUP_CODE_CONFIG.CHARSET;

    // è¡çªå›é¿ã®ãŸã‚ã®ã‚»ãƒƒãƒˆ
    const generatedCodes = new Set<string>();

    while (codes.length < BACKUP_CODE_CONFIG.COUNT) {
      let code = '';

      // å„æ–‡å­—ã‚’æš—å·å­¦çš„ã«å®‰å…¨ãªä¹±æ•°ã§é¸æŠ
      for (let i = 0; i < BACKUP_CODE_CONFIG.LENGTH; i++) {
        const randomIndex = this.getSecureRandomInt(0, charset.length - 1);
        code += charset.charAt(randomIndex);
      }

      // é‡è¤‡ãƒã‚§ãƒƒã‚¯
      if (!generatedCodes.has(code)) {
        generatedCodes.add(code);
        codes.push(code);
      }
    }

    return codes;
  }

  /**
   * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã®æ¤œè¨¼ã¨ãƒãƒ¼ã‚¯
   * ä½¿ç”¨æ¸ˆã¿ã‚³ãƒ¼ãƒ‰ã¯è‡ªå‹•çš„ã«ç„¡åŠ¹åŒ–ã•ã‚Œã‚‹
   */
  async verifyAndMarkBackupCode(
    userId: string,
    inputCode: string
  ): Promise<{
    isValid: boolean;
    remainingCodes: number;
    warningLevel: 'none' | 'low' | 'critical';
  }> {
    try {
      // å…¥åŠ›ã‚³ãƒ¼ãƒ‰ã®æ­£è¦åŒ–
      const normalizedCode = inputCode.toUpperCase().trim();

      if (normalizedCode.length !== BACKUP_CODE_CONFIG.LENGTH) {
        return {
          isValid: false,
          remainingCodes: 0,
          warningLevel: 'none',
        };
      }

      // ç¾åœ¨ã®MFAè¨­å®šå–å¾—
      const supabase = await this.supabase;
      const { data: mfaSettings, error } = await supabase
        .from('user_mfa_settings')
        .select('*')
        .eq('user_id', userId)
        .eq('is_enabled', true)
        .single();

      if (error || !mfaSettings) {
        throw new Error('MFAè¨­å®šãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
      }

      const backupCodes = (mfaSettings.backup_codes as string[]) || [];
      const codeIndex = backupCodes.indexOf(normalizedCode);

      if (codeIndex === -1) {
        // ç„¡åŠ¹ãªã‚³ãƒ¼ãƒ‰ã®è©¦è¡Œã‚’ãƒ­ã‚°è¨˜éŒ²
        await this.logBackupCodeEvent(userId, 'invalid_attempt', {
          code: normalizedCode.slice(0, 2) + '****',
          remainingCodes: backupCodes.length,
        });

        return {
          isValid: false,
          remainingCodes: backupCodes.length,
          warningLevel: this.getWarningLevel(backupCodes.length),
        };
      }

      // ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã‚’ä½¿ç”¨æ¸ˆã¿ã¨ã—ã¦ãƒãƒ¼ã‚¯ï¼ˆå‰Šé™¤ï¼‰
      const updatedBackupCodes = [...backupCodes];
      updatedBackupCodes.splice(codeIndex, 1);

      // ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æ›´æ–°
      const supabase2 = await this.supabase;
      await supabase2
        .from('user_mfa_settings')
        .update({
          backup_codes: updatedBackupCodes,
          last_used_at: new Date().toISOString(),
        })
        .eq('user_id', userId);

      // ä½¿ç”¨ãƒ­ã‚°è¨˜éŒ²
      await this.logBackupCodeEvent(userId, 'code_used', {
        code: normalizedCode.slice(0, 2) + '****',
        remainingCodes: updatedBackupCodes.length,
      });

      const warningLevel = this.getWarningLevel(updatedBackupCodes.length);

      // æ®‹ã‚Šã‚³ãƒ¼ãƒ‰ãŒå°‘ãªã„å ´åˆã®è­¦å‘Š
      if (warningLevel !== 'none') {
        await this.logBackupCodeEvent(userId, 'low_codes_warning', {
          remainingCodes: updatedBackupCodes.length,
          warningLevel,
        });
      }

      return {
        isValid: true,
        remainingCodes: updatedBackupCodes.length,
        warningLevel,
      };
    } catch (error) {
      throw new Error(
        `ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰æ¤œè¨¼ã‚¨ãƒ©ãƒ¼: ${error instanceof Error ? error.message : error}`
      );
    }
  }

  /**
   * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ä½¿ç”¨çŠ¶æ³å–å¾—
   */
  async getBackupCodeUsage(userId: string): Promise<BackupCodeUsage> {
    try {
      const supabase = await this.supabase;
      const { data: mfaSettings, error } = await supabase
        .from('user_mfa_settings')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (error || !mfaSettings) {
        throw new Error('MFAè¨­å®šãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
      }

      const backupCodes = (mfaSettings.backup_codes as string[]) || [];
      const remainingCount = backupCodes.length;
      const totalUsed = BACKUP_CODE_CONFIG.COUNT - remainingCount;

      return {
        totalGenerated: BACKUP_CODE_CONFIG.COUNT,
        totalUsed,
        remainingCount,
        lastUsed: mfaSettings.last_used_at
          ? new Date(mfaSettings.last_used_at)
          : undefined,
        generatedAt: new Date(
          mfaSettings.setup_completed_at || mfaSettings.created_at
        ),
        warningLevel: this.getWarningLevel(remainingCount),
      };
    } catch (error) {
      throw new Error(
        `ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ä½¿ç”¨çŠ¶æ³å–å¾—ã‚¨ãƒ©ãƒ¼: ${error instanceof Error ? error.message : error}`
      );
    }
  }

  /**
   * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰å†ç”Ÿæˆ
   * æ—¢å­˜ã®ã‚³ãƒ¼ãƒ‰ã‚’å…¨ã¦ç„¡åŠ¹åŒ–ã—ã¦æ–°ã—ã„ã‚»ãƒƒãƒˆã‚’ç”Ÿæˆ
   */
  async regenerateBackupCodes(
    userId: string,
    adminUserId?: string
  ): Promise<string[]> {
    try {
      // æ–°ã—ã„ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ç”Ÿæˆ
      const newBackupCodes = this.generateBackupCodes();

      // ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æ›´æ–°
      const supabase = await this.supabase;
      const { error } = await supabase
        .from('user_mfa_settings')
        .update({
          backup_codes: newBackupCodes,
          backup_codes_regenerated_at: new Date().toISOString(),
        })
        .eq('user_id', userId)
        .eq('is_enabled', true);

      if (error) {
        throw new Error(`ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ã‚¨ãƒ©ãƒ¼: ${error.message}`);
      }

      // å†ç”Ÿæˆãƒ­ã‚°è¨˜éŒ²
      await this.logBackupCodeEvent(userId, 'codes_regenerated', {
        newCodeCount: newBackupCodes.length,
        regeneratedBy: adminUserId || userId,
        isAdminAction: !!adminUserId,
      });

      return newBackupCodes;
    } catch (error) {
      throw new Error(
        `ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰å†ç”Ÿæˆã‚¨ãƒ©ãƒ¼: ${error instanceof Error ? error.message : error}`
      );
    }
  }

  /**
   * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰çµ±è¨ˆå–å¾—ï¼ˆç®¡ç†è€…ç”¨ï¼‰
   */
  async getBackupCodeStatistics(clinicId: string): Promise<{
    totalUsersWithMFA: number;
    usersWithBackupCodes: number;
    usersWithLowCodes: number;
    averageCodesRemaining: number;
    recentUsageCount: number;
  }> {
    try {
      // MFAæœ‰åŠ¹ãƒ¦ãƒ¼ã‚¶ãƒ¼çµ±è¨ˆ
      const supabase = await this.supabase;
      const { data: mfaUsers, error } = await supabase
        .from('user_mfa_settings')
        .select('backup_codes, last_used_at')
        .eq('clinic_id', clinicId)
        .eq('is_enabled', true);

      if (error) {
        throw new Error(`çµ±è¨ˆå–å¾—ã‚¨ãƒ©ãƒ¼: ${error.message}`);
      }

      const totalUsersWithMFA = mfaUsers.length;
      let usersWithBackupCodes = 0;
      let usersWithLowCodes = 0;
      let totalCodesRemaining = 0;

      for (const user of mfaUsers) {
        const codes = (user.backup_codes as string[]) || [];
        const remainingCount = codes.length;

        if (remainingCount > 0) {
          usersWithBackupCodes++;
          totalCodesRemaining += remainingCount;

          if (remainingCount <= BACKUP_CODE_CONFIG.MIN_REMAINING_WARNING) {
            usersWithLowCodes++;
          }
        }
      }

      const averageCodesRemaining =
        usersWithBackupCodes > 0
          ? Math.round((totalCodesRemaining / usersWithBackupCodes) * 100) / 100
          : 0;

      // æœ€è¿‘ã®ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ä½¿ç”¨å›æ•°ï¼ˆç›´è¿‘7æ—¥é–“ï¼‰
      const weekAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);
      const supabase2 = await this.supabase;
      const { count: recentUsageCount } = await supabase2
        .from('security_events')
        .select('*', { count: 'exact', head: true })
        .eq('event_type', 'mfa_backup_code_success')
        .gte('created_at', weekAgo.toISOString())
        .in(
          'user_id',
          mfaUsers.map(u => u.user_id)
        );

      return {
        totalUsersWithMFA,
        usersWithBackupCodes,
        usersWithLowCodes,
        averageCodesRemaining,
        recentUsageCount: recentUsageCount || 0,
      };
    } catch (error) {
      throw new Error(
        `ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰çµ±è¨ˆå–å¾—ã‚¨ãƒ©ãƒ¼: ${error instanceof Error ? error.message : error}`
      );
    }
  }

  /**
   * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã®ãƒ•ã‚©ãƒ¼ãƒãƒƒãƒˆï¼ˆè¡¨ç¤ºç”¨ï¼‰
   * å¯èª­æ€§ã‚’å‘ä¸Šã•ã›ã‚‹ãŸã‚ã«ãƒã‚¤ãƒ•ãƒ³ã§åŒºåˆ‡ã‚‹
   */
  formatBackupCodeForDisplay(code: string): string {
    if (code.length !== BACKUP_CODE_CONFIG.LENGTH) {
      return code;
    }

    // 4æ–‡å­—ãšã¤ãƒã‚¤ãƒ•ãƒ³ã§åŒºåˆ‡ã‚‹
    return code.match(/.{1,4}/g)?.join('-') || code;
  }

  /**
   * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã®æ¤œè¨¼ï¼ˆãƒ•ã‚©ãƒ¼ãƒãƒƒãƒˆæ¸ˆã¿å…¥åŠ›å¯¾å¿œï¼‰
   */
  normalizeBackupCodeInput(input: string): string {
    // ãƒã‚¤ãƒ•ãƒ³ã€ã‚¹ãƒšãƒ¼ã‚¹ã€å°æ–‡å­—ã‚’æ­£è¦åŒ–
    return input.toUpperCase().replace(/[-\s]/g, '').trim();
  }

  /**
   * æš—å·å­¦çš„ã«å®‰å…¨ãªä¹±æ•°ç”Ÿæˆ
   */
  private getSecureRandomInt(min: number, max: number): number {
    const range = max - min + 1;
    const bytesNeeded = Math.ceil(Math.log2(range) / 8);
    const maxValidValue = Math.floor(256 ** bytesNeeded / range) * range - 1;

    let randomValue: number;
    do {
      const randomBytes = new Uint8Array(bytesNeeded);

      // Node.jsç’°å¢ƒã¨ãƒ–ãƒ©ã‚¦ã‚¶ç’°å¢ƒä¸¡å¯¾å¿œ
      if (
        typeof globalThis !== 'undefined' &&
        globalThis.crypto &&
        globalThis.crypto.getRandomValues
      ) {
        globalThis.crypto.getRandomValues(randomBytes);
      } else {
        // ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯ï¼ˆéæ¨å¥¨ï¼‰
        for (let i = 0; i < bytesNeeded; i++) {
          randomBytes[i] = Math.floor(Math.random() * 256);
        }
      }

      randomValue = randomBytes.reduce((acc, byte, index) => {
        return acc + byte * 256 ** index;
      }, 0);
    } while (randomValue > maxValidValue);

    return min + (randomValue % range);
  }

  /**
   * è­¦å‘Šãƒ¬ãƒ™ãƒ«åˆ¤å®š
   */
  private getWarningLevel(remainingCodes: number): 'none' | 'low' | 'critical' {
    if (remainingCodes === 0) {
      return 'critical';
    } else if (remainingCodes <= BACKUP_CODE_CONFIG.MIN_REMAINING_WARNING) {
      return 'low';
    } else {
      return 'none';
    }
  }

  /**
   * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã‚¤ãƒ™ãƒ³ãƒˆãƒ­ã‚°è¨˜éŒ²
   */
  private async logBackupCodeEvent(
    userId: string,
    eventType: string,
    details: Record<string, unknown>
  ): Promise<void> {
    try {
      const supabase = await this.supabase;
      await supabase.from('security_events').insert({
        event_type: `mfa_backup_${eventType}`,
        user_id: userId,
        event_details: details,
        ip_address: '', // ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ã§è¨­å®šã•ã‚Œã‚‹
        user_agent: '', // ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ã§è¨­å®šã•ã‚Œã‚‹
        created_at: new Date().toISOString(),
      });
    } catch (error) {
      // ãƒ­ã‚°è¨˜éŒ²ã‚¨ãƒ©ãƒ¼ã¯ä¸»æ©Ÿèƒ½ã‚’å¦¨ã’ãªã„
      const { logger } = await import('@/lib/logger');
      logger.error('ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã‚¤ãƒ™ãƒ³ãƒˆãƒ­ã‚°è¨˜éŒ²ã‚¨ãƒ©ãƒ¼:', error);
    }
  }
}

