/**
 * MFAç®¡ç†ã‚¯ãƒ©ã‚¹
 * TOTPï¼ˆTime-based One-Time Passwordï¼‰èªè¨¼ã®å®Œå…¨å®Ÿè£…
 */
export class MFAManager {
  private supabase;

  constructor() {
    this.supabase = createClient();
  }

  /**
   * MFAã‚»ãƒƒãƒˆã‚¢ãƒƒãƒ—é–‹å§‹
   * ç§˜å¯†éµã¨QRã‚³ãƒ¼ãƒ‰ã€ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã‚’ç”Ÿæˆ
   */
  async initiateMFASetup(
    userId: string,
    clinicId: string
  ): Promise<MFASetupResult> {
    try {
      // å…¥åŠ›å€¤æ¤œè¨¼
      const validatedData = MFAConfigSchema.parse({ userId, clinicId });

      // æ—¢å­˜ã®MFAè¨­å®šç¢ºèª
      const existingMFA = await this.getMFAStatus(userId);
      if (existingMFA.isEnabled) {
        throw new Error('MFAã¯æ—¢ã«æœ‰åŠ¹åŒ–ã•ã‚Œã¦ã„ã¾ã™');
      }

      // ç§˜å¯†éµç”Ÿæˆï¼ˆRFC 4648 Base32ã‚¨ãƒ³ã‚³ãƒ¼ãƒ‰ï¼‰
      const secret = speakeasy.generateSecret({
        name: `æ•´éª¨é™¢ç®¡ç†SaaS (${userId})`,
        issuer: 'æ•´éª¨é™¢ç®¡ç†SaaS',
        length: 32, // 256ãƒ“ãƒƒãƒˆå¼·åº¦
      });

      if (!secret.base32) {
        throw new Error('ç§˜å¯†éµã®ç”Ÿæˆã«å¤±æ•—ã—ã¾ã—ãŸ');
      }

      // QRã‚³ãƒ¼ãƒ‰ç”Ÿæˆ
      const qrCodeUrl = await this.generateQRCode(secret.otpauth_url || '');

      // ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ç”Ÿæˆï¼ˆ10å€‹ï¼‰
      const backupCodes = this.generateBackupCodes();

      // ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ã«ä¸€æ™‚ä¿å­˜ï¼ˆã‚»ãƒƒãƒˆã‚¢ãƒƒãƒ—å®Œäº†ã¾ã§ï¼‰
      const supabase = await this.supabase;
      await supabase.from('mfa_setup_sessions').insert({
        user_id: validatedData.userId,
        clinic_id: validatedData.clinicId,
        secret_key: secret.base32,
        backup_codes: backupCodes,
        expires_at: new Date(Date.now() + 15 * 60 * 1000).toISOString(), // 15åˆ†æœ‰åŠ¹
        created_at: new Date().toISOString(),
      });

      return {
        secretKey: secret.base32,
        qrCodeUrl,
        backupCodes,
        manualEntryKey: this.formatSecretForManualEntry(secret.base32),
      };
    } catch (error) {
      throw new Error(
        `MFAã‚»ãƒƒãƒˆã‚¢ãƒƒãƒ—é–‹å§‹ã‚¨ãƒ©ãƒ¼: ${error instanceof Error ? error.message : error}`
      );
    }
  }

  /**
   * MFAã‚»ãƒƒãƒˆã‚¢ãƒƒãƒ—å®Œäº†
   * ãƒ¦ãƒ¼ã‚¶ãƒ¼ãŒTOTPãƒˆãƒ¼ã‚¯ãƒ³ã‚’å…¥åŠ›ã—ã¦ã‚»ãƒƒãƒˆã‚¢ãƒƒãƒ—ã‚’ç¢ºèª
   */
  async completeMFASetup(userId: string, token: string): Promise<boolean> {
    try {
      // å…¥åŠ›å€¤æ¤œè¨¼
      const validatedVerification = MFAVerificationSchema.parse({
        userId,
        token,
      });

      // ã‚»ãƒƒãƒˆã‚¢ãƒƒãƒ—ã‚»ãƒƒã‚·ãƒ§ãƒ³å–å¾—
      const supabase = await this.supabase;
      const { data: setupSession, error } = await supabase
        .from('mfa_setup_sessions')
        .select('*')
        .eq('user_id', validatedVerification.userId)
        .gte('expires_at', new Date().toISOString())
        .order('created_at', { ascending: false })
        .limit(1)
        .single();

      if (error || !setupSession) {
        throw new Error(
          'MFAã‚»ãƒƒãƒˆã‚¢ãƒƒãƒ—ã‚»ãƒƒã‚·ãƒ§ãƒ³ãŒè¦‹ã¤ã‹ã‚‰ãªã„ã‹æœŸé™åˆ‡ã‚Œã§ã™'
        );
      }

      // TOTPãƒˆãƒ¼ã‚¯ãƒ³æ¤œè¨¼
      const isValidToken = speakeasy.totp.verify({
        secret: setupSession.secret_key,
        encoding: 'base32',
        token: validatedVerification.token,
        window: 2, // ã‚»ãƒƒãƒˆã‚¢ãƒƒãƒ—æ™‚ã¯å°‘ã—å¯›å®¹ã«
      });

      if (!isValidToken) {
        throw new Error('ç„¡åŠ¹ãªTOTPãƒˆãƒ¼ã‚¯ãƒ³ã§ã™');
      }

      // MFAè¨­å®šã‚’æ­£å¼ã«æœ‰åŠ¹åŒ–
      const supabase2 = await this.supabase;
      await supabase2.from('user_mfa_settings').upsert({
        user_id: validatedVerification.userId,
        clinic_id: setupSession.clinic_id,
        secret_key: setupSession.secret_key,
        backup_codes: setupSession.backup_codes,
        is_enabled: true,
        setup_completed_at: new Date().toISOString(),
        last_used_at: new Date().toISOString(),
        created_at: new Date().toISOString(),
      });

      // ã‚»ãƒƒãƒˆã‚¢ãƒƒãƒ—ã‚»ãƒƒã‚·ãƒ§ãƒ³å‰Šé™¤
      await supabase2
        .from('mfa_setup_sessions')
        .delete()
        .eq('id', setupSession.id);

      return true;
    } catch (error) {
      throw new Error(
        `MFAã‚»ãƒƒãƒˆã‚¢ãƒƒãƒ—å®Œäº†ã‚¨ãƒ©ãƒ¼: ${error instanceof Error ? error.message : error}`
      );
    }
  }

  /**
   * TOTPèªè¨¼æ¤œè¨¼
   * ãƒ­ã‚°ã‚¤ãƒ³æ™‚ã‚„ã‚»ãƒ³ã‚·ãƒ†ã‚£ãƒ–ãªæ“ä½œæ™‚ã®èªè¨¼
   */
  async verifyTOTP(
    userId: string,
    token: string,
    window: number = 1
  ): Promise<boolean> {
    try {
      // å…¥åŠ›å€¤æ¤œè¨¼
      const validatedVerification = MFAVerificationSchema.parse({
        userId,
        token,
        window,
      });

      // MFAè¨­å®šå–å¾—
      const supabase = await this.supabase;
      const { data: mfaSettings, error } = await supabase
        .from('user_mfa_settings')
        .select('*')
        .eq('user_id', validatedVerification.userId)
        .eq('is_enabled', true)
        .single();

      if (error || !mfaSettings) {
        throw new Error('MFAè¨­å®šãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
      }

      // TOTPæ¤œè¨¼
      const isValid = speakeasy.totp.verify({
        secret: mfaSettings.secret_key,
        encoding: 'base32',
        token: validatedVerification.token,
        window: validatedVerification.window,
      });

      if (isValid) {
        // æœ€çµ‚ä½¿ç”¨æ—¥æ™‚æ›´æ–°
        const supabase2 = await this.supabase;
        await supabase2
          .from('user_mfa_settings')
          .update({ last_used_at: new Date().toISOString() })
          .eq('user_id', validatedVerification.userId);

        // æˆåŠŸãƒ­ã‚°è¨˜éŒ²
        await this.logMFAEvent({
          userId: validatedVerification.userId,
          eventType: 'totp_success',
          details: { window: validatedVerification.window },
        });

        return true;
      } else {
        // å¤±æ•—ãƒ­ã‚°è¨˜éŒ²
        await this.logMFAEvent({
          userId: validatedVerification.userId,
          eventType: 'totp_failed',
          details: { token: token.slice(0, 2) + '****' }, // éƒ¨åˆ†çš„ãªãƒˆãƒ¼ã‚¯ãƒ³ã®ã¿ãƒ­ã‚°
        });

        return false;
      }
    } catch (error) {
      throw new Error(
        `TOTPæ¤œè¨¼ã‚¨ãƒ©ãƒ¼: ${error instanceof Error ? error.message : error}`
      );
    }
  }

  /**
   * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰æ¤œè¨¼
   * TOTPãŒåˆ©ç”¨ã§ããªã„å ´åˆã®ç·Šæ€¥ã‚¢ã‚¯ã‚»ã‚¹
   */
  async verifyBackupCode(userId: string, code: string): Promise<boolean> {
    try {
      // å…¥åŠ›å€¤æ¤œè¨¼
      const validatedCode = BackupCodeSchema.parse({
        userId,
        code: code.toUpperCase(),
      });

      // MFAè¨­å®šå–å¾—
      const supabase = await this.supabase;
      const { data: mfaSettings, error } = await supabase
        .from('user_mfa_settings')
        .select('*')
        .eq('user_id', validatedCode.userId)
        .eq('is_enabled', true)
        .single();

      if (error || !mfaSettings) {
        throw new Error('MFAè¨­å®šãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
      }

      const backupCodes = mfaSettings.backup_codes || [];
      const codeIndex = backupCodes.indexOf(validatedCode.code);

      if (codeIndex === -1) {
        // å¤±æ•—ãƒ­ã‚°è¨˜éŒ²
        await this.logMFAEvent({
          userId: validatedCode.userId,
          eventType: 'backup_code_failed',
          details: { code: validatedCode.code.slice(0, 2) + '****' },
        });
        return false;
      }

      // ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ã‚’ä½¿ç”¨æ¸ˆã¿ã¨ã—ã¦ãƒãƒ¼ã‚¯ï¼ˆå‰Šé™¤ï¼‰
      const updatedBackupCodes = [...backupCodes];
      updatedBackupCodes.splice(codeIndex, 1);

      const supabase2 = await this.supabase;
      await supabase2
        .from('user_mfa_settings')
        .update({
          backup_codes: updatedBackupCodes,
          last_used_at: new Date().toISOString(),
        })
        .eq('user_id', validatedCode.userId);

      // æˆåŠŸãƒ­ã‚°è¨˜éŒ²
      await this.logMFAEvent({
        userId: validatedCode.userId,
        eventType: 'backup_code_success',
        details: {
          code: validatedCode.code.slice(0, 2) + '****',
          remainingCodes: updatedBackupCodes.length,
        },
      });

      // ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ãŒå°‘ãªããªã£ãŸå ´åˆã®è­¦å‘Š
      if (updatedBackupCodes.length <= 2) {
        await this.logMFAEvent({
          userId: validatedCode.userId,
          eventType: 'backup_codes_low',
          details: { remainingCodes: updatedBackupCodes.length },
        });
      }

      return true;
    } catch (error) {
      throw new Error(
        `ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰æ¤œè¨¼ã‚¨ãƒ©ãƒ¼: ${error instanceof Error ? error.message : error}`
      );
    }
  }

  /**
   * MFAçŠ¶æ…‹å–å¾—
   */
  async getMFAStatus(userId: string): Promise<MFAStatus> {
    try {
      const supabase = await this.supabase;
      const { data: mfaSettings, error } = await supabase
        .from('user_mfa_settings')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (error || !mfaSettings) {
        return {
          isEnabled: false,
          hasBackupCodes: false,
        };
      }

      return {
        isEnabled: mfaSettings.is_enabled,
        hasBackupCodes: (mfaSettings.backup_codes || []).length > 0,
        lastUsed: mfaSettings.last_used_at
          ? new Date(mfaSettings.last_used_at)
          : undefined,
        setupCompletedAt: mfaSettings.setup_completed_at
          ? new Date(mfaSettings.setup_completed_at)
          : undefined,
      };
    } catch (error) {
      throw new Error(
        `MFAçŠ¶æ…‹å–å¾—ã‚¨ãƒ©ãƒ¼: ${error instanceof Error ? error.message : error}`
      );
    }
  }

  /**
   * MFAç„¡åŠ¹åŒ–
   * ç®¡ç†è€…ã¾ãŸã¯ãƒ¦ãƒ¼ã‚¶ãƒ¼è‡ªèº«ã«ã‚ˆã‚‹ç„¡åŠ¹åŒ–
   */
  async disableMFA(userId: string, adminUserId?: string): Promise<boolean> {
    try {
      // MFAè¨­å®šã‚’ç„¡åŠ¹åŒ–
      const supabase = await this.supabase;
      const { error } = await supabase
        .from('user_mfa_settings')
        .update({
          is_enabled: false,
          disabled_at: new Date().toISOString(),
          disabled_by: adminUserId || userId,
        })
        .eq('user_id', userId);

      if (error) {
        throw new Error(`ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ã‚¨ãƒ©ãƒ¼: ${error.message}`);
      }

      // ç„¡åŠ¹åŒ–ãƒ­ã‚°è¨˜éŒ²
      await this.logMFAEvent({
        userId,
        eventType: 'mfa_disabled',
        details: {
          disabledBy: adminUserId || userId,
          isAdminAction: !!adminUserId,
        },
      });

      return true;
    } catch (error) {
      throw new Error(
        `MFAç„¡åŠ¹åŒ–ã‚¨ãƒ©ãƒ¼: ${error instanceof Error ? error.message : error}`
      );
    }
  }

  /**
   * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰å†ç”Ÿæˆ
   */
  async regenerateBackupCodes(userId: string): Promise<string[]> {
    try {
      // æ–°ã—ã„ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ç”Ÿæˆ
      const newBackupCodes = this.generateBackupCodes();

      // ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æ›´æ–°
      const supabase = await this.supabase;
      const { error } = await supabase
        .from('user_mfa_settings')
        .update({
          backup_codes: newBackupCodes,
          backup_codes_regenerated_at: new Date().toISOString(),
        })
        .eq('user_id', userId)
        .eq('is_enabled', true);

      if (error) {
        throw new Error(`ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ã‚¨ãƒ©ãƒ¼: ${error.message}`);
      }

      // å†ç”Ÿæˆãƒ­ã‚°è¨˜éŒ²
      await this.logMFAEvent({
        userId,
        eventType: 'backup_codes_regenerated',
        details: { codeCount: newBackupCodes.length },
      });

      return newBackupCodes;
    } catch (error) {
      throw new Error(
        `ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰å†ç”Ÿæˆã‚¨ãƒ©ãƒ¼: ${error instanceof Error ? error.message : error}`
      );
    }
  }

  /**
   * QRã‚³ãƒ¼ãƒ‰ç”Ÿæˆ
   */
  private async generateQRCode(otpauthUrl: string): Promise<string> {
    try {
      return await qrcode.toDataURL(otpauthUrl, {
        errorCorrectionLevel: 'M',
        type: 'image/png',
        quality: 0.92,
        margin: 1,
        width: 256,
        color: {
          dark: '#000000',
          light: '#FFFFFF',
        },
      });
    } catch (error) {
      throw new Error('QRã‚³ãƒ¼ãƒ‰ç”Ÿæˆã«å¤±æ•—ã—ã¾ã—ãŸ');
    }
  }

  /**
   * ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ã‚³ãƒ¼ãƒ‰ç”Ÿæˆï¼ˆ10å€‹ï¼‰
   */
  private generateBackupCodes(): string[] {
    const codes: string[] = [];
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';

    for (let i = 0; i < 10; i++) {
      let code = '';
      for (let j = 0; j < 8; j++) {
        code += chars.charAt(Math.floor(Math.random() * chars.length));
      }
      codes.push(code);
    }

    return codes;
  }

  /**
   * æ‰‹å‹•å…¥åŠ›ç”¨ã®ç§˜å¯†éµãƒ•ã‚©ãƒ¼ãƒãƒƒãƒˆ
   */
  private formatSecretForManualEntry(secret: string): string {
    return secret.match(/.{1,4}/g)?.join(' ') || secret;
  }

  /**
   * MFAã‚¤ãƒ™ãƒ³ãƒˆãƒ­ã‚°è¨˜éŒ²
   */
  private async logMFAEvent(event: {
    userId: string;
    eventType: string;
    details?: Record<string, any>;
  }): Promise<void> {
    try {
      const supabase = await this.supabase;
      await supabase.from('security_events').insert({
        event_type: `mfa_${event.eventType}`,
        user_id: event.userId,
        event_details: event.details || {},
        ip_address: '', // ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ã§è¨­å®šã•ã‚Œã‚‹
        user_agent: '', // ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ã§è¨­å®šã•ã‚Œã‚‹
        created_at: new Date().toISOString(),
      });
    } catch (error) {
      // ãƒ­ã‚°è¨˜éŒ²ã‚¨ãƒ©ãƒ¼ã¯ä¸»æ©Ÿèƒ½ã‚’å¦¨ã’ãªã„
      console.error('MFAã‚¤ãƒ™ãƒ³ãƒˆãƒ­ã‚°è¨˜éŒ²ã‚¨ãƒ©ãƒ¼:', error);
    }
  }
}

// ã‚·ãƒ³ã‚°ãƒ«ãƒˆãƒ³ã‚¤ãƒ³ã‚¹ã‚¿ãƒ³ã‚¹
export const mfaManager = new MFAManager();

/**
 * ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ãƒ‘ãƒ•ã‚©ãƒ¼ãƒãƒ³ã‚¹æœ€é©åŒ–
 * è¤‡æ•°ã®DBå•ã„åˆã‚ã›ã‚’ä¸¦åˆ—åŒ–ã—ã¦ãƒ¬ã‚¹ãƒãƒ³ã‚¹ã‚¿ã‚¤ãƒ æ”¹å–„
 */

import { NextRequest } from 'next/server';
import { createClient } from '@/lib/supabase';
import { SessionManager } from '@/lib/session-manager';
import { SecurityMonitor } from '@/lib/security-monitor';
import { logger } from '@/lib/logger';

export interface OptimizedAuthData {
  user: unknown;
  profile: unknown;
  customSessionValidation?: unknown;
  securityThreats?: Array<{ severity?: string }>;
}

/**
 * ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ç”¨æœ€é©åŒ–ã•ã‚ŒãŸèªè¨¼ãƒ»ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒã‚§ãƒƒã‚¯
 * è¤‡æ•°ã®DBå•ã„åˆã‚ã›ã‚’ä¸¦åˆ—å®Ÿè¡Œã—ã¦ãƒ‘ãƒ•ã‚©ãƒ¼ãƒãƒ³ã‚¹å‘ä¸Š
 */
export class MiddlewareOptimizer {
  private sessionManager: SessionManager;
  private securityMonitor: SecurityMonitor;

  constructor() {
    this.sessionManager = new SessionManager();
    this.securityMonitor = new SecurityMonitor();
  }

  /**
   * ä¸¦åˆ—åŒ–ã•ã‚ŒãŸèªè¨¼ãƒ»ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒã‚§ãƒƒã‚¯
   */
  async performOptimizedAuthCheck(
    request: NextRequest,
    isAdminRoute: boolean = false
  ): Promise<OptimizedAuthData | null> {
    const supabase = await createClient();
    const ipAddress = this.getClientIP(request);
    const userAgent = request.headers.get('user-agent') || '';
    const customSessionToken = request.cookies.get('session-token')?.value;

    try {
      // ä¸¦åˆ—å®Ÿè¡Œã™ã‚‹Promiseã‚’æº–å‚™
      const promises: Promise<any>[] = [
        // 1. Supabaseãƒ¦ãƒ¼ã‚¶ãƒ¼å–å¾—
        supabase.auth.getUser(),
      ];

      // 2. ã‚«ã‚¹ã‚¿ãƒ ã‚»ãƒƒã‚·ãƒ§ãƒ³æ¤œè¨¼ï¼ˆãƒˆãƒ¼ã‚¯ãƒ³ãŒã‚ã‚‹å ´åˆï¼‰
      if (customSessionToken) {
        promises.push(this.sessionManager.validateSession(customSessionToken));
      } else {
        promises.push(Promise.resolve(null));
      }

      // åŸºæœ¬ãƒã‚§ãƒƒã‚¯ã‚’ä¸¦åˆ—å®Ÿè¡Œ
      const [userResult, customSessionValidation] = await Promise.all(promises);

      const {
        data: { user },
        error: userError,
      } = userResult;

      // æœªèªè¨¼ã®å ´åˆã¯æ—©æœŸãƒªã‚¿ãƒ¼ãƒ³
      if (userError || !user) {
        return null;
      }

      // ãƒ¦ãƒ¼ã‚¶ãƒ¼èªè¨¼æˆåŠŸå¾Œã®è¿½åŠ ãƒã‚§ãƒƒã‚¯ã‚’ä¸¦åˆ—å®Ÿè¡Œ
      const additionalPromises: Promise<any>[] = [
        // 3. ãƒ¦ãƒ¼ã‚¶ãƒ¼ãƒ—ãƒ­ãƒ•ã‚¡ã‚¤ãƒ«å–å¾—
        Promise.resolve(
          supabase
            .from('profiles')
            .select('role, clinic_id, is_active, full_name')
            .eq('user_id', user.id)
            .single()
        ),
      ];

      // 4. ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£åˆ†æï¼ˆã‚«ã‚¹ã‚¿ãƒ ã‚»ãƒƒã‚·ãƒ§ãƒ³ãŒæœ‰åŠ¹ãªå ´åˆï¼‰
      if (customSessionValidation?.isValid && customSessionValidation.session) {
        additionalPromises.push(
          this.securityMonitor.analyzeSessionActivity(
            customSessionValidation.session,
            { ipAddress, userAgent }
          )
        );
      } else {
        additionalPromises.push(Promise.resolve([]));
      }

      // è¿½åŠ ãƒã‚§ãƒƒã‚¯ã‚’ä¸¦åˆ—å®Ÿè¡Œ
      const [profileResult, securityThreats] =
        await Promise.all(additionalPromises);

      return {
        user,
        profile: profileResult.data,
        customSessionValidation,
        securityThreats: securityThreats || [],
      };
    } catch (error) {
      logger.error('æœ€é©åŒ–èªè¨¼ãƒã‚§ãƒƒã‚¯ã‚¨ãƒ©ãƒ¼:', error);
      throw error;
    }
  }

  /**
   * ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£è„…å¨ã®æ‰¹åˆ¤çš„è©•ä¾¡
   * é«˜ãƒªã‚¹ã‚¯ã®è„…å¨ã®ã¿ã‚’ãƒ•ã‚£ãƒ«ã‚¿ãƒªãƒ³ã‚°
   */
  evaluateCriticalThreats(
    threats: Array<{ severity?: string }>
  ): Array<{ severity?: string }> {
    return threats.filter(
      threat => threat.severity === 'high' || threat.severity === 'critical'
    );
  }

  /**
   * ã‚»ãƒƒã‚·ãƒ§ãƒ³æƒ…å ±ã®éåŒæœŸæ›´æ–°
   * ãƒ¬ã‚¹ãƒãƒ³ã‚¹ã‚¿ã‚¤ãƒ ã«å½±éŸ¿ã—ãªã„ã‚ˆã†ã«èƒŒæ™¯ã§å®Ÿè¡Œ
   */
  async updateSessionInBackground(
    sessionToken: string,
    ipAddress: string,
    userId: string,
    clinicId?: string
  ): Promise<void> {
    // éåŒæœŸã§ã‚»ãƒƒã‚·ãƒ§ãƒ³æƒ…å ±ã‚’æ›´æ–°ï¼ˆãƒ¬ã‚¹ãƒãƒ³ã‚¹ã‚’ãƒ–ãƒ­ãƒƒã‚¯ã—ãªã„ï¼‰
    setImmediate(async () => {
      try {
        await this.sessionManager.refreshSession(sessionToken, ipAddress);

        // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã‚¤ãƒ™ãƒ³ãƒˆã®è¨˜éŒ²ã‚‚éåŒæœŸ
        await this.securityMonitor.logSecurityEvent({
          event_type: 'session_activity',
          event_category: 'session_management',
          severity_level: 'info',
          event_description: 'Session refresh via middleware',
          user_id: userId,
          clinic_id: clinicId || 'unknown',
          ip_address: ipAddress,
          user_agent: 'middleware',
          source_component: 'middleware_optimizer',
          event_data: {
            action: 'session_refresh',
            timestamp: new Date().toISOString(),
          },
        });
      } catch (error) {
        logger.error('Background session update failed:', error);
      }
    });
  }

  /**
   * ç®¡ç†è€…æ¨©é™ãƒã‚§ãƒƒã‚¯ã®æœ€é©åŒ–
   */
  validateAdminAccess(
    profile: any,
    requestPath: string
  ): {
    isAuthorized: boolean;
    reason?: string;
  } {
    if (!profile || !profile.is_active) {
      return {
        isAuthorized: false,
        reason: 'inactive_profile',
      };
    }

    const adminRoles = ['admin', 'clinic_admin', 'manager'];
    if (!adminRoles.includes(profile.role)) {
      return {
        isAuthorized: false,
        reason: 'insufficient_privileges',
      };
    }

    return { isAuthorized: true };
  }

  /**
   * ã‚­ãƒ£ãƒƒã‚·ãƒ¥å¯¾å¿œã®ã‚¯ãƒ©ã‚¤ã‚¢ãƒ³ãƒˆIPå–å¾—
   */
  private getClientIP(request: NextRequest): string {
    // x-forwarded-for ãƒ˜ãƒƒãƒ€ãƒ¼ã‚’å„ªå…ˆ
    const forwarded = request.headers.get('x-forwarded-for');
    if (forwarded) {
      return forwarded.split(',')[0].trim();
    }

    // x-real-ip ãƒ˜ãƒƒãƒ€ãƒ¼ã‚’ãƒã‚§ãƒƒã‚¯
    const realIp = request.headers.get('x-real-ip');
    if (realIp) {
      return realIp;
    }

    // CF-Connecting-IPï¼ˆCloudflareï¼‰ã‚’ãƒã‚§ãƒƒã‚¯
    const cfIp = request.headers.get('cf-connecting-ip');
    if (cfIp) {
      return cfIp;
    }

    return 'unknown';
  }
}

/**
 * ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚­ãƒ£ãƒƒã‚·ãƒ¥ç®¡ç†
 * çŸ­æœŸé–“ã®ã‚­ãƒ£ãƒƒã‚·ãƒ¥ã§DBå•ã„åˆã‚ã›ã‚’å‰Šæ¸›
 */
export class SessionCache {
  private static cache = new Map<
    string,
    {
      data: any;
      expires: number;
    }
  >();

  static set(key: string, data: unknown, ttlSeconds: number = 60): void {
    this.cache.set(key, {
      data,
      expires: Date.now() + ttlSeconds * 1000,
    });
  }

  static get(key: string): unknown | null {
    const cached = this.cache.get(key);

    if (!cached) {
      return null;
    }

    if (Date.now() > cached.expires) {
      this.cache.delete(key);
      return null;
    }

    return cached.data;
  }

  static clear(): void {
    this.cache.clear();
  }

  /**
   * å®šæœŸçš„ãªã‚­ãƒ£ãƒƒã‚·ãƒ¥ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
   */
  static startPeriodicCleanup(): void {
    setInterval(
      () => {
        const now = Date.now();
        for (const [key, value] of this.cache.entries()) {
          if (now > value.expires) {
            this.cache.delete(key);
          }
        }
      },
      5 * 60 * 1000
    ); // 5åˆ†ã”ã¨
  }
}

/**
 * è¤‡æ•°ãƒ‡ãƒã‚¤ã‚¹åˆ¶å¾¡ã‚·ã‚¹ãƒ†ãƒ 
 * Phase 3A: ã‚»ãƒƒã‚·ãƒ§ãƒ³ç®¡ç†å¼·åŒ–ã®ä¸€ç’°ã¨ã—ã¦ã®è¤‡æ•°ãƒ‡ãƒã‚¤ã‚¹ç®¡ç†
 */

import { createClient } from '@/lib/supabase-browser';
import { SessionManager, type DeviceInfo } from './session-manager';
import { SecurityMonitor } from './security-monitor';
import { logger } from '@/lib/logger';

// ================================================================
// å‹å®šç¾©
// ================================================================

export interface DeviceSession {
  sessionId: string;
  deviceInfo: DeviceInfo;
  ipAddress?: string;
  userAgent?: string;
  lastActivity: Date;
  createdAt: Date;
  isCurrentDevice: boolean;
  isTrusted: boolean;
  location?: {
    country?: string;
    region?: string;
    city?: string;
  };
}

export interface MultiDeviceConfig {
  maxConcurrentDevices: number;
  requireDeviceTrust: boolean;
  allowDifferentIPs: boolean;
  notifyNewDevice: boolean;
  autoRevokeOldSessions: boolean;
  trustNewDeviceAfterDays: number;
}

export interface DeviceManagementAction {
  action: 'trust' | 'block' | 'revoke_session' | 'revoke_all_other';
  deviceId?: string;
  sessionId?: string;
  reason?: string;
}

export interface DeviceSecurityAlert {
  type:
    | 'new_device'
    | 'suspicious_activity'
    | 'concurrent_limit'
    | 'location_change';
  severity: 'low' | 'medium' | 'high';
  message: string;
  deviceInfo: DeviceInfo;
  timestamp: Date;
  actionRequired: boolean;
}

// ================================================================
// è¤‡æ•°ãƒ‡ãƒã‚¤ã‚¹ç®¡ç†ã‚¯ãƒ©ã‚¹
// ================================================================

export class MultiDeviceManager {
  private readonly supabase;
  private sessionManager: SessionManager;
  private securityMonitor: SecurityMonitor;

  constructor() {
    this.supabase = createClient();
    this.sessionManager = new SessionManager();
    this.securityMonitor = new SecurityMonitor();
  }

  private getSupabase() {
    return this.supabase;
  }

  /**
   * ãƒ‡ãƒã‚¤ã‚¹ä¿¡é ¼åˆ¤å®šï¼ˆå…¬é–‹APIï¼‰
   * æŒ‡ç´‹(JSONæ–‡å­—åˆ—)ã§ç™»éŒ²æ¸ˆã¿ã‹ã¤ä¿¡é ¼æ¸ˆã¿ã‹ã‚’åˆ¤å®š
   */
  async isDeviceTrusted(
    userId: string,
    deviceFingerprint: string
  ): Promise<boolean> {
    try {
      const supabase = await this.getSupabase();
      const query = supabase
        .from('registered_devices')
        .select('is_trusted, trust_score, trust_level, device_fingerprint')
        .eq('user_id', userId)
        .eq('device_fingerprint', deviceFingerprint)
        .limit(1);

      let { data, error } = await query.single();

      // single() ãŒæœªè¨­å®šï¼ˆthenableã®ã¿ï¼‰ã®å ´åˆã«ã‚‚å¯¾å¿œ
      if (error || !data) {
        try {
          const res = await query;
          // Supabase builder may return thenable without strict typing
          data = res && res.data ? res.data : null;
        } catch (_) {
          // ignore
        }
      }

      if (!data) return false;

      const record = Array.isArray(data) ? data[0] || null : data;
      if (!record) return false;

      // ä¸€è‡´æ¤œè¨¼ï¼ˆã‚¯ã‚¨ãƒªçµæœãŒä»–æŒ‡ç´‹ã®å¯èƒ½æ€§ã«å‚™ãˆã‚‹ï¼‰
      if (
        typeof record.device_fingerprint === 'string' &&
        record.device_fingerprint !== deviceFingerprint
      ) {
        return false;
      }

      return Boolean(
        record.is_trusted === true ||
        record.trust_level === 'trusted' ||
        (typeof record.trust_score === 'number' && record.trust_score >= 80)
      );
    } catch (_) {
      return false;
    }
  }

  /**
   * ãƒ¦ãƒ¼ã‚¶ãƒ¼ã®ã‚¢ã‚¯ãƒ†ã‚£ãƒ–ãƒ‡ãƒã‚¤ã‚¹ä¸€è¦§å–å¾—
   */
  async getUserDevices(
    userId: string,
    clinicId: string
  ): Promise<DeviceSession[]> {
    try {
      const supabase = await this.getSupabase();
      const { data: sessions, error } = await supabase
        .from('user_sessions')
        .select(
          `
          id,
          device_info,
          ip_address,
          user_agent,
          last_activity,
          created_at,
          is_active,
          geolocation
        `
        )
        .eq('user_id', userId)
        .eq('clinic_id', clinicId)
        .eq('is_active', true)
        .eq('is_revoked', false)
        .order('last_activity', { ascending: false });

      if (error || !sessions) {
        logger.error('ãƒ‡ãƒã‚¤ã‚¹ä¸€è¦§å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
        return [];
      }

      // ç¾åœ¨ã®ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’ç‰¹å®šã™ã‚‹ãŸã‚ã®ãƒˆãƒ¼ã‚¯ãƒ³ã‚’å–å¾—ï¼ˆå®Ÿè£…ã¯ç’°å¢ƒã«ä¾å­˜ï¼‰
      const currentSessionToken = this.getCurrentSessionToken();

      return sessions.map(session => ({
        sessionId: session.id,
        deviceInfo: session.device_info || {
          device: 'unknown',
          os: 'unknown',
          browser: 'unknown',
        },
        ipAddress: session.ip_address,
        userAgent: session.user_agent,
        lastActivity: new Date(session.last_activity),
        createdAt: new Date(session.created_at),
        isCurrentDevice: session.session_token === currentSessionToken,
        isTrusted: this.isDeviceTrustedByAge(
          session.device_info,
          session.created_at
        ),
        location: session.geolocation || undefined,
      }));
    } catch (error) {
      logger.error('getUserDevices ã‚¨ãƒ©ãƒ¼:', error);
      return [];
    }
  }

  /**
   * æ–°ãƒ‡ãƒã‚¤ã‚¹ç™»éŒ²æ™‚ã®æ¤œè¨¼
   */
  async validateNewDevice(
    userId: string,
    clinicId: string,
    deviceInfo: DeviceInfo,
    ipAddress?: string
  ): Promise<{
    isAllowed: boolean;
    alerts: DeviceSecurityAlert[];
    config: MultiDeviceConfig;
  }> {
    const config = await this.getMultiDeviceConfig(clinicId);
    const alerts: DeviceSecurityAlert[] = [];

    // æ—¢å­˜ã®ã‚¢ã‚¯ãƒ†ã‚£ãƒ–ãƒ‡ãƒã‚¤ã‚¹æ•°ã‚’ãƒã‚§ãƒƒã‚¯
    const activeDeviceCount = await this.sessionManager.getActiveSessionCount(
      userId,
      clinicId
    );

    if (activeDeviceCount >= config.maxConcurrentDevices) {
      alerts.push({
        type: 'concurrent_limit',
        severity: 'high',
        message: `ãƒ‡ãƒã‚¤ã‚¹æ•°ä¸Šé™ï¼ˆ${config.maxConcurrentDevices}å°ï¼‰ã«é”ã—ã¦ã„ã¾ã™`,
        deviceInfo,
        timestamp: new Date(),
        actionRequired: true,
      });

      if (!config.autoRevokeOldSessions) {
        return { isAllowed: false, alerts, config };
      }
    }

    // æ–°ãƒ‡ãƒã‚¤ã‚¹ã®æ¤œå‡º
    const isNewDevice = await this.isNewDevice(userId, deviceInfo);
    if (isNewDevice) {
      alerts.push({
        type: 'new_device',
        severity: config.notifyNewDevice ? 'medium' : 'low',
        message: 'æ–°ã—ã„ãƒ‡ãƒã‚¤ã‚¹ã‹ã‚‰ã®ã‚¢ã‚¯ã‚»ã‚¹ã§ã™',
        deviceInfo,
        timestamp: new Date(),
        actionRequired: config.requireDeviceTrust,
      });
    }

    // ç•°ãªã‚‹IPã‚¢ãƒ‰ãƒ¬ã‚¹ã‹ã‚‰ã®ã‚¢ã‚¯ã‚»ã‚¹
    if (ipAddress) {
      const locationChange = await this.detectLocationChange(userId, ipAddress);
      if (locationChange) {
        alerts.push({
          type: 'location_change',
          severity: 'medium',
          message: 'é€šå¸¸ã¨ã¯ç•°ãªã‚‹åœ°åŸŸã‹ã‚‰ã®ã‚¢ã‚¯ã‚»ã‚¹ã§ã™',
          deviceInfo,
          timestamp: new Date(),
          actionRequired: false,
        });
      }
    }

    return { isAllowed: true, alerts, config };
  }

  /**
   * ãƒ‡ãƒã‚¤ã‚¹ç®¡ç†ã‚¢ã‚¯ã‚·ãƒ§ãƒ³å®Ÿè¡Œ
   */
  async executeDeviceAction(
    action: DeviceManagementAction,
    userId: string,
    clinicId: string
  ): Promise<{ success: boolean; message: string }> {
    try {
      switch (action.action) {
        case 'trust':
          return await this.trustDevice(action.deviceId!, userId, clinicId);

        case 'block':
          return await this.blockDevice(
            action.deviceId!,
            userId,
            clinicId,
            action.reason
          );

        case 'revoke_session':
          return await this.revokeDeviceSession(
            action.sessionId!,
            action.reason || 'manual'
          );

        case 'revoke_all_other':
          return await this.revokeAllOtherSessions(
            userId,
            clinicId,
            action.sessionId
          );

        default:
          return { success: false, message: 'ä¸æ˜ãªã‚¢ã‚¯ã‚·ãƒ§ãƒ³ã§ã™' };
      }
    } catch (error) {
      logger.error('Device action execution error:', error);
      return {
        success: false,
        message: 'ã‚¢ã‚¯ã‚·ãƒ§ãƒ³å®Ÿè¡Œä¸­ã«ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ',
      };
    }
  }

  /**
   * ãƒ‡ãƒã‚¤ã‚¹åŒæœŸçŠ¶æ…‹ã®ç¢ºèª
   */
  async checkDeviceSyncStatus(
    userId: string,
    clinicId: string
  ): Promise<{
    totalDevices: number;
    activeDevices: number;
    trustedDevices: number;
    suspiciousDevices: number;
    lastSyncAt?: Date;
  }> {
    const devices = await this.getUserDevices(userId, clinicId);

    const activeDevices = devices.filter(
      d => d.lastActivity > new Date(Date.now() - 24 * 60 * 60 * 1000)
    );
    const trustedDevices = devices.filter(d => d.isTrusted);
    const suspiciousDevices = devices.filter(
      d => !d.isTrusted && activeDevices.includes(d)
    );

    return {
      totalDevices: devices.length,
      activeDevices: activeDevices.length,
      trustedDevices: trustedDevices.length,
      suspiciousDevices: suspiciousDevices.length,
      lastSyncAt: devices.length > 0 ? devices[0].lastActivity : undefined,
    };
  }

  /**
   * ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£æ¨å¥¨äº‹é …ã®ç”Ÿæˆ
   */
  async generateSecurityRecommendations(
    userId: string,
    clinicId: string
  ): Promise<
    Array<{
      type: 'action' | 'warning' | 'info';
      title: string;
      description: string;
      actionLabel?: string;
      actionData?: any;
    }>
  > {
    const devices = await this.getUserDevices(userId, clinicId);
    const config = await this.getMultiDeviceConfig(clinicId);
    const recommendations = [];

    // å¤ã„ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®æ¤œå‡º
    const oldSessions = devices.filter(
      d => d.lastActivity < new Date(Date.now() - 7 * 24 * 60 * 60 * 1000)
    );

    if (oldSessions.length > 0) {
      recommendations.push({
        type: 'action' as const,
        title: 'å¤ã„ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®æ•´ç†',
        description: `${oldSessions.length}å€‹ã®å¤ã„ã‚»ãƒƒã‚·ãƒ§ãƒ³ãŒã‚ã‚Šã¾ã™`,
        actionLabel: 'å¤ã„ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’å‰Šé™¤',
        actionData: {
          action: 'revoke_old_sessions',
          sessionIds: oldSessions.map(s => s.sessionId),
        },
      });
    }

    // ä¿¡é ¼ã•ã‚Œã¦ã„ãªã„ãƒ‡ãƒã‚¤ã‚¹ã®æ¤œå‡º
    const untrustedDevices = devices.filter(d => !d.isTrusted);
    if (untrustedDevices.length > 0) {
      recommendations.push({
        type: 'warning' as const,
        title: 'ä¿¡é ¼ã•ã‚Œã¦ã„ãªã„ãƒ‡ãƒã‚¤ã‚¹',
        description: `${untrustedDevices.length}å°ã®ãƒ‡ãƒã‚¤ã‚¹ãŒä¿¡é ¼æ¸ˆã¿ãƒªã‚¹ãƒˆã«ã‚ã‚Šã¾ã›ã‚“`,
      });
    }

    // ãƒ‡ãƒã‚¤ã‚¹æ•°ä¸Šé™ã®è­¦å‘Š
    if (devices.length >= config.maxConcurrentDevices * 0.8) {
      recommendations.push({
        type: 'warning' as const,
        title: 'ãƒ‡ãƒã‚¤ã‚¹æ•°ä¸Šé™ã«è¿‘ã¥ã„ã¦ã„ã¾ã™',
        description: `ç¾åœ¨${devices.length}/${config.maxConcurrentDevices}å°ã®ãƒ‡ãƒã‚¤ã‚¹ãŒç™»éŒ²ã•ã‚Œã¦ã„ã¾ã™`,
      });
    }

    return recommendations;
  }

  // ================================================================
  // ãƒ—ãƒ©ã‚¤ãƒ™ãƒ¼ãƒˆãƒ¡ã‚½ãƒƒãƒ‰
  // ================================================================

  /**
   * è¤‡æ•°ãƒ‡ãƒã‚¤ã‚¹è¨­å®šå–å¾—
   */
  private async getMultiDeviceConfig(
    clinicId: string
  ): Promise<MultiDeviceConfig> {
    const supabase = await this.getSupabase();
    const { data: policy } = await supabase
      .from('session_policies')
      .select('*')
      .eq('clinic_id', clinicId)
      .eq('is_active', true)
      .single();

    if (!policy) {
      // ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆè¨­å®š
      return {
        maxConcurrentDevices: 3,
        requireDeviceTrust: false,
        allowDifferentIPs: true,
        notifyNewDevice: true,
        autoRevokeOldSessions: true,
        trustNewDeviceAfterDays: 7,
      };
    }

    return {
      maxConcurrentDevices: policy.max_devices_per_user || 3,
      requireDeviceTrust: policy.require_device_registration || false,
      allowDifferentIPs: !policy.block_concurrent_different_ips,
      notifyNewDevice: policy.notify_new_device_login,
      autoRevokeOldSessions: true,
      trustNewDeviceAfterDays: policy.remember_device_days || 7,
    };
  }

  /**
   * æ–°ãƒ‡ãƒã‚¤ã‚¹åˆ¤å®š
   */
  private async isNewDevice(
    userId: string,
    deviceInfo: DeviceInfo
  ): Promise<boolean> {
    const supabase = await this.getSupabase();
    const { count } = await supabase
      .from('user_sessions')
      .select('*', { count: 'exact' })
      .eq('user_id', userId)
      .contains('device_info', {
        device: deviceInfo.device,
        os: deviceInfo.os,
      });

    return (count || 0) === 0;
  }

  /**
   * ä½ç½®å¤‰æ›´ã®æ¤œå‡º
   */
  private async detectLocationChange(
    userId: string,
    currentIP: string
  ): Promise<boolean> {
    const supabase = await this.getSupabase();
    const { data: recentSessions } = await supabase
      .from('user_sessions')
      .select('ip_address, geolocation')
      .eq('user_id', userId)
      .eq('is_active', true)
      .neq('ip_address', currentIP)
      .limit(5);

    if (!recentSessions || recentSessions.length === 0) {
      return false;
    }

    // ç°¡æ˜“çš„ãªä½ç½®åˆ¤å®š
    const knownIPs = recentSessions.map(s => s.ip_address);
    return !knownIPs.some(ip => this.isSimilarIP(currentIP, ip));
  }

  /**
   * IPé¡ä¼¼æ€§åˆ¤å®š
   */
  private isSimilarIP(ip1: string, ip2: string): boolean {
    if (!ip1 || !ip2) return false;

    // åŒã˜ã‚µãƒ–ãƒãƒƒãƒˆï¼ˆ/24ï¼‰ã‹ãƒã‚§ãƒƒã‚¯
    const parts1 = ip1.split('.');
    const parts2 = ip2.split('.');

    if (parts1.length !== 4 || parts2.length !== 4) return false;

    return parts1.slice(0, 3).join('.') === parts2.slice(0, 3).join('.');
  }

  /**
   * ãƒ‡ãƒã‚¤ã‚¹ä¿¡é ¼æ€§åˆ¤å®š
   */
  private isDeviceTrustedByAge(
    deviceInfo: DeviceInfo,
    createdAt: string
  ): boolean {
    const createdDate = new Date(createdAt);
    const daysSinceCreated =
      (Date.now() - createdDate.getTime()) / (1000 * 60 * 60 * 24);

    // 7æ—¥ä»¥ä¸Šä½¿ç”¨ã•ã‚Œã¦ã„ã‚‹ãƒ‡ãƒã‚¤ã‚¹ã¯ä¿¡é ¼æ¸ˆã¿ã¨ã¿ãªã™
    return daysSinceCreated >= 7;
  }

  /**
   * ãƒ‡ãƒã‚¤ã‚¹ä¿¡é ¼è¨­å®š
   */
  private async trustDevice(
    deviceId: string,
    userId: string,
    clinicId: string
  ): Promise<{
    success: boolean;
    message: string;
  }> {
    // registered_devicesãƒ†ãƒ¼ãƒ–ãƒ«ã«ç™»éŒ²
    const supabase = await this.getSupabase();
    const { error } = await supabase.from('registered_devices').upsert({
      user_id: userId,
      clinic_id: clinicId,
      device_fingerprint: deviceId,
      trust_level: 'trusted',
      trusted_at: new Date().toISOString(),
    });

    if (error) {
      return { success: false, message: 'ãƒ‡ãƒã‚¤ã‚¹ã®ä¿¡é ¼è¨­å®šã«å¤±æ•—ã—ã¾ã—ãŸ' };
    }

    return { success: true, message: 'ãƒ‡ãƒã‚¤ã‚¹ã‚’ä¿¡é ¼æ¸ˆã¿ã«è¨­å®šã—ã¾ã—ãŸ' };
  }

  /**
   * ãƒ‡ãƒã‚¤ã‚¹ãƒ–ãƒ­ãƒƒã‚¯
   */
  private async blockDevice(
    deviceId: string,
    userId: string,
    clinicId: string,
    reason?: string
  ): Promise<{ success: boolean; message: string }> {
    const supabase = await this.getSupabase();
    const { error } = await supabase.from('registered_devices').upsert({
      user_id: userId,
      clinic_id: clinicId,
      device_fingerprint: deviceId,
      trust_level: 'blocked',
      blocked_at: new Date().toISOString(),
      blocked_reason: reason,
    });

    if (error) {
      return {
        success: false,
        message: 'ãƒ‡ãƒã‚¤ã‚¹ã®ãƒ–ãƒ­ãƒƒã‚¯è¨­å®šã«å¤±æ•—ã—ã¾ã—ãŸ',
      };
    }

    // è©²å½“ãƒ‡ãƒã‚¤ã‚¹ã®ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’ç„¡åŠ¹åŒ–
    await supabase
      .from('user_sessions')
      .update({
        is_active: false,
        is_revoked: true,
        revoked_at: new Date().toISOString(),
        revoked_reason: 'device_blocked',
      })
      .eq('user_id', userId)
      .eq('clinic_id', clinicId)
      .contains('device_info', { device: deviceId });

    return { success: true, message: 'ãƒ‡ãƒã‚¤ã‚¹ã‚’ãƒ–ãƒ­ãƒƒã‚¯ã—ã¾ã—ãŸ' };
  }

  /**
   * ã‚»ãƒƒã‚·ãƒ§ãƒ³ç„¡åŠ¹åŒ–
   */
  private async revokeDeviceSession(
    sessionId: string,
    reason: string
  ): Promise<{
    success: boolean;
    message: string;
  }> {
    const success = await this.sessionManager.revokeSession(
      sessionId,
      reason as any
    );

    return {
      success,
      message: success
        ? 'ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’ç„¡åŠ¹åŒ–ã—ã¾ã—ãŸ'
        : 'ã‚»ãƒƒã‚·ãƒ§ãƒ³ç„¡åŠ¹åŒ–ã«å¤±æ•—ã—ã¾ã—ãŸ',
    };
  }

  /**
   * ä»–ã®å…¨ã‚»ãƒƒã‚·ãƒ§ãƒ³ç„¡åŠ¹åŒ–
   */
  private async revokeAllOtherSessions(
    userId: string,
    clinicId: string,
    keepSessionId?: string
  ): Promise<{ success: boolean; message: string }> {
    try {
      const supabase = await this.getSupabase();
      const { data: sessions } = await supabase
        .from('user_sessions')
        .select('id')
        .eq('user_id', userId)
        .eq('clinic_id', clinicId)
        .eq('is_active', true)
        .eq('is_revoked', false)
        .neq('id', keepSessionId || '');

      if (!sessions || sessions.length === 0) {
        return {
          success: true,
          message: 'ç„¡åŠ¹åŒ–ã™ã‚‹ã‚»ãƒƒã‚·ãƒ§ãƒ³ãŒã‚ã‚Šã¾ã›ã‚“ã§ã—ãŸ',
        };
      }

      let revokedCount = 0;
      for (const session of sessions) {
        const success = await this.sessionManager.revokeSession(
          session.id,
          'manual_logout'
        );
        if (success) revokedCount++;
      }

      return {
        success: revokedCount > 0,
        message: `${revokedCount}å€‹ã®ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’ç„¡åŠ¹åŒ–ã—ã¾ã—ãŸ`,
      };
    } catch (error) {
      console.error('revokeAllOtherSessions error:', error);
      return { success: false, message: 'ã‚»ãƒƒã‚·ãƒ§ãƒ³ç„¡åŠ¹åŒ–ã«å¤±æ•—ã—ã¾ã—ãŸ' };
    }
  }

  /**
   * ç¾åœ¨ã®ã‚»ãƒƒã‚·ãƒ§ãƒ³ãƒˆãƒ¼ã‚¯ãƒ³å–å¾—
   */
  private getCurrentSessionToken(): string | null {
    if (typeof document === 'undefined') {
      return null;
    }

    const cookies = document.cookie.split(';');
    const sessionCookie = cookies.find(cookie =>
      cookie.trim().startsWith('session-token=')
    );

    return sessionCookie ? sessionCookie.split('=')[1] : null;
  }
}

