  }

  /**
   * CSPé•åã‚¢ãƒ©ãƒ¼ãƒˆãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ç”Ÿæˆ
   */
  private generateCSPAlertMessage(violation: {
    severity: string;
    violated_directive: string;
    blocked_uri: string;
    document_uri: string;
    threat_score: number;
