// ãƒ‘ãƒ•ã‚©ãƒ¼ãƒãƒ³ã‚¹æ¸¬å®šã¨Web Vitalsç›£è¦–
import { logger } from '@/lib/logger';

export interface PerformanceMetrics {
  fcp?: number; // First Contentful Paint
  lcp?: number; // Largest Contentful Paint
  fid?: number; // First Input Delay
  cls?: number; // Cumulative Layout Shift
  ttfb?: number; // Time to First Byte
}

export class PerformanceMonitor {
  private metrics: PerformanceMetrics = {};
  private observer?: PerformanceObserver;

  constructor() {
    if (typeof window !== 'undefined') {
      this.initializeObservers();
    }
  }

  private initializeObservers() {
    // Web Vitalsç›£è¦–
    if ('PerformanceObserver' in window) {
      try {
        // Largest Contentful Paint
        const lcpObserver = new PerformanceObserver(list => {
          const entries = list.getEntries();
          const lastEntry = entries[entries.length - 1] as PerformanceEntry & {
            renderTime?: number;
            loadTime?: number;
          };
          this.metrics.lcp =
            lastEntry.renderTime || lastEntry.loadTime || lastEntry.startTime;
        });
        lcpObserver.observe({ entryTypes: ['largest-contentful-paint'] });

        // First Input Delay
        const fidObserver = new PerformanceObserver(list => {
          const firstInput = list.getEntries()[0] as PerformanceEntry & {
            processingStart?: number;
          };
          this.metrics.fid = firstInput.processingStart
            ? firstInput.processingStart - firstInput.startTime
            : 0;
        });
        fidObserver.observe({ entryTypes: ['first-input'] });

        // Cumulative Layout Shift
        let clsValue = 0;
        const clsObserver = new PerformanceObserver(list => {
          for (const entry of list.getEntries()) {
            const layoutShift = entry as PerformanceEntry & {
              value?: number;
              hadRecentInput?: boolean;
            };
            if (!layoutShift.hadRecentInput) {
              clsValue += layoutShift.value || 0;
            }
          }
          this.metrics.cls = clsValue;
        });
        clsObserver.observe({ entryTypes: ['layout-shift'] });
      } catch (e) {
        logger.warn(
          'Performance Observer not supported or failed to initialize'
        );
      }
    }

    // Navigation Timing API
    window.addEventListener('load', () => {
      setTimeout(() => {
        const navigation = performance.getEntriesByType(
          'navigation'
        )[0] as PerformanceNavigationTiming;
        if (navigation) {
          this.metrics.fcp = navigation.responseStart - navigation.requestStart;
          this.metrics.ttfb =
            navigation.responseStart - navigation.requestStart;
        }
      }, 0);
    });
  }

  getMetrics(): PerformanceMetrics {
    return { ...this.metrics };
  }

  // Core Web Vitalsè©•ä¾¡
  evaluateMetrics(): {
    lcp: 'good' | 'needs-improvement' | 'poor';
    fid: 'good' | 'needs-improvement' | 'poor';
    cls: 'good' | 'needs-improvement' | 'poor';
  } {
    return {
      lcp: this.metrics.lcp
        ? this.metrics.lcp <= 2500
          ? 'good'
          : this.metrics.lcp <= 4000
            ? 'needs-improvement'
            : 'poor'
        : 'good',
      fid: this.metrics.fid
        ? this.metrics.fid <= 100
          ? 'good'
          : this.metrics.fid <= 300
            ? 'needs-improvement'
            : 'poor'
        : 'good',
      cls: this.metrics.cls
        ? this.metrics.cls <= 0.1
          ? 'good'
          : this.metrics.cls <= 0.25
            ? 'needs-improvement'
            : 'poor'
        : 'good',
    };
  }

  // ãƒ¬ãƒãƒ¼ãƒˆé€ä¿¡ï¼ˆé–‹ç™ºç”¨ï¼‰
  report() {
    const metrics = this.getMetrics();
    const evaluation = this.evaluateMetrics();

    logger.info('ğŸš€ Performance Metrics');
    logger.log('Metrics:', metrics);
    logger.log('Evaluation:', evaluation);

    return { metrics, evaluation };
  }
}

// Bundle Size Analyzerï¼ˆé–‹ç™ºç”¨ï¼‰
export const analyzeBundleSize = () => {
  if (typeof window === 'undefined') return;

  const scripts = Array.from(document.querySelectorAll('script[src]'));
  const styles = Array.from(
    document.querySelectorAll('link[rel="stylesheet"]')
  );

  logger.info('ğŸ“¦ Bundle Analysis');
  logger.log('Scripts:', scripts.length);
  logger.log('Stylesheets:', styles.length);

  // ãƒªã‚½ãƒ¼ã‚¹ã‚µã‚¤ã‚ºã‚’æ¨å®š
  Promise.all([
    ...scripts.map(script =>
      fetch((script as HTMLScriptElement).src, { method: 'HEAD' })
        .then(res => ({
          url: (script as HTMLScriptElement).src,
          size: res.headers.get('content-length') || 'unknown',
        }))
        .catch(() => ({
          url: (script as HTMLScriptElement).src,
          size: 'error',
        }))
    ),
  ]).then(results => {
    logger.log('Bundle analysis results:', results);
  });
};

// ãƒ¬ãƒ³ãƒ€ãƒªãƒ³ã‚°ãƒ‘ãƒ•ã‚©ãƒ¼ãƒãƒ³ã‚¹æ¸¬å®š
export const measureRenderTime = (componentName: string) => {
  return {
    start: () => performance.mark(`${componentName}-render-start`),
    end: () => {
      performance.mark(`${componentName}-render-end`);
      performance.measure(
        `${componentName}-render`,
        `${componentName}-render-start`,
        `${componentName}-render-end`
      );

      const measure = performance.getEntriesByName(
        `${componentName}-render`
      )[0];
      logger.log(
        `âš¡ ${componentName} render time: ${measure.duration.toFixed(2)}ms`
      );

      return measure.duration;
    },
  };
};

/**
 * ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢
 * Phase 3B: è‡ªå‹•ãƒ¬ãƒ¼ãƒˆåˆ¶é™é©ç”¨ãƒ»Next.jsçµ±åˆ
 */

import { NextRequest, NextResponse } from 'next/server';
import { rateLimiter, RateLimitType } from './rate-limiter';
import { logger } from '@/lib/logger';

// ãƒ¬ãƒ¼ãƒˆåˆ¶é™è¨­å®š
interface RateLimitConfig {
  type: RateLimitType;
  keyGenerator: (request: NextRequest) => string;
  skipIf?: (request: NextRequest) => boolean;
  onLimitExceeded?: (request: NextRequest, result: any) => NextResponse;
}

const hasRateLimitBackend = Boolean(
  process.env.UPSTASH_REDIS_REST_URL && process.env.UPSTASH_REDIS_REST_TOKEN
);

/**
 * ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ç”Ÿæˆé–¢æ•°
 */
export function createRateLimitMiddleware(config: RateLimitConfig) {
  return async (request: NextRequest): Promise<NextResponse | null> => {
    try {
      if (!hasRateLimitBackend) {
        return null;
      }

      // ã‚¹ã‚­ãƒƒãƒ—æ¡ä»¶ãƒã‚§ãƒƒã‚¯
      if (config.skipIf && config.skipIf(request)) {
        return null; // ã‚¹ã‚­ãƒƒãƒ—
      }

      const identifier = config.keyGenerator(request);

      // ãƒ›ãƒ¯ã‚¤ãƒˆãƒªã‚¹ãƒˆãƒã‚§ãƒƒã‚¯
      const isWhitelisted = await rateLimiter.isWhitelisted(
        config.type,
        identifier
      );
      if (isWhitelisted) {
        return null; // ãƒ›ãƒ¯ã‚¤ãƒˆãƒªã‚¹ãƒˆã¯åˆ¶é™ã—ãªã„
      }

      // ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒã‚§ãƒƒã‚¯
      const result = await rateLimiter.checkRateLimit(config.type, identifier);

      if (!result.allowed) {
        // ã‚«ã‚¹ã‚¿ãƒ ãƒãƒ³ãƒ‰ãƒ©ãƒ¼ãŒã‚ã‚‹å ´åˆã¯ä½¿ç”¨
        if (config.onLimitExceeded) {
          return config.onLimitExceeded(request, result);
        }

        // ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆã®ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒ¬ã‚¹ãƒãƒ³ã‚¹
        return new NextResponse(
          JSON.stringify({
            error: 'Rate limit exceeded',
            message: getRateLimitMessage(config.type, result),
            retryAfter: result.retryAfter,
            blockLevel: result.blockLevel,
          }),
          {
            status: 429,
            headers: {
              'Content-Type': 'application/json',
              'X-RateLimit-Limit': result.limit.toString(),
              'X-RateLimit-Remaining': result.remaining.toString(),
              'X-RateLimit-Reset': result.resetTime.toString(),
              'Retry-After': (result.retryAfter || 60).toString(),
            },
          }
        );
      }

      // ãƒ¬ã‚¹ãƒãƒ³ã‚¹ã«ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒ˜ãƒƒãƒ€ãƒ¼ã‚’è¿½åŠ 
      return NextResponse.next({
        headers: {
          'X-RateLimit-Limit': result.limit.toString(),
          'X-RateLimit-Remaining': result.remaining.toString(),
          'X-RateLimit-Reset': result.resetTime.toString(),
        },
      });
    } catch (error) {
      logger.error('ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ã‚¨ãƒ©ãƒ¼:', error);
      // ã‚¨ãƒ©ãƒ¼æ™‚ã¯åˆ¶é™ã—ãªã„ï¼ˆãƒ•ã‚§ã‚¤ãƒ«ã‚ªãƒ¼ãƒ—ãƒ³ï¼‰
      return null;
    }
  };
}

/**
 * å®šç¾©æ¸ˆã¿ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢
 */
export const loginRateLimit = createRateLimitMiddleware({
