/**
 * é«˜æ€§èƒ½ãƒ¬ãƒ¼ãƒˆåˆ¶é™ã‚·ã‚¹ãƒ†ãƒ 
 * Phase 3B: Redis/Upstash Redisçµ±åˆã«ã‚ˆã‚‹æ®µéšçš„ãƒ–ãƒ­ãƒƒã‚¯æ©Ÿèƒ½
 */

import { Redis } from '@upstash/redis';
import { logger } from '@/lib/logger';
import { z } from 'zod';

// ãƒ¬ãƒ¼ãƒˆåˆ¶é™è¨­å®š
export const RATE_LIMIT_CONFIG = {
  // ãƒ­ã‚°ã‚¤ãƒ³è©¦è¡Œåˆ¶é™
  LOGIN_ATTEMPTS: {
    WINDOW: 900, // 15åˆ†ï¼ˆç§’ï¼‰
    MAX_ATTEMPTS: 5,
    BLOCK_DURATION: [60, 300, 3600, 86400], // 1åˆ†â†’5åˆ†â†’1æ™‚é–“â†’24æ™‚é–“
  },

  // APIå‘¼ã³å‡ºã—åˆ¶é™
  API_CALLS: {
    WINDOW: 60, // 1åˆ†
    MAX_CALLS: 100,
    BURST_LIMIT: 10, // ãƒãƒ¼ã‚¹ãƒˆåˆ¶é™
  },

  // ã‚»ãƒƒã‚·ãƒ§ãƒ³ä½œæˆåˆ¶é™
  SESSION_CREATION: {
    WINDOW: 300, // 5åˆ†
    MAX_SESSIONS: 3,
    BLOCK_DURATION: 1800, // 30åˆ†
  },

  // MFAè©¦è¡Œåˆ¶é™
  MFA_ATTEMPTS: {
    WINDOW: 300, // 5åˆ†
    MAX_ATTEMPTS: 10,
    BLOCK_DURATION: 900, // 15åˆ†
  },
} as const;

// ãƒ¬ãƒ¼ãƒˆåˆ¶é™ã‚¿ã‚¤ãƒ—
export type RateLimitType =
  | 'login_attempts'
  | 'api_calls'
  | 'session_creation'
  | 'mfa_attempts';

// ãƒ¬ãƒ¼ãƒˆåˆ¶é™çµæœ
export interface RateLimitResult {
  allowed: boolean;
  limit: number;
  remaining: number;
  resetTime: number;
  retryAfter?: number;
  blockLevel?: number;
  escalated?: boolean;
}

// ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒ«ãƒ¼ãƒ«
const RateLimitRuleSchema = z.object({
  type: z.enum([
    'login_attempts',
    'api_calls',
    'session_creation',
    'mfa_attempts',
  ]),
  identifier: z.string().min(1, 'è­˜åˆ¥å­ãŒå¿…è¦ã§ã™'),
  window: z.number().positive('ã‚¦ã‚£ãƒ³ãƒ‰ã‚¦ã¯æ­£ã®æ•°ã§ã‚ã‚‹å¿…è¦ãŒã‚ã‚Šã¾ã™'),
  limit: z.number().positive('åˆ¶é™å€¤ã¯æ­£ã®æ•°ã§ã‚ã‚‹å¿…è¦ãŒã‚ã‚Šã¾ã™'),
  blockDuration: z.number().positive().optional(),
});

export type RateLimitRule = z.infer<typeof RateLimitRuleSchema>;

/**
 * Rediså¯¾å¿œé«˜æ€§èƒ½ãƒ¬ãƒ¼ãƒˆåˆ¶é™ã‚¯ãƒ©ã‚¹
 * åˆ†æ•£ç’°å¢ƒã§ã®ä¸€è²«æ€§ã¨ãƒ‘ãƒ•ã‚©ãƒ¼ãƒãƒ³ã‚¹ã‚’ä¸¡ç«‹
 */
export class RateLimiter {
  private redis: Redis;

  constructor() {
    // Upstash Redisæ¥ç¶šï¼ˆç’°å¢ƒå¤‰æ•°ã‹ã‚‰è¨­å®šå–å¾—ï¼‰
    this.redis = new Redis({
      url: process.env.UPSTASH_REDIS_REST_URL || '',
      token: process.env.UPSTASH_REDIS_REST_TOKEN || '',
    });
  }

  /**
   * ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒã‚§ãƒƒã‚¯ãƒ»é©ç”¨
   * æ®µéšçš„ãƒ–ãƒ­ãƒƒã‚¯æ©Ÿèƒ½ä»˜ã
   */
  async checkRateLimit(
    type: RateLimitType,
    identifier: string,
    customConfig?: Partial<{
      window: number;
      limit: number;
      blockDuration: number;
    }>
  ): Promise<RateLimitResult> {
    try {
      const config = this.getConfig(type);
      const window = customConfig?.window || config.WINDOW;
      const limit =
        customConfig?.limit ||
        ('MAX_ATTEMPTS' in config && config.MAX_ATTEMPTS) ||
        ('MAX_CALLS' in config && config.MAX_CALLS) ||
        ('MAX_SESSIONS' in config && config.MAX_SESSIONS) ||
        0;

      const key = this.generateKey(type, identifier);
      const blockKey = `${key}:block`;
      const escalationKey = `${key}:escalation`;

      const now = Math.floor(Date.now() / 1000);
      const windowStart = now - window;

      // ãƒ–ãƒ­ãƒƒã‚¯çŠ¶æ…‹ãƒã‚§ãƒƒã‚¯
      const blockInfo = await this.redis.get(blockKey);
      if (blockInfo) {
        const blockData = JSON.parse(blockInfo as string);
        const unblockTime = blockData.unblockTime;

        if (now < unblockTime) {
          return {
            allowed: false,
            limit,
            remaining: 0,
            resetTime: unblockTime,
            retryAfter: unblockTime - now,
            blockLevel: blockData.level,
          };
        } else {
          // ãƒ–ãƒ­ãƒƒã‚¯æœŸé–“çµ‚äº†
          await this.redis.del(blockKey);
        }
      }

      // ã‚¹ãƒ©ã‚¤ãƒ‡ã‚£ãƒ³ã‚°ã‚¦ã‚£ãƒ³ãƒ‰ã‚¦ã§ã®ã‚«ã‚¦ãƒ³ãƒˆå–å¾—
      const pipeline = this.redis.pipeline();

      // å¤ã„ã‚¨ãƒ³ãƒˆãƒªã‚’å‰Šé™¤
      pipeline.zremrangebyscore(key, 0, windowStart);

      // ç¾åœ¨ã®æ™‚åˆ»ã‚’ã‚¹ã‚³ã‚¢ã¨ã—ã¦è¿½åŠ 
      pipeline.zadd(key, { score: now, member: `${now}-${Math.random()}` });

      // ç¾åœ¨ã®ã‚«ã‚¦ãƒ³ãƒˆã‚’å–å¾—
      pipeline.zcard(key);

      // TTLè¨­å®š
      pipeline.expire(key, window + 60);

      const results = await pipeline.exec();
      const currentCount = (results?.[2]?.[1] as number) || 0;

      const resetTime = now + window;
      const remaining = Math.max(0, limit - currentCount);

      if (currentCount > limit) {
        // ãƒ¬ãƒ¼ãƒˆåˆ¶é™ã«é”ã—ãŸå ´åˆã®æ®µéšçš„ãƒ–ãƒ­ãƒƒã‚¯å‡¦ç†
        const escalationResult = await this.handleEscalation(
          type,
          identifier,
          escalationKey,
          blockKey,
          now
        );

        return {
          allowed: false,
          limit,
          remaining: 0,
          resetTime,
          retryAfter: escalationResult.blockDuration,
          blockLevel: escalationResult.level,
          escalated: true,
        };
      }

      return {
        allowed: true,
        limit,
        remaining,
        resetTime,
      };
    } catch (error) {
      logger.error('ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒã‚§ãƒƒã‚¯ã‚¨ãƒ©ãƒ¼:', error);

      // Redisã‚¨ãƒ©ãƒ¼æ™‚ã¯åˆ¶é™ã—ãªã„ï¼ˆãƒ•ã‚§ã‚¤ãƒ«ã‚ªãƒ¼ãƒ—ãƒ³ï¼‰
      return {
        allowed: true,
        limit: 0,
        remaining: 0,
        resetTime: 0,
      };
    }
  }

  /**
   * æ®µéšçš„ãƒ–ãƒ­ãƒƒã‚¯ãƒ»ã‚¨ã‚¹ã‚«ãƒ¬ãƒ¼ã‚·ãƒ§ãƒ³å‡¦ç†
   */
  private async handleEscalation(
    type: RateLimitType,
    identifier: string,
    escalationKey: string,
    blockKey: string,
    now: number
  ): Promise<{ level: number; blockDuration: number }> {
    // ç¾åœ¨ã®ã‚¨ã‚¹ã‚«ãƒ¬ãƒ¼ã‚·ãƒ§ãƒ³ãƒ¬ãƒ™ãƒ«å–å¾—
    const escalationData = await this.redis.get(escalationKey);
    let level = 0;

    if (escalationData) {
      const data = JSON.parse(escalationData as string);
      level = data.level + 1;
    }

    // ãƒ–ãƒ­ãƒƒã‚¯æœŸé–“ã®æ±ºå®š
    const config = this.getConfig(type);
    let blockDuration: number;

    if (type === 'login_attempts') {
      // ãƒ­ã‚°ã‚¤ãƒ³è©¦è¡Œã®æ®µéšçš„ãƒ–ãƒ­ãƒƒã‚¯
      const loginConfig = RATE_LIMIT_CONFIG.LOGIN_ATTEMPTS;
      const durations = loginConfig.BLOCK_DURATION;
      blockDuration = durations[Math.min(level, durations.length - 1)];
    } else if (type === 'session_creation') {
      blockDuration = RATE_LIMIT_CONFIG.SESSION_CREATION.BLOCK_DURATION;
    } else if (type === 'mfa_attempts') {
      blockDuration = RATE_LIMIT_CONFIG.MFA_ATTEMPTS.BLOCK_DURATION;
    } else {
      // ãã®ä»–ã®ã‚¿ã‚¤ãƒ— - ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆ5åˆ†
      blockDuration = 300;
    }

    const unblockTime = now + blockDuration;

    // ãƒ–ãƒ­ãƒƒã‚¯æƒ…å ±ã‚’ä¿å­˜
    await this.redis.setex(
      blockKey,
      blockDuration + 60, // å°‘ã—ä½™è£•ã‚’æŒãŸã›ã‚‹
      JSON.stringify({
        level,
        blockTime: now,
        unblockTime,
        identifier,
        type,
      })
    );

    // ã‚¨ã‚¹ã‚«ãƒ¬ãƒ¼ã‚·ãƒ§ãƒ³æƒ…å ±ã‚’æ›´æ–°
    await this.redis.setex(
      escalationKey,
      86400, // 24æ™‚é–“ä¿æŒ
      JSON.stringify({
        level,
        lastEscalation: now,
      })
    );

    // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ­ã‚°è¨˜éŒ²
    await this.logRateLimitEvent({
      type: 'rate_limit_exceeded',
      rateLimitType: type,
      identifier,
      level,
      blockDuration,
      timestamp: now,
    });

    return { level, blockDuration };
  }

  /**
   * ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒªã‚»ãƒƒãƒˆï¼ˆç®¡ç†è€…ç”¨ï¼‰
   */
  async resetRateLimit(
    type: RateLimitType,
    identifier: string
  ): Promise<boolean> {
    try {
      const key = this.generateKey(type, identifier);
      const blockKey = `${key}:block`;
      const escalationKey = `${key}:escalation`;

      const pipeline = this.redis.pipeline();
      pipeline.del(key);
      pipeline.del(blockKey);
      pipeline.del(escalationKey);

      await pipeline.exec();

      // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ­ã‚°è¨˜éŒ²
      await this.logRateLimitEvent({
        type: 'rate_limit_reset',
        rateLimitType: type,
        identifier,
        timestamp: Math.floor(Date.now() / 1000),
      });

      return true;
    } catch (error) {
      console.error('ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒªã‚»ãƒƒãƒˆã‚¨ãƒ©ãƒ¼:', error);
      return false;
    }
  }

  /**
   * ãƒ›ãƒ¯ã‚¤ãƒˆãƒªã‚¹ãƒˆç®¡ç†
   */
  async addToWhitelist(
    type: RateLimitType,
    identifier: string,
    ttl?: number
  ): Promise<boolean> {
    try {
      const whitelistKey = `whitelist:${type}:${identifier}`;

      if (ttl) {
        await this.redis.setex(whitelistKey, ttl, '1');
      } else {
        await this.redis.set(whitelistKey, '1');
      }

      return true;
    } catch (error) {
      console.error('ãƒ›ãƒ¯ã‚¤ãƒˆãƒªã‚¹ãƒˆè¿½åŠ ã‚¨ãƒ©ãƒ¼:', error);
      return false;
    }
  }

  /**
   * ãƒ›ãƒ¯ã‚¤ãƒˆãƒªã‚¹ãƒˆãƒã‚§ãƒƒã‚¯
   */
  async isWhitelisted(
    type: RateLimitType,
    identifier: string
  ): Promise<boolean> {
    try {
      const whitelistKey = `whitelist:${type}:${identifier}`;
      const result = await this.redis.exists(whitelistKey);
      return result === 1;
    } catch (error) {
      console.error('ãƒ›ãƒ¯ã‚¤ãƒˆãƒªã‚¹ãƒˆãƒã‚§ãƒƒã‚¯ã‚¨ãƒ©ãƒ¼:', error);
      return false;
    }
  }

  /**
   * ãƒ¬ãƒ¼ãƒˆåˆ¶é™çµ±è¨ˆå–å¾—
   */
  async getRateLimitStats(
    type: RateLimitType,
    identifier: string
  ): Promise<{
    currentCount: number;
    isBlocked: boolean;
    blockLevel?: number;
    nextResetTime: number;
  }> {
    try {
      const key = this.generateKey(type, identifier);
      const blockKey = `${key}:block`;

      const config = this.getConfig(type);
      const window = config.WINDOW;
      const now = Math.floor(Date.now() / 1000);
      const windowStart = now - window;

      // ç¾åœ¨ã®ã‚«ã‚¦ãƒ³ãƒˆå–å¾—
      const currentCount = await this.redis.zcount(key, windowStart, now);

      // ãƒ–ãƒ­ãƒƒã‚¯çŠ¶æ…‹ãƒã‚§ãƒƒã‚¯
      const blockInfo = await this.redis.get(blockKey);
      let isBlocked = false;
      let blockLevel: number | undefined;

      if (blockInfo) {
        const blockData = JSON.parse(blockInfo as string);
        isBlocked = now < blockData.unblockTime;
        blockLevel = blockData.level;
      }

      return {
        currentCount: currentCount || 0,
        isBlocked,
        blockLevel,
        nextResetTime: now + window,
      };
    } catch (error) {
      console.error('ãƒ¬ãƒ¼ãƒˆåˆ¶é™çµ±è¨ˆå–å¾—ã‚¨ãƒ©ãƒ¼:', error);
      return {
        currentCount: 0,
        isBlocked: false,
        nextResetTime: 0,
      };
    }
  }

  /**
   * åœ°åŸŸåˆ¥åˆ¶é™ãƒã‚§ãƒƒã‚¯ï¼ˆå°†æ¥æ‹¡å¼µç”¨ï¼‰
   */
  async checkGeographicRestriction(
    ipAddress: string,
    allowedCountries?: string[]
  ): Promise<{ allowed: boolean; country?: string }> {
    // TODO: IP Geolocation APIã¨ã®çµ±åˆ
    // ç¾åœ¨ã¯å…¨ã¦è¨±å¯
    return { allowed: true };
  }

  /**
   * è¨­å®šå–å¾—
   */
  private getConfig(type: RateLimitType) {
    switch (type) {
      case 'login_attempts':
        return RATE_LIMIT_CONFIG.LOGIN_ATTEMPTS;
      case 'api_calls':
        return RATE_LIMIT_CONFIG.API_CALLS;
      case 'session_creation':
        return RATE_LIMIT_CONFIG.SESSION_CREATION;
      case 'mfa_attempts':
        return RATE_LIMIT_CONFIG.MFA_ATTEMPTS;
      default:
        return RATE_LIMIT_CONFIG.API_CALLS;
    }
  }

  /**
   * Redisã‚­ãƒ¼ç”Ÿæˆ
   */
  private generateKey(type: RateLimitType, identifier: string): string {
    return `rate_limit:${type}:${identifier}`;
  }

  /**
   * ãƒ¬ãƒ¼ãƒˆåˆ¶é™ã‚¤ãƒ™ãƒ³ãƒˆãƒ­ã‚°è¨˜éŒ²
   */
  private async logRateLimitEvent(event: {
    type: string;
    rateLimitType: RateLimitType;
    identifier: string;
    level?: number;
    blockDuration?: number;
    timestamp: number;
  }): Promise<void> {
    try {
      // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã‚¤ãƒ™ãƒ³ãƒˆãƒ†ãƒ¼ãƒ–ãƒ«ã¸ã®è¨˜éŒ²
      // å®Ÿè£…ã§ã¯å®Ÿéš›ã®ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æŒ¿å…¥å‡¦ç†
      console.log('Rate Limit Event:', event);
    } catch (error) {
      // ãƒ­ã‚°è¨˜éŒ²ã‚¨ãƒ©ãƒ¼ã¯ä¸»æ©Ÿèƒ½ã‚’å¦¨ã’ãªã„
      console.error('ãƒ¬ãƒ¼ãƒˆåˆ¶é™ã‚¤ãƒ™ãƒ³ãƒˆãƒ­ã‚°è¨˜éŒ²ã‚¨ãƒ©ãƒ¼:', error);
    }
  }
}

