   */
  async handleSecurityThreat(threat: SecurityThreat): Promise<void> {
    try {
      // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã‚¤ãƒ™ãƒ³ãƒˆã‚’ãƒ­ã‚°ã«è¨˜éŒ²
      await this.logSecurityEvent({
