  /**
   * ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ€ãƒƒã‚·ãƒ¥ãƒœãƒ¼ãƒ‰ç”¨çµ±è¨ˆãƒ‡ãƒ¼ã‚¿å–å¾—
   */
  async getSecurityStatistics(
    clinicId: string,
    days: number = 30
  ): Promise<{
    totalEvents: number;
    criticalThreats: number;
    blockedIps: number;
    suspiciousLogins: number;
    eventsByType: Record<string, number>;
    eventsByDay: Array<{ date: string; count: number }>;
  }> {
    const startDate = new Date();
    startDate.setDate(startDate.getDate() - days);

    const supabase = this.supabase;
    const { data: events, error } = await supabase
      .from('security_events')
      .select('*')
      .eq('clinic_id', clinicId)
      .gte('created_at', startDate.toISOString());

    if (error || !events) {
      logger.error('ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£çµ±è¨ˆå–å¾—ã‚¨ãƒ©ãƒ¼:', error);
      return {
        totalEvents: 0,
        criticalThreats: 0,
        blockedIps: 0,
        suspiciousLogins: 0,
        eventsByType: {},
        eventsByDay: [],
      };
    }

    // çµ±è¨ˆãƒ‡ãƒ¼ã‚¿ã®è¨ˆç®—
    const eventsByType: Record<string, number> = {};
    const eventsByDay: Record<string, number> = {};
    let criticalThreats = 0;
    let suspiciousLogins = 0;

    events.forEach(event => {
      // ã‚¿ã‚¤ãƒ—åˆ¥é›†è¨ˆ
      eventsByType[event.event_type] =
        (eventsByType[event.event_type] || 0) + 1;

      // æ—¥åˆ¥é›†è¨ˆ
      const date = new Date(event.created_at).toISOString().split('T')[0];
      eventsByDay[date] = (eventsByDay[date] || 0) + 1;

      // é‡è¦åº¦åˆ¥é›†è¨ˆ
      if (
        event.severity_level === 'critical' ||
        event.severity_level === 'error'
      ) {
        criticalThreats++;
      }

      if (
        event.event_type.includes('suspicious_login') ||
        event.event_type.includes('brute_force')
      ) {
        suspiciousLogins++;
      }
    });

    // æ—¥åˆ¥ãƒ‡ãƒ¼ã‚¿ã‚’é…åˆ—ã«å¤‰æ›
    const eventsByDayArray = Object.entries(eventsByDay).map(
      ([date, count]) => ({ date, count })
    );

    return {
      totalEvents: events.length,
      criticalThreats,
      blockedIps: 0, // TODO: IPãƒ–ãƒ­ãƒƒã‚¯æ©Ÿèƒ½å®Ÿè£…æ™‚ã«è¿½åŠ 
      suspiciousLogins,
      eventsByType,
      eventsByDay: eventsByDayArray,
    };
  }

