  /**
   * ãƒ–ãƒ«ãƒ¼ãƒˆãƒ•ã‚©ãƒ¼ã‚¹æ”»æ’ƒæ¤œå‡º
   */
  private async detectBruteForce(
    attempt: LoginAttempt
  ): Promise<AnomalyDetectionResult> {
    const timeWindow = 15 * 60 * 1000; // 15åˆ†
    const maxAttempts = 5;

    const supabase = this.supabase;
    const { count, error } = await supabase
      .from('security_events')
      .select('*', { count: 'exact' })
      .eq('ip_address', attempt.ipAddress)
      .in('event_type', ['login_failed', 'authentication_failed'])
      .gte(
        'created_at',
        new Date(attempt.timestamp.getTime() - timeWindow).toISOString()
      );

    if (error) {
      return {
        isAnomalous: false,
        confidence: 0,
        reasons: [],
        recommendedActions: [],
      };
    }

    const attemptCount = (count || 0) + (!attempt.success ? 1 : 0);
    const confidence = Math.min(attemptCount / maxAttempts, 1);

    return {
      isAnomalous: attemptCount >= maxAttempts,
      confidence,
      reasons: [`${attemptCount}å›ã®é€£ç¶šãƒ­ã‚°ã‚¤ãƒ³å¤±æ•— (é–¾å€¤: ${maxAttempts})`],
      recommendedActions: [
        'IPã‚¢ãƒ‰ãƒ¬ã‚¹ã®ä¸€æ™‚ãƒ–ãƒ­ãƒƒã‚¯',
        'ç®¡ç†è€…ã¸ã®é€šçŸ¥',
        'ãƒ¦ãƒ¼ã‚¶ãƒ¼ã‚¢ã‚«ã‚¦ãƒ³ãƒˆã®ç¢ºèª',
      ],
    };
  }

  /**
   * ä½ç½®ç•°å¸¸æ¤œå‡º
   */
  private async detectLocationAnomaly(
    userId: string,
    ipAddress: string
  ): Promise<AnomalyDetectionResult> {
    // éå»30æ—¥é–“ã®æ­£å¸¸ãªãƒ­ã‚°ã‚¤ãƒ³å ´æ‰€ã‚’å–å¾—
    const supabase = this.supabase;
    const { data: recentSessions, error } = await supabase
      .from('user_sessions')
      .select('ip_address, geolocation')
      .eq('user_id', userId)
      .eq('is_active', true)
      .gte(
        'created_at',
        new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString()
      );

    if (error || !recentSessions || recentSessions.length === 0) {
      return {
        isAnomalous: false,
        confidence: 0,
        reasons: [],
        recommendedActions: [],
      };
    }

    // ç°¡æ˜“çš„ãªä½ç½®åˆ¤å®šï¼ˆå®Ÿéš›ã®å®Ÿè£…ã§ã¯åœ°ç†çš„è·é›¢ã‚’è¨ˆç®—ï¼‰
    const knownIps = new Set(recentSessions.map(s => s.ip_address));
    const isKnownLocation = knownIps.has(ipAddress);

    if (!isKnownLocation && recentSessions.length >= 3) {
      return {
        isAnomalous: true,
        confidence: 0.6,
        reasons: ['éå»30æ—¥é–“ã«ä½¿ç”¨ã•ã‚Œã¦ã„ãªã„IPã‚¢ãƒ‰ãƒ¬ã‚¹'],
        recommendedActions: ['ãƒ¦ãƒ¼ã‚¶ãƒ¼ã¸ã®ç¢ºèª', 'ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®è¿½åŠ ç›£è¦–'],
      };
    }

    return {
      isAnomalous: false,
      confidence: 0,
      reasons: [],
      recommendedActions: [],
    };
  }

  /**
   * è¤‡æ•°ãƒ‡ãƒã‚¤ã‚¹ãƒ­ã‚°ã‚¤ãƒ³æ¤œå‡º
   */
  private async detectMultipleDeviceLogins(
    userId: string,
    userAgent: string
  ): Promise<AnomalyDetectionResult> {
    const timeWindow = 30 * 60 * 1000; // 30åˆ†
    const startTime = new Date(Date.now() - timeWindow);

    const supabase = this.supabase;
    const { data: recentSessions, error } = await supabase
      .from('user_sessions')
      .select('user_agent, device_info, created_at')
      .eq('user_id', userId)
      .eq('is_active', true)
      .gte('created_at', startTime.toISOString());

    if (error || !recentSessions) {
      return {
        isAnomalous: false,
        confidence: 0,
        reasons: [],
        recommendedActions: [],
      };
    }

    // ç•°ãªã‚‹ãƒ‡ãƒã‚¤ã‚¹ã‚¿ã‚¤ãƒ—ã®æ•°ã‚’è¨ˆç®—
    const deviceTypes = new Set(
      recentSessions.map(s => s.device_info?.device || 'unknown')
    );

    if (deviceTypes.size >= 3) {
      return {
        isAnomalous: true,
        confidence: 0.7,
        reasons: [`30åˆ†ä»¥å†…ã«${deviceTypes.size}ç¨®é¡ã®ãƒ‡ãƒã‚¤ã‚¹ã‹ã‚‰ãƒ­ã‚°ã‚¤ãƒ³`],
        recommendedActions: ['ãƒ¦ãƒ¼ã‚¶ãƒ¼ã¸ã®ç¢ºèªé€šçŸ¥', 'ã‚»ãƒƒã‚·ãƒ§ãƒ³ãƒ¬ãƒ“ãƒ¥ãƒ¼'],
      };
    }

    return {
      isAnomalous: false,
      confidence: 0,
      reasons: [],
      recommendedActions: [],
    };
  }

  /**
   * ã‚»ãƒƒã‚·ãƒ§ãƒ³ä¹—ã£å–ã‚Šæ¤œå‡º
   */
  private async detectSessionHijack(
    session: UserSession,
    currentActivity: { ipAddress?: string; userAgent?: string }
  ): Promise<AnomalyDetectionResult> {
    const reasons: string[] = [];
    let confidence = 0;

    // IPã‚¢ãƒ‰ãƒ¬ã‚¹å¤‰æ›´ã®æ¤œå‡ºï¼ˆå˜ç‹¬ã§ã‚‚æ¤œçŸ¥ã«åˆ°é”ã™ã‚‹é‡ã¿ï¼‰
    if (
      session.ip_address &&
      currentActivity.ipAddress &&
      session.ip_address !== currentActivity.ipAddress
    ) {
      reasons.push('IPã‚¢ãƒ‰ãƒ¬ã‚¹ã®å¤‰æ›´');
      confidence += 0.6;
    }

    // User-Agentå¤‰æ›´ã®æ¤œå‡º
    if (currentActivity.userAgent) {
      // æ˜ã‚‰ã‹ã«æ€ªã—ã„UAã®æ¤œå‡º
      if (
        /(automated|headless|bot|crawler|spider)/i.test(
          currentActivity.userAgent
        )
      ) {
        reasons.push('ç–‘ã‚ã—ã„User-Agentï¼ˆè‡ªå‹•åŒ–/ãƒœãƒƒãƒˆã®å¯èƒ½æ€§ï¼‰');
        confidence += 0.6;
      }
      if (session.user_agent) {
        if (session.user_agent !== currentActivity.userAgent) {
          reasons.push('User-Agentã®å¤‰æ›´');
          confidence += 0.3;
        }
      } else if (session.device_info?.browser) {
        // UAæœªä¿å­˜ã®å ´åˆã¯ãƒ‡ãƒã‚¤ã‚¹æƒ…å ±ã¨å¤§ã¾ã‹ã«æ¯”è¼ƒ
        const uaLower = currentActivity.userAgent.toLowerCase();
        const browserLower = String(
          session.device_info.browser || ''
        ).toLowerCase();
        if (browserLower && !uaLower.includes(browserLower)) {
          reasons.push('User-Agentã®ä¸ä¸€è‡´ï¼ˆä¿å­˜ãƒ–ãƒ©ã‚¦ã‚¶ã¨ç•°ãªã‚‹ï¼‰');
          confidence += 0.6;
        }
      }
    }

    // ã‚»ãƒƒã‚·ãƒ§ãƒ³ä½œæˆã‹ã‚‰å¤§å¹…ã«æ™‚é–“ãŒçµŒéã—ã¦ã„ã‚‹
    const sessionAge = Date.now() - new Date(session.created_at).getTime();
    const maxAge = (session.max_session_hours || 8) * 60 * 60 * 1000;
    if (sessionAge > maxAge * 0.9) {
      reasons.push('ã‚»ãƒƒã‚·ãƒ§ãƒ³æœŸé™ãŒè¿‘ã„');
      confidence += 0.2;
    }

    return {
      isAnomalous: confidence > 0.5,
      confidence,
      reasons,
      recommendedActions:
        confidence > 0.7
          ? ['ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®å¼·åˆ¶çµ‚äº†', 'ãƒ¦ãƒ¼ã‚¶ãƒ¼ã¸ã®ç·Šæ€¥é€šçŸ¥', 'å†èªè¨¼ã®è¦æ±‚']
          : ['ã‚»ãƒƒã‚·ãƒ§ãƒ³ã®ç›£è¦–å¼·åŒ–', 'ãƒ­ã‚°ã®è©³ç´°è¨˜éŒ²'],
    };
  }

