  /**
   * è‡ªå‹•å¯¾å¿œã®å®Ÿè¡Œ
   */
  private async executeAutomaticResponse(
    threat: SecurityThreat
  ): Promise<void> {
    switch (threat.threatType) {
      case 'brute_force':
        if (threat.severity === 'high' || threat.severity === 'critical') {
          // TODO: IPã‚¢ãƒ‰ãƒ¬ã‚¹ã®ä¸€æ™‚ãƒ–ãƒ­ãƒƒã‚¯
          logger.warn('ãƒ–ãƒ«ãƒ¼ãƒˆãƒ•ã‚©ãƒ¼ã‚¹æ”»æ’ƒIPã‚’ãƒ–ãƒ­ãƒƒã‚¯', {
            ipAddress: threat.ipAddress,
          });
        }
        break;

      case 'session_hijack':
        if (threat.userId && threat.severity === 'high') {
          // ç–‘ã‚ã—ã„ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’å¼·åˆ¶çµ‚äº†
          const sessions = await this.sessionManager.getUserSessions(
            threat.userId,
            threat.clinicId!
          );
          for (const session of sessions) {
            if (session.ip_address === threat.ipAddress) {
              await this.sessionManager.revokeSession(
                session.id,
                'security_violation'
              );
            }
          }
        }
        break;

      case 'multiple_devices':
        // è¿½åŠ ç›£è¦–ã®è¨­å®š
        logger.info('è¤‡æ•°ãƒ‡ãƒã‚¤ã‚¹ãƒ­ã‚°ã‚¤ãƒ³ã®ç›£è¦–å¼·åŒ–', {
          userId: threat.userId,
        });
        break;
    }
  }

