  /**
   * é‡è¦åº¦ãƒãƒƒãƒ”ãƒ³ã‚°
   */
  private mapSeverityLevel(
    level: string
  ): 'low' | 'medium' | 'high' | 'critical' {
    switch (level) {
      case 'critical':
        return 'critical';
      case 'error':
        return 'high';
      case 'warning':
        return 'medium';
      default:
        return 'low';
    }
  }

  /**
   * ã‚¢ãƒ©ãƒ¼ãƒˆã‚¿ã‚¤ãƒˆãƒ«ç”Ÿæˆ
   */
  private generateAlertTitle(eventType: string): string {
    const titleMap: Record<string, string> = {
