   */
  private static buildCSPString(csp: Record<string, string[]>): string {
    return Object.entries(csp)
      .map(([directive, sources]) => {
        if (sources.length === 0) {
          return directive;
        }
        return `${directive} ${sources.join(' ')}`;
      })
      .join('; ');
  }

  /**
   * nonceç”Ÿæˆï¼ˆå‹•çš„ã‚¹ã‚¯ãƒªãƒ—ãƒˆç”¨ï¼‰
   */
  static generateNonce(): string {
    // æš—å·å­¦çš„ã«å®‰å…¨ãªä¹±æ•°ã§nonceç”Ÿæˆ
    const array = new Uint8Array(16);

    if (
      typeof globalThis !== 'undefined' &&
      globalThis.crypto &&
      globalThis.crypto.getRandomValues
    ) {
      globalThis.crypto.getRandomValues(array);
    } else {
      // ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯ï¼ˆWeb Cryptoéå¯¾å¿œç’°å¢ƒï¼‰
      for (let i = 0; i < 16; i++) {
        array[i] = Math.floor(Math.random() * 256);
      }
    }

    // Edge Runtimeã§ã¯BufferãŒå­˜åœ¨ã—ãªã„ãŸã‚ã€Webæ¨™æº–ã®ã‚¨ãƒ³ã‚³ãƒ¼ãƒ‰ã«ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯ã™ã‚‹
    if (typeof Buffer !== 'undefined') {
      return Buffer.from(array).toString('base64');
    }

    let binary = '';
    for (const byte of array) {
      binary += String.fromCharCode(byte);
    }

    if (typeof btoa === 'function') {
      return btoa(binary);
    }

    // æœ€çµ‚ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯ï¼ˆbtoaã‚‚ãªã„ç’°å¢ƒã¯ç¨€ï¼‰
    return binary;
  }

  /**
   * CSPé•åãƒ¬ãƒãƒ¼ãƒˆå‡¦ç†
   */
  static async handleCSPViolation(report: CSPViolationReport): Promise<void> {
    try {
      // é•åãƒ¬ãƒ™ãƒ«ã®åˆ¤å®š
      const severity = this.assessViolationSeverity(report);

      // ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ãƒ­ã‚°ã«è¨˜éŒ²
      const logEntry = {
        type: 'csp_violation',
        severity,
        documentUri: report['document-uri'],
        violatedDirective: report['violated-directive'],
        blockedUri: report['blocked-uri'],
        disposition: report.disposition,
        timestamp: new Date().toISOString(),
        details: report,
      };

      // ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ã¾ãŸã¯ãƒ­ã‚°ã‚·ã‚¹ãƒ†ãƒ ã«è¨˜éŒ²
      logger.warn('CSP Violation:', logEntry);

      // é‡å¤§ãªé•åã®å ´åˆã¯ç®¡ç†è€…ã«é€šçŸ¥
      if (severity === 'high' || severity === 'critical') {
        await this.notifyAdminsOfCSPViolation(logEntry);
      }
    } catch (error) {
      logger.error('CSPé•åãƒ¬ãƒãƒ¼ãƒˆå‡¦ç†ã‚¨ãƒ©ãƒ¼:', error);
    }
  }

  /**
   * é•åã®é‡è¦åº¦åˆ¤å®š
   */
  private static assessViolationSeverity(
    report: CSPViolationReport
  ): 'low' | 'medium' | 'high' | 'critical' {
    const violatedDirective = report['violated-directive'];
    const blockedUri = report['blocked-uri'];

    // ã‚¯ãƒªãƒ†ã‚£ã‚«ãƒ«: script-srcé•åã§javascript:ã‚¹ã‚­ãƒ¼ãƒ 
    if (
      violatedDirective.includes('script-src') &&
      blockedUri.startsWith('javascript:')
    ) {
      return 'critical';
    }

    // é«˜: script-srcé•åã§å¤–éƒ¨ãƒ‰ãƒ¡ã‚¤ãƒ³
    if (
      violatedDirective.includes('script-src') &&
      blockedUri.startsWith('http')
    ) {
      return 'high';
    }

    // ä¸­: frame-ancestorsé•åï¼ˆclickjackingè©¦è¡Œï¼‰
    if (violatedDirective.includes('frame-ancestors')) {
      return 'medium';
    }

    // ãã®ä»–ã¯ä½ãƒ¬ãƒ™ãƒ«
    return 'low';
  }

  /**
   * ç®¡ç†è€…é€šçŸ¥ï¼ˆé‡å¤§ãªé•åæ™‚ï¼‰
   */
  private static async notifyAdminsOfCSPViolation(
