  // åˆ©ç”¨å¯èƒ½æ™‚é–“å–å¾—æ©Ÿèƒ½
  async getAvailableTimeSlots(
    staffId: string,
    date: Date,
    durationMinutes: number
  ): Promise<TimeSlot[]> {
    // å–¶æ¥­æ™‚é–“ã‚’å–å¾—
    const staff = await this.getStaffById(staffId);
    if (!staff) {
      throw new Error('ã‚¹ã‚¿ãƒƒãƒ•ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
    }

    // workingHours null safety (jest-test-stabilization-spec.md 6.5)
    if (!staff.workingHours) {
      return [
        { time: '09:00', available: false, conflictReason: 'å–¶æ¥­æ™‚é–“å¤–' },
      ];
    }

    const dayName = this.getDayName(date);
    const workingHours = staff.workingHours[dayName];

    if (!workingHours) {
      return [
        { time: '09:00', available: false, conflictReason: 'å–¶æ¥­æ™‚é–“å¤–' },
      ];
    }

    // ãã®æ—¥ã®æ—¢å­˜äºˆç´„ã‚’å–å¾—
    const existingReservations = await this.getReservationsByStaff(
      staffId,
      date
    );

    // æ™‚é–“ã‚¹ãƒ­ãƒƒãƒˆã‚’ç”Ÿæˆ
    const slots: TimeSlot[] = [];
    const startHour = parseInt(workingHours.start.split(':')[0]);
    const startMinute = parseInt(workingHours.start.split(':')[1]);
    const endHour = parseInt(workingHours.end.split(':')[0]);
    const endMinute = parseInt(workingHours.end.split(':')[1]);

    const startTime = new Date(date);
    startTime.setHours(startHour, startMinute, 0, 0);

    const endTime = new Date(date);
    endTime.setHours(endHour, endMinute, 0, 0);

    // 30åˆ†é–“éš”ã§ã‚¹ãƒ­ãƒƒãƒˆã‚’ç”Ÿæˆ
    const currentTime = new Date(startTime);
    while (currentTime < endTime) {
      const timeString = currentTime.toTimeString().substring(0, 5);
      const slotEndTime = new Date(
        currentTime.getTime() + durationMinutes * 60000
      );

      // å–¶æ¥­æ™‚é–“å¤–ãƒã‚§ãƒƒã‚¯
      if (currentTime < startTime || slotEndTime > endTime) {
        slots.push({
          time: timeString,
          available: false,
          conflictReason: 'å–¶æ¥­æ™‚é–“å¤–',
        });
      } else {
        // æ—¢å­˜äºˆç´„ã¨ã®é‡è¤‡ãƒã‚§ãƒƒã‚¯
        const conflict = existingReservations.find(reservation => {
          const resStart = new Date(reservation.startTime);
          const resEnd = new Date(reservation.endTime);
          return currentTime < resEnd && slotEndTime > resStart;
        });

        if (conflict) {
          slots.push({
            time: timeString,
            available: false,
            conflictReason: `äºˆç´„æ¸ˆã¿: ${(conflict as any).customerName ?? ''}æ§˜`,
          });
        } else {
          slots.push({
            time: timeString,
            available: true,
          });
        }
      }

      currentTime.setMinutes(currentTime.getMinutes() + 30);
    }

    return slots;
  }

  // äºˆç´„ä½œæˆæ©Ÿèƒ½
  async createReservation(data: CreateReservationData): Promise<Reservation> {
    const supabase = await this.getSupabase();
    // ãƒãƒªãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³
    const validation = await this.validateTimeSlot(
      data.staffId,
      data.startTime,
      data.endTime
    );
    if (!validation.isValid) {
      throw new Error(validation.reason);
    }

    const reservationData = {
      ...data,
      clinic_id: this.clinicId,
      status: 'unconfirmed',
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    const { data: result, error } = await supabase
      .from('reservations')
      .insert(reservationData)
      .select()
      .single();

    if (error) {
      throw new Error(error.message);
    }

    return result;
  }

  async createMultipleReservations(
    data: CreateMultipleReservationData
  ): Promise<Reservation[]> {
    const reservations: Reservation[] = [];

    for (const date of data.dates) {
      const startTime = new Date(date);
      startTime.setHours(
        data.baseStartTime.getHours(),
        data.baseStartTime.getMinutes()
      );

      const endTime = new Date(startTime.getTime() + data.duration * 60000);

      const reservationData: CreateReservationData = {
        customerId: data.customerId,
        menuId: data.menuId,
        staffId: data.staffId,
        startTime,
        endTime,
        channel: data.channel,
        notes: data.notes,
        createdBy: data.createdBy,
      };

      const reservation = await this.createReservation(reservationData);
      reservations.push(reservation);
    }

    return reservations;
  }

