      .eq('user_id', userId)
      .eq('clinic_id', clinicId)
      .order('last_activity', { ascending: false });

    if (error || !sessions) {
      console.error('ãƒ¦ãƒ¼ã‚¶ãƒ¼ã‚»ãƒƒã‚·ãƒ§ãƒ³å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
      return [];
