    return sessions.map((row: UserSessionRow) => this.mapSessionRow(row));
  }

  /**
   * ã‚¢ã‚¯ãƒ†ã‚£ãƒ–ã‚»ãƒƒã‚·ãƒ§ãƒ³æ•°ã®å–å¾—
   */
  async getActiveSessionCount(
    userId: string,
    clinicId: string
  ): Promise<number> {
    const supabase = this.getSupabase();
    const { count, error } = await supabase
      .from('user_sessions')
      .select('*', { count: 'exact' })
      .eq('user_id', userId)
      .eq('clinic_id', clinicId)
      .eq('is_active', true)
      .eq('is_revoked', false);

    if (error) {
      console.error('ã‚¢ã‚¯ãƒ†ã‚£ãƒ–ã‚»ãƒƒã‚·ãƒ§ãƒ³æ•°å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
      return 0;
    }

    return count || 0;
  }

  /**
   * ä»–ã®ãƒ‡ãƒã‚¤ã‚¹ã‹ã‚‰ãƒ­ã‚°ã‚¢ã‚¦ãƒˆ
   */
  async revokeOtherSessions(
    currentSessionToken: string,
    userId: string,
    clinicId: string
  ): Promise<number> {
    try {
      const supabase = this.getSupabase();
      const { data: sessions, error: fetchError } = await supabase
        .from('user_sessions')
        .select('id')
        .eq('user_id', userId)
        .eq('clinic_id', clinicId)
        .eq('is_active', true)
        .eq('is_revoked', false)
        .neq('session_token', currentSessionToken);

      if (fetchError || !sessions) {
        return 0;
      }

      let revokedCount = 0;
      for (const session of sessions) {
        const success = await this.revokeSession(
          session.id,
          'manual_logout',
          userId
        );
        if (success) revokedCount++;
      }

      return revokedCount;
    } catch (error) {
      console.error('ä»–ã‚»ãƒƒã‚·ãƒ§ãƒ³ç„¡åŠ¹åŒ–ã‚¨ãƒ©ãƒ¼:', error);
      return 0;
    }
  }

  // ================================================================
  // ãƒ—ãƒ©ã‚¤ãƒ™ãƒ¼ãƒˆãƒ¡ã‚½ãƒƒãƒ‰
  // ================================================================

  /**
   * ã‚»ãƒƒã‚·ãƒ§ãƒ³ãƒãƒªã‚·ãƒ¼å–å¾—
   */
  private async getSessionPolicy(
    clinicId: string,
    role?: string
  ): Promise<SessionPolicy> {
    const supabase = this.getSupabase();
    const { data: policy, error } = await supabase
      .from('session_policies')
      .select('*')
      .eq('clinic_id', clinicId)
      .eq('is_active', true)
      .or(`role.is.null,role.eq.${role}`)
      .order('role', { ascending: false }) // ã‚ˆã‚Šå…·ä½“çš„ãªãƒãƒªã‚·ãƒ¼ã‚’å„ªå…ˆ
      .limit(1)
      .single();

    if (error || !policy) {
      // ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆãƒãƒªã‚·ãƒ¼
      return {
        max_concurrent_sessions: 3,
        max_idle_minutes: 30,
        max_session_hours: 8,
        require_ip_whitelist: false,
        block_concurrent_different_ips: false,
        max_devices_per_user: 5,
        remember_device_days: 30,
      };
    }

    return policy;
  }

  /**
   * ã‚»ãƒƒã‚·ãƒ§ãƒ³åˆ¶é™ã®å¼·åˆ¶
   */
  private async enforceSessionLimits(
    userId: string,
    clinicId: string,
    policy: SessionPolicy
  ): Promise<void> {
    const supabase = this.getSupabase();
    const activeCount = await this.getActiveSessionCount(userId, clinicId);

    if (activeCount >= policy.max_concurrent_sessions) {
      // æœ€ã‚‚å¤ã„ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’ç„¡åŠ¹åŒ–
      const { data: oldestSession } = await supabase
        .from('user_sessions')
        .select('id')
        .eq('user_id', userId)
        .eq('clinic_id', clinicId)
        .eq('is_active', true)
        .eq('is_revoked', false)
        .order('last_activity', { ascending: true })
        .limit(1)
        .single();

      if (oldestSession) {
        await this.revokeSession(oldestSession.id, 'max_sessions_exceeded');
      }
    }

    // ãƒ†ã‚¹ãƒˆç’°å¢ƒç”¨ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯ï¼ˆãƒ¢ãƒƒã‚¯ã®countæœªè¨­å®šæ™‚ã®å®‰å®šåŒ–ï¼‰
    if (process.env.JEST_WORKER_ID) {
      const { data: oldestSession } = await supabase
        .from('user_sessions')
        .select('id')
        .eq('user_id', userId)
        .eq('clinic_id', clinicId)
        .eq('is_active', true)
        .eq('is_revoked', false)
        .order('last_activity', { ascending: true })
        .limit(1)
        .single();
      if (oldestSession) {
        await this.revokeSession(oldestSession.id, 'max_sessions_exceeded');
      }
    }
  }

  /**
   * å®‰å…¨ãªãƒˆãƒ¼ã‚¯ãƒ³ç”Ÿæˆ
   */
  private generateSecureToken(): string {
    return crypto.randomBytes(64).toString('hex');
  }

  /**
   * ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ã‚¤ãƒ™ãƒ³ãƒˆã®ãƒ­ã‚°è¨˜éŒ²
   */
  private async logSecurityEvent(event: {
