/**
 * User-Agentã‹ã‚‰ãƒ‡ãƒã‚¤ã‚¹æƒ…å ±ã‚’è§£æ
 */
export function parseUserAgent(userAgent: string): DeviceInfo {
  const ua = userAgent.toLowerCase();

  // ãƒ‡ãƒã‚¤ã‚¹åˆ¤å®š
  let device: string = 'Unknown';
  if (
    ua.includes('mobile') ||
    ua.includes('iphone') ||
    ua.includes('android')
  ) {
    device = 'mobile';
  } else if (ua.includes('tablet') || ua.includes('ipad')) {
    device = 'tablet';
  } else if (
    ua.includes('windows') ||
    ua.includes('mac') ||
    ua.includes('linux') ||
    ua.includes('x11')
  ) {
    device = 'desktop';
  }

  // OSåˆ¤å®š (iOS/iPad/iPhoneã‚’macã‚ˆã‚Šå…ˆã«åˆ¤å®š)
  let os = 'Unknown';
  if (ua.includes('iphone') || ua.includes('ipad') || ua.includes('ipod'))
    os = 'iOS';
  else if (ua.includes('android')) os = 'Android';
  else if (ua.includes('windows')) os = 'Windows';
  else if (ua.includes('mac')) os = 'macOS';
  else if (ua.includes('linux')) os = 'Linux';

  // ãƒ–ãƒ©ã‚¦ã‚¶åˆ¤å®š
  let browser = 'Unknown';
  if (ua.includes('edge')) browser = 'Edge';
  else if (ua.includes('chrome')) browser = 'Chrome';
  else if (ua.includes('firefox')) browser = 'Firefox';
  else if (ua.includes('safari')) browser = 'Safari';

  let browserVersion: string | undefined;
  const versionMatchers: Record<string, RegExp> = {
    Chrome: /chrome\/([\d.]+)/i,
    Firefox: /firefox\/([\d.]+)/i,
    Safari: /version\/([\d.]+)/i,
    Edge: /edg(?:e|)\/([\d.]+)/i,
  };
  const versionRegex = versionMatchers[browser];
  if (versionRegex) {
    const match = userAgent.match(versionRegex);
    if (match?.[1]) {
      browserVersion = match[1];
    }
  }

  const deviceInfo: DeviceInfo = { device, os, browser };
  if (browserVersion) {
    deviceInfo.browserVersion = browserVersion;
  }

  return deviceInfo;
}

/**
 * IPã‚¢ãƒ‰ãƒ¬ã‚¹ã‹ã‚‰ä½ç½®æƒ…å ±ã‚’å–å¾—ï¼ˆç°¡æ˜“ç‰ˆï¼‰
 */
export async function getGeolocationFromIP(
  ipAddress: string
): Promise<Geolocation | null> {
  // å®Ÿéš›ã®å®Ÿè£…ã§ã¯ã€GeoIP APIã‚µãƒ¼ãƒ“ã‚¹ã‚’ä½¿ç”¨
  // ç¾åœ¨ã¯ç°¡æ˜“çš„ãªå®Ÿè£…
  if (
    ipAddress.startsWith('192.168.') ||
    ipAddress.startsWith('10.') ||
    ipAddress === '127.0.0.1'
  ) {
    return { country: 'JP', region: 'Local', city: 'Local' };
  }

  // æœ¬ç•ªç’°å¢ƒã§ã¯å¤–éƒ¨GeoIP APIã‚’å‘¼ã³å‡ºã—
  return null;
}

