import { createAdminClient } from '@/lib/supabase';
import { logger } from '@/lib/logger';
import type { TableConfig, TableColumn } from '@/types/admin';

// ãƒ†ãƒ¼ãƒ–ãƒ«è¨­å®šã‚­ãƒ£ãƒƒã‚·ãƒ¥
const tableConfigCache = new Map<string, CachedTableConfig>();
const CACHE_TTL = 5 * 60 * 1000; // 5åˆ†é–“ã‚­ãƒ£ãƒƒã‚·ãƒ¥

interface CachedTableConfig {
  config: TableConfig;
  timestamp: number;
}

/**
 * ç®¡ç†å¯èƒ½ãªãƒ†ãƒ¼ãƒ–ãƒ«ä¸€è¦§ã‚’å‹•çš„ã«å–å¾—
 * information_schemaã‹ã‚‰å®Ÿéš›ã®DBã‚¹ã‚­ãƒ¼ãƒã‚’å‚ç…§
 */
export async function getManageableTables(): Promise<string[]> {
  const supabase = createAdminClient();

  // public ã‚¹ã‚­ãƒ¼ãƒã®ä¸­ã§ç®¡ç†å¯¾è±¡ã¨ãªã‚‹ãƒ†ãƒ¼ãƒ–ãƒ«ã‚’å–å¾—
  const { data, error } = await supabase.rpc('get_manageable_tables');

  if (error) {
    logger.error('ç®¡ç†å¯èƒ½ãƒ†ãƒ¼ãƒ–ãƒ«ã®å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
    // ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯: åŸºæœ¬çš„ãªãƒ†ãƒ¼ãƒ–ãƒ«ä¸€è¦§
    return [
      'treatment_menus',
      'menu_categories',
      'staff_members',
      'patient_profiles',
      'clinic_settings',
    ];
  }

  return data?.map((row: any) => row.table_name) || [];
}

/**
 * ãƒ†ãƒ¼ãƒ–ãƒ«è¨­å®šã‚’å‹•çš„ã«ç”Ÿæˆ
 * PostgreSQLã®information_schemaã‚’å‚ç…§ã—ã¦ã‚«ãƒ©ãƒ æƒ…å ±ã‚’å–å¾—
 */
export async function getTableConfig(
  tableName: string
): Promise<TableConfig | null> {
  // ã‚­ãƒ£ãƒƒã‚·ãƒ¥ãƒã‚§ãƒƒã‚¯
  const cached = tableConfigCache.get(tableName);
  if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
    return cached.config;
  }

  const supabase = createAdminClient();

  try {
    // ã‚«ãƒ©ãƒ æƒ…å ±ã‚’å–å¾—
    const { data: columns, error } = await supabase.rpc('get_table_columns', {
      table_name_param: tableName,
    });

    if (error) {
      logger.error(`ãƒ†ãƒ¼ãƒ–ãƒ«è¨­å®šå–å¾—ã‚¨ãƒ©ãƒ¼ (${tableName}):`, error);
      return null;
    }

    if (!columns || columns.length === 0) {
      logger.warn(`ãƒ†ãƒ¼ãƒ–ãƒ« ${tableName} ã®ã‚«ãƒ©ãƒ æƒ…å ±ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“`);
      return null;
    }

    // TableConfigã‚’æ§‹ç¯‰
    const config: TableConfig = {
      name: tableName,
      displayName: generateDisplayName(tableName),
      columns: {},
    };

    columns.forEach((col: any) => {
      const columnConfig: TableColumn = {
        type: mapPostgreSQLTypeToTableColumnType(col.data_type),
        label: generateColumnLabel(col.column_name),
        required: !col.is_nullable,
        readonly: isReadOnlyColumn(col.column_name),
      };

      // å‹ã«å¿œã˜ãŸè¿½åŠ è¨­å®š
      if (col.character_maximum_length) {
        columnConfig.maxLength = col.character_maximum_length;
      }

      if (col.numeric_precision) {
        columnConfig.precision = col.numeric_precision;
      }

      // å¤–éƒ¨ã‚­ãƒ¼æƒ…å ±ãŒã‚ã‚Œã°è¨­å®š
      if (col.foreign_table) {
        columnConfig.foreign_key = col.foreign_table;
      }

      config.columns[col.column_name] = columnConfig;
    });

    // ã‚­ãƒ£ãƒƒã‚·ãƒ¥ã«ä¿å­˜
    tableConfigCache.set(tableName, {
      config,
      timestamp: Date.now(),
    });

    return config;
  } catch (error) {
    logger.error(`ãƒ†ãƒ¼ãƒ–ãƒ«è¨­å®šå–å¾—ä¸­ã®ã‚¨ãƒ©ãƒ¼ (${tableName}):`, error);
    return null;
  }
}

/**
 * PostgreSQLã®ãƒ‡ãƒ¼ã‚¿å‹ã‚’TableColumnTypeã«ãƒãƒƒãƒ”ãƒ³ã‚°
 */
function mapPostgreSQLTypeToTableColumnType(
  pgType: string
): TableColumn['type'] {
  const typeMapping: Record<string, TableColumn['type']> = {
    'character varying': 'string',
    varchar: 'string',
    text: 'text',
    char: 'string',
    integer: 'integer',
    bigint: 'integer',
    smallint: 'integer',
    decimal: 'decimal',
    numeric: 'decimal',
    real: 'decimal',
    'double precision': 'decimal',
    boolean: 'boolean',
    'timestamp with time zone': 'timestamp',
    'timestamp without time zone': 'timestamp',
    date: 'timestamp',
    uuid: 'uuid',
    json: 'json',
    jsonb: 'json',
  };

  return typeMapping[pgType.toLowerCase()] || 'string';
}

/**
 * ãƒ†ãƒ¼ãƒ–ãƒ«åã‹ã‚‰è¡¨ç¤ºåã‚’ç”Ÿæˆ
 */
function generateDisplayName(tableName: string): string {
  const displayNames: Record<string, string> = {
