    staff_members: 'ã‚¹ã‚¿ãƒƒãƒ•',
    patient_profiles: 'æ‚£è€…æƒ…å ±',
    clinic_settings: 'ã‚¯ãƒªãƒ‹ãƒƒã‚¯è¨­å®š',
    appointment_slots: 'äºˆç´„æ ',
    medical_records: 'è¨ºç™‚è¨˜éŒ²',
    payment_records: 'æ”¯æ‰•ã„è¨˜éŒ²',
    insurance_claims: 'ä¿é™ºè«‹æ±‚',
