/**
 * ã‚«ãƒ©ãƒ åã‹ã‚‰è¡¨ç¤ºãƒ©ãƒ™ãƒ«ã‚’ç”Ÿæˆ
 */
function generateColumnLabel(columnName: string): string {
  const labelMapping: Record<string, string> = {
    id: 'ID',
    name: 'åå‰',
    email: 'ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹',
    phone: 'é›»è©±ç•ªå·',
    address: 'ä½æ‰€',
    description: 'èª¬æ˜',
    price: 'æ–™é‡‘',
    duration_minutes: 'æ‰€è¦æ™‚é–“ï¼ˆåˆ†ï¼‰',
    is_active: 'æœ‰åŠ¹',
    created_at: 'ä½œæˆæ—¥æ™‚',
    updated_at: 'æ›´æ–°æ—¥æ™‚',
    clinic_id: 'ã‚¯ãƒªãƒ‹ãƒƒã‚¯ID',
    category_id: 'ã‚«ãƒ†ã‚´ãƒªID',
    display_order: 'è¡¨ç¤ºé †',
    is_insurance_applicable: 'ä¿é™ºé©ç”¨',
    insurance_points: 'ä¿é™ºç‚¹æ•°',
